import * as api from "@/api/project/srm/materialClassify/orgmaterialParameters"
import { DICT_CODE } from '@/utils/constant'
import caiGou from '@/components/Select/xtpt/caigou'//下拉组件
import Pagination from "@/components/Pagination"
import { mapState } from "vuex"
import dialogMaterielCategory from '@/components/Dialog/dialogMaterielCategory.vue'//主数据物料选择
import addMain from "./dialog/addMain"
export default {
    name: "RFQconfigure",
    components: { Pagination, caiGou, dialogMaterielCategory, addMain },
    data () {
        return {
            DICT_CODE: DICT_CODE,
            theight: 0,//表格高度
            isSingle: true,//表格是否单选 点击各按钮根据流程逻辑控制单多选
            list: [],
            total: 0,
            listQuery: {
                //查询
                page: true,
                currentPage: 1,
                pageSize: 15
            },
            currentSelectedRow: null,//当前选中的行
            selectedRows: null,//多选时选中的所有行
            tableKey: 0 //表格索引
        }
    },
    created () { },
    computed: {
        ...mapState({
            dt_manufacture_diff: state => state.dict.dt_manufacture_diff,
            dt_unit_type: state => state.dict.dt_unit_type,
            dt_timeunit: state => state.dict.dt_timeunit,
            dt_srm_pdz: state => state.dict.dt_srm_pdz,
            dt_srm_pc0: state => state.dict.dt_srm_pc0,
        })
    },
    mounted () {
        this.$store.dispatch("dict/getDicData", ["dt_manufacture_diff", 'dt_unit_type', 'dt_timeunit', 'dt_srm_pdz', 'dt_srm_pc0'])
        this.setTableHeight()
        //表格高度自适应
        window.onresize = () => {
            this.setTableHeight()
        }
        this.getList()
    },
    watch: {

    },
    methods: {
        //表格高度计算
        setTableHeight () {
            this.theight = this.$myFun.getSingleTbHeight()
        },
        refresh () {
            this.getList()
        },
        handleClassifyCode () {
            if (this.listQuery.purchaseOrg) {
                this.$refs.refZsjWlxz.listQuery.orgCode = this.listQuery.purchaseOrg
                this.$refs.refZsjWlxz.selectedRows = []
                this.$refs.refZsjWlxz.handleQuery()
                this.$refs.refZsjWlxz.dialogVisible = true
            } else {
                this.$message({ type: 'warning', message: '请先选择采购组织' })
            }
        },
        confirmMateriel (data) {
            this.$set(this.listQuery, 'materialClassification', data[0].classCode)
            this.$set(this.listQuery, 'materialClassificationName', data[0].className)
        },
        //获取表格数据
        getList () {
            api.queryRecords(this.listQuery).then(res => {
                let options = [this.dt_manufacture_diff, this.dt_unit_type, this.dt_unit_type, this.dt_timeunit, this.dt_srm_pdz, this.dt_srm_pc0,]
                res.data.list = this.$myFun.srmCodeToName(res.data.list, options, ['makeDistinction', 'purchaseUmo', 'basicUmo', 'leadTimeUnit', 'purchaseTriggeringMethod', 'purchaseStrategy'])
                this.list = res.data.list
                this.total = res.data.pages.count
            })
        },
        //表格查询
        handleQuery () {
            this.listQuery.currentPage = 1
            this.getList()
        },
        //新增按钮单击事件方法
        handleCreate () {
            this.$refs.addMain.resetTemp()
            this.$refs.addMain.dialogStatus = 'create'
            this.$refs.addMain.dialogVisible = true
        },
        //编辑按钮单击事件方法
        handleUpdate (index, val) {
            this.$refs.addMain.dialogStatus = 'update'
            this.$refs.addMain.temp = { ...val }
            this.$refs.addMain.dialogVisible = true
        },
        //删除按钮单击事件方法
        handleDelete (index, val) {
            this.$confirm("是否删除该信息", "提示", {
                confirmButtonText: "确定",
                cancelButtonText: "取消",
                type: "warning"
            }).then(() => {
                api.deleteRecord(val.rowId).then(() => {
                    this.$message({
                        title: '成功',
                        message: '删除成功',
                        type: 'success',
                        duration: 2000
                    })
                    this.getList()
                })
            }).catch(() => {
                this.$message({
                    type: "info",
                    message: "已取消"
                })
            })
        },
        // 表格选择框选中 注:参数为选中的所有行的数组
        selectChange (val) {
            if (this.isSingle) {
                if (val.length > 1) {
                    this.$refs.tb.clearSelection() //清除其他行的选中
                    this.$refs.tb.toggleRowSelection(val[val.length - 1], "selected") //单击行绑定点击事件
                } else if (val.length === 1) {
                    this.selectedRows = val
                    this.currentSelectedRow = val[val.length - 1]
                }
            } else {
                this.selectedRows = val
                this.currentSelectedRow = val[val.length - 1]
            }
        },
        //点击表格某一行
        rowClick (val) {
            this.$refs.tb.toggleRowSelection(val, "selected") //单击行绑定点击事件
        }
    }
};
