import * as api from "@/api/project/srm/contractManage/problemManagement"
import resume from "../dialog/resume" //履历
import { DICT_CODE } from '@/utils/constant'
import caiGou from '@/components/Select/xtpt/caigou'//下拉组件
import Pagination from "@/components/Pagination"
import { mapState } from "vuex"
import close from "../dialog/close" //复检
import addMain from "../dialog/addMain" //
import examine from "../dialog/examine" //方案审核
export default {
    name: "problemManagement",
    components: { Pagination, caiGou, resume, close, addMain, examine },
    data () {
        return {
            modalnum: null,//模板编号
            theight: 0,
            isSingle: true,//表格是否单选 点击各按钮根据流程逻辑控制单多选
            currentSelectedRow: null,//当前选中的行
            selectedRows: null,//多选时选中的所有行
            list: null,
            listSub: null,
            total: 0,
            selectedrow: null,//主表选中行
            DICT_CODE: DICT_CODE,
            tableConfig: [
                { label: '序号', prop: 'rowId', align: 'center', width: '80px' },
                { label: '组织编码', prop: 'orgName', align: 'center', width: 'auto' },
                { label: '问题计划号', prop: 'questionNo', align: 'center', width: '120px' },
                { label: '问题状态', prop: 'stateCN', align: 'center', width: 'auto' },
                { label: '严重等级', prop: 'severityLevelCN', align: 'center', width: 'auto' },
                { label: '问题大类', prop: 'statisticalBigClassCN', align: 'center', width: 'auto' },
                { label: '供应商', prop: 'supplierCode', align: 'center', width: 'auto' },
                { label: '供应商名称', prop: 'supplierName', align: 'center', width: '150px' },
                { label: '物料分类', prop: 'materielClass', align: 'center', width: 'auto' },
                { label: '物料', prop: 'materielCode', align: 'center', width: 'auto' },
                { label: '物料名称', prop: 'materielName', align: 'center', width: 'auto' },
                { label: '问题描述', prop: 'questionDesc', align: 'center', width: 'auto' },
                { label: '发现时间', prop: 'firstFindTime', align: 'center', width: 'auto' },
                { label: '创建人', prop: 'creatorName', align: 'center', width: 'auto' },
                { label: '创建时间', prop: 'createTime', align: 'center', width: 'auto' },
                { label: '修改人', prop: 'updatorName', align: 'center', width: 'auto' },
                { label: '修改时间', prop: 'updateTime', align: 'center', width: 'auto' }
            ],
            subtableConfig: [
                { label: '序号', prop: 'rowId', align: 'center', width: '80px' },
                { label: '处理单号', prop: 'questionNo', align: 'center', width: '120px' },
                { label: '问题计划号', prop: 'handleOrderNo', align: 'center', width: '120px' },
                { label: '类型', prop: 'handleTypeCodeCN', align: 'center', width: 'auto' },
                { label: '状态', prop: 'handleState', align: 'center', width: 'auto' },
                { label: '解决对策', prop: 'solutions', align: 'center', width: 'auto' },
                { label: '方案审核', prop: 'auditStateCodeCN', align: 'center', width: 'auto' },
                { label: '审核说明', prop: 'auditExplain', align: 'center', width: 'auto' },
                { label: '负责人', prop: 'handler', align: 'center', width: 'auto' },
                { label: '预计完成时间', prop: 'predictCompletionTime', align: 'center', width: 'auto' },
                { label: '预计关闭日期', prop: 'closeTime', align: 'center', width: 'auto' },
                { label: '实际解决日期', prop: 'realSolveTime', align: 'center', width: 'auto' },
                { label: '创建人', prop: 'creator', align: 'center', width: 'auto' },
                { label: '创建时间', prop: 'createTime', align: 'center', width: 'auto' },
                { label: '修改人', prop: 'updator', align: 'center', width: 'auto' },
                { label: '修改时间', prop: 'updateTime', align: 'center', width: 'auto' }
            ],
            listQuery: {
                purchaseOrg: 'dtcg0001',
                page: true,
                currentPage: 1,
                pageSize: 10
            },
            sublistQuery: {
                page: true,
                currentPage: 1,
                pageSize: 10
            },
            tableKey: [0, 1]//表格索引，虚拟dom渲染表格时使用

        }
    },
    computed: {
        ...mapState({
            dt_srm_pc3: state => state.dict.dt_srm_pc3,
            dt_srm_pc4: state => state.dict.dt_srm_pc4,
            dt_srm_pb4: state => state.dict.dt_srm_pb4,
            dt_srm_pc5: state => state.dict.dt_srm_pc5,
            dt_srm_pcl: state => state.dict.dt_srm_pcl,
            dt_srm_pcj: state => state.dict.dt_srm_pcl,
        })
    },
    mounted () {
        this.$store.dispatch("dict/getDicData", ['dt_srm_pc3', 'dt_srm_pc4', 'dt_srm_pb4', 'dt_srm_pc5', 'dt_srm_pcl', 'dt_srm_pcj'])
        this.setTableHeight()
        //表格高度自适应
        window.onresize = () => {
            this.setTableHeight()
        }
        this.getList()
    },
    methods: {
        //表格高度计算
        setTableHeight () {
            this.theight = this.$myFun.getSingleTbHeight() - 285
        },
        refresh () {
            this.getList()
        },
        checked () {
            this.$refs.tb.toggleRowSelection(this.list[0], true)//官网方法，重点
        },
        //显示主表
        getList () {
            api.queryRecord(this.listQuery).then(res => {
                let options = [this.dt_srm_pc3, this.dt_srm_pc4, this.dt_srm_pb4]
                res.data = this.$myFun.codeToName(res.data, options, ['state', 'severityLevel', 'statisticalBigClass'])
                this.list = res.data.list
                this.total = res.data.pages.count
                this.$nextTick(function () {
                    this.checked()
                })
            })
        },
        //显示从表
        getSubList () {
            let dto = {
                currentPage: 1,
                pageSize: 10,
                rowId: this.selectlistRow.rowId
            }
            api.queryRecordItems(dto).then(res => {
                let options = [this.dt_srm_pc5, this.dt_srm_pcl]
                res.data = this.$myFun.codeToName(res.data, options, ['handleTypeCode', 'auditStateCode'])
                this.listSub = res.data.list
            })
        },
        //新增按钮单击事件
        handleCreate () {
            // this.$emit('setState', 'create', '')、
            this.$refs.addMain.initForm()
            this.$refs.addMain.dialogStatus = 'create'
            this.$refs.addMain.dialogVisible = true
        },
        //确认按钮单击事件
        handleConfirm () {
            if (this.selectlistRow) {
                this.$confirm(`是否确认该信息？`, "提示", {
                    confirmButtonText: "确定",
                    cancelButtonText: "取消",
                    type: "warning"
                }).then(() => {
                    let dto = {
                        rowIds: [this.selectlistRow.rowId],
                        confirmer: this.selectlistRow.creatorName,
                        confirmCode: this.selectlistRow.creator,
                    }
                    api.confirmRecord(dto).then(() => {
                        this.$message({
                            title: '成功',
                            message: '确认成功',
                            type: 'success',
                            duration: 2000
                        })
                        this.getList()
                    })
                }).catch(() => {
                    this.$message({
                        type: "info",
                        message: "已取消"
                    })
                })
            } else {
                this.$message({
                    title: '警告',
                    message: '请选择一条信息',
                    type: 'warning',
                    duration: 2000
                })
            }
        },
        //关闭按钮单击事件
        handleClose () {
            if (this.selectlistRow) {
                this.$refs.close.dialogStatus = 'create'
                this.$refs.close.dialogVisible = true
                this.$refs.close.temp.rowId = this.selectlistRow.rowId
                this.$refs.close.temp.close_people = this.selectlistRow.creatorName
                this.$refs.close.temp.close_people_code = this.selectlistRow.creator
            } else {
                this.$message({
                    title: '警告',
                    message: '请选择一条信息',
                    type: 'warning',
                    duration: 2000
                })
            }

        },
        //再重发按钮单击事件
        handleReissued () {
            if (this.selectlistRow) {
                this.$confirm(`确认重新打开问题？打开后可重新进行问题处理；问题单号：${this.selectlistRow.questionNo}`, "提示", {
                    confirmButtonText: "确定",
                    cancelButtonText: "取消",
                    type: "warning"
                }).then(() => {
                    api.newRecord(this.selectlistRow.rowId).then(() => {
                        this.$message({
                            title: '成功',
                            message: '再重发成功',
                            type: 'success',
                            duration: 2000
                        })
                        this.getList()
                    })
                }).catch(() => {
                    this.$message({
                        type: "info",
                        message: "已取消"
                    })
                })
            } else {
                this.$message({
                    title: '警告',
                    message: '请选择一条信息',
                    type: 'warning',
                    duration: 2000
                })
            }
        },
        //履历
        handleResume () {
            if (this.selectlistRow) {
                this.$refs.resume.dialogVisible = true
                this.$refs.resume.rowId = this.selectlistRow.rowId
                this.$refs.resume.getList()
            } else {
                this.$message({
                    title: '警告',
                    message: '请选择一条信息',
                    type: 'warning',
                    duration: 2000
                })
            }

        },
        //行内删除按钮单击事件
        handleDelete (index, val) {
            console.log(index, val)
            this.$confirm("是否删除该信息", "提示", {
                confirmButtonText: "确定",
                cancelButtonText: "取消",
                type: "warning"
            }).then(() => {
                api.deleteRecord(val.rowId).then(() => {
                    this.$message({
                        title: '成功',
                        message: '删除成功',
                        type: 'success',
                        duration: 2000
                    })
                    this.getList()
                })
            }).catch(() => {
                this.$message({
                    type: "info",
                    message: "已取消"
                })
            })
        },
        handleUpdate (index, val) {
            //行内编辑按钮单击事件
            this.$refs.addMain.temp = { ...val }
            this.$refs.addMain.dialogStatus = 'update'
            this.$refs.addMain.dialogVisible = true
        },
        //查询
        handleQuery () {
            this.listSub = []
            this.listQuery.currentPage = 1
            this.getList()
        },
        //从表查询按钮单击事件
        handleSearch () {
            if (this.selectlistRow) {
                let dto = {
                    currentPage: 1,
                    pageSize: 10,
                    rowId: this.selectlistRow.rowId
                }
                api.queryRecordItems(dto).then(res => {
                    console.log(res, 'ppkkk')
                    let options = [this.dt_srm_pc5, this.dt_srm_pcl]
                    res.data = this.$myFun.codeToName(res.data, options, ['handleTypeCode', 'auditState'])
                    this.listSub = res.data.list
                })
            } else {
                this.$message({
                    title: '警告',
                    message: '请选择一条信息',
                    type: 'warning',
                    duration: 2000
                })
            }
        },
        handleSee (index, val) {
            api.queryRecordAll(val.rowId).then(res => {
                console.log(res, 'kkkkkk')
                this.$emit('setState', 'search', res.data)

            })
        },
        //从表方案处理按钮单击事件
        handlePlan () {
            if (this.selectlistRow) {
                if (this.selectlistRow.state == 'PC31') {
                    let status = this.listSub.every(v => {
                        return v.handleStateCode !== 'PCJ0'
                    })
                    if (status) {
                        this.$emit('setState', 'create', this.selectlistRow)
                    } else {
                        this.$message({
                            title: '警告',
                            message: '含有编制状态不可处理方案',
                            type: 'warning',
                            duration: 2000
                        })
                    }
                } else {
                    this.$message({
                        title: '警告',
                        message: '非问题解决状态不能执行此操作',
                        type: 'warning',
                        duration: 2000
                    })
                }
            } else {
                this.$message({
                    title: '警告',
                    message: '请选择一条信息',
                    type: 'warning',
                    duration: 2000
                })
            }
        },
        handleUpdateItems (index, val) {
            this.$emit('setState', 'update', val)
        },
        //从表提交按钮单击事件
        handleSubmit () {
            if (this.selectlistRow && this.selectlistRow.state == 'PC32') {
                if (this.selectedRowsSub) {
                    this.$confirm(`是否提交？`, "提示", {
                        confirmButtonText: "确定",
                        cancelButtonText: "取消",
                        type: "warning"
                    }).then(() => {
                        let dto = {
                            rowId: this.selectedRowsSub.rowId
                        }
                        api.submitRecord(dto).then(() => {
                            this.$message({
                                title: '成功',
                                message: '提交成功',
                                type: 'success',
                                duration: 2000
                            })
                            this.getList()
                        })
                    }).catch(() => {
                        this.$message({
                            type: "info",
                            message: "已取消"
                        })
                    })
                } else {
                    this.$message({
                        title: '警告',
                        message: '请选择一条信息',
                        type: 'warning',
                        duration: 2000
                    })
                }
            } else {
                this.$message({
                    title: '警告',
                    message: '非解决中状态不可执行此操作',
                    type: 'warning',
                    duration: 2000
                })
            }

        },
        //方案审核按钮单击事件
        handleExamine () {
            if (this.selectlistRow && this.selectlistRow.state == 'PC32') {
                if (this.selectedRowsSub) {
                    this.$refs.examine.dialogVisible = true
                    this.$refs.examine.temp.rowId = this.selectedRowsSub.rowId
                } else {
                    this.$message({
                        title: '警告',
                        message: '请选择一条信息',
                        type: 'warning',
                        duration: 2000
                    })
                }
            } else {
                this.$message({
                    title: '警告',
                    message: '非解决中状态不可执行此操作',
                    type: 'warning',
                    duration: 2000
                })
            }


        },
        //问题解决按钮单击事件
        handleSolve () {
            if (this.selectlistRow && this.selectlistRow.state == 'PC32') {
                if (this.selectedRowsSub) {
                    this.$confirm(`确认已按处理方案解决问题？确认后将由相关负责人进行复检；处理单号：${this.selectedRowsSub.questionNo}`, "提示", {
                        confirmButtonText: "确定",
                        cancelButtonText: "取消",
                        type: "warning"
                    }).then(() => {
                        let dto = {
                            rowId: this.selectedRowsSub.rowId
                        }
                        api.solveRecord(dto).then(() => {
                            this.$message({
                                title: '成功',
                                message: '解决成功',
                                type: 'success',
                                duration: 2000
                            })
                            this.getList()
                        })
                    }).catch(() => {
                        this.$message({
                            type: "info",
                            message: "已取消"
                        })
                    })
                } else {
                    this.$message({
                        title: '警告',
                        message: '请选择一条信息',
                        type: 'warning',
                        duration: 2000
                    })
                }
            } else {
                this.$message({
                    title: '警告',
                    message: '非解决中状态不可执行此操作',
                    type: 'warning',
                    duration: 2000
                })
            }

        },
        //从表复检按钮单击事件
        handleExamin () {
            if (this.selectlistRow && this.selectlistRow.state == 'PC32') {
                if (this.selectedRowsSub) {
                    this.$refs.close.dialogStatus = 'update'
                    this.$refs.close.dialogVisible = true
                    this.$refs.close.temp.rowId = this.selectedRowsSub.rowId
                } else {
                    this.$message({
                        title: '警告',
                        message: '请选择一条信息',
                        type: 'warning',
                        duration: 2000
                    })
                }
            } else {
                this.$message({
                    title: '警告',
                    message: '非解决中状态不可执行此操作',
                    type: 'warning',
                    duration: 2000
                })
            }
        },
        //从表答按钮单击事件
        handleAnswer (index, val) {
            console.log(index, val)
            // this.$refs.addMain.dialogVisible = true
        },
        //从表删除按钮单击事件
        handleDeleteSub (index, val) {
            console.log(index, val)
            this.$confirm("是否删除该信息", "提示", {
                confirmButtonText: "确定",
                cancelButtonText: "取消",
                type: "warning"
            }).then(() => {
                api.deleteRecordItems(val.rowId).then(() => {
                    this.$message({
                        title: '成功',
                        message: '删除成功',
                        type: 'success',
                        duration: 2000
                    })
                    this.getList()
                })
            }).catch(() => {
                this.$message({
                    type: "info",
                    message: "已取消"
                })
            })
        },
        //每页大小变化
        handleSizeChange (val) {
            this.listQuery.pageSize = val
            this.getList()
        },
        //当前页变化
        handleCurrentChange (val) {
            this.listQuery.currentPage = val
            this.getList()
        },
        // 表格选择框选中 注:参数为选中的所有行的数组
        selectChange (val) {
            this.selectlistRow = val[val.length - 1]
            if (val.length > 1) {
                this.$refs.tb.clearSelection()
                this.$refs.tb.toggleRowSelection(val.pop())
            }
            if (this.selectlistRow) {
                this.getSubList()
            }
            this.$refs.tb.setCurrentRow(this.selectlistRow)
        },
        //点击表格某一行
        rowClick (val) {
            this.$refs.tb.clearSelection()
            this.$refs.tb.toggleRowSelection(val)
        },
        selectChangeSub (val) {
            this.selectedRowsSub = val[val.length - 1]
            if (val.length > 1) {
                this.$refs.tbSub.clearSelection()
                this.$refs.tbSub.toggleRowSelection(val.pop())
            }
            this.$refs.tbSub.setCurrentRow(this.selectedRowsSub)
        },
        //加载时默认选中第一行
        setFirstLine () {
            this.$nextTick(() => {
                this.$refs.tbmain.setCurrentRow(this.list[0])
            })
        }
    }
};
