import * as api from "@/api/project/srm/contractManage/evaluationPlan"
import { queryRecords } from "@/api/project/srm/dataBase/dictMaintenance"
import { DICT_CODE } from '@/utils/constant'
import caiGou from '@/components/Select/xtpt/caigou'//下拉组件
import { mapState } from "vuex"
import throttle from 'lodash/throttle'
export default {
    components: { caiGou },
    data () {
        return {
            phaseList: [],
            DICT_CODE: DICT_CODE,
            dialogStatus: '',
            temp: {
                evaluationPlanNo: undefined,
                orgCode: undefined,
                orgName: undefined,
                evaluationType: undefined,
                evaluationClassification: undefined,
                evaluationCategory: undefined,
                evaluationStage: undefined,
                evaluationYear: undefined,
                startTime: undefined,
                endTime: undefined,
                // evaluationCycle: undefined,
                remark: undefined,
            },
            dialogVisible: false,
            rules: {
                orgCode: [{ required: true, message: '采购组织不能为空', trigger: 'blur' }],
                evaluationType: [{ required: true, message: '评价类型不能为空', trigger: 'blur' }],
                evaluationClassification: [{ required: true, message: '评价分类不能为空', trigger: 'blur' }],
                evaluationCategory: [{ required: true, message: '评价类别不能为空', trigger: 'blur' }],
                evaluationStage: [{ required: true, message: '评价阶段不能为空', trigger: 'blur' }],
                evaluationYear: [{ required: true, message: '评价年度不能为空', trigger: 'blur' }],
                startTime: [{ required: true, message: '评价起日不能为空', trigger: 'blur' }],
                endTime: [{ required: true, message: '评价止日不能为空', trigger: 'blur' }],
            }
        }
    },
    computed: {
        ...mapState({
            dt_srm_pak: state => state.dict.dt_srm_pak, // 评价类型
            dt_srm_pb1: state => state.dict.dt_srm_pb1,   // 评价分类
            dt_srm_pb2: state => state.dict.dt_srm_pb2,  // 评价类别
        })
    },
    watch: {
        'temp.evaluationCategory' (val) {
            if (!val) {
                this.temp.evaluationStage = undefined
                this.phaseList = []
            } else {
                this.changeData(val)
            }
        },
    },
    mounted () {
        this.$store.dispatch("dict/getDicData", ["dt_srm_pak", 'dt_srm_pb1', 'dt_srm_pb2'])
    },
    methods: {
        // 项目类别联调项目阶段
        changeData (val) {
            if (val) {
                let dto = {
                    currentPage: 1,
                    pageSize: 15,
                    parentCode: val
                }
                queryRecords(dto).then(res => {
                    console.log(res)
                    let options = [this.dt_srm_p0]
                    res.data.list = this.$myFun.srmCodeToName(res.data.list, options, ['state'])
                    this.phaseList = res.data.list
                })
            }
        },
        //获取组织编码名称
        selectName (val) {
            this.temp.orgName = val
        },

        createData: throttle(function () {
            this.$refs.dataForm.validate(valid => {
                if (valid) {
                    console.log(JSON.stringify(this.temp))
                    api.addRecord(this.temp).then(() => {
                        this.$message({
                            title: '成功',
                            message: '新增成功',
                            type: 'success',
                            duration: 2000
                        })
                        this.dialogVisible = false
                        this.$emit('refresh')
                    })
                } else {
                    return false
                }
            })
        }, 2000,
            {
                leading: true,
                trailing: false
            }
        ),
        updateData: throttle(function () {
            api.updateRecord(this.temp.rowId, this.temp).then(() => {
                this.$message({
                    title: '成功',
                    message: '编辑成功',
                    type: 'success',
                    duration: 2000
                })
                this.dialogVisible = false
                this.$emit('refresh')
            })
        }, 2000,
            {
                leading: true,
                trailing: false
            }
        ),
        resetTemp () {
            this.temp = {
                evaluationPlanNo: undefined,
                orgCode: undefined,
                orgName: undefined,
                evaluationType: undefined,
                evaluationClassification: undefined,
                evaluationCategory: undefined,
                evaluationStage: undefined,
                evaluationYear: undefined,
                startTime: undefined,
                endTime: undefined,
                // evaluationCycle: undefined,
                remark: undefined,

            }
        }
    }
}
