import { mapState } from "vuex"
import * as api from "@/api/project/srm/supplierManage/supplierAccess/investigatePlan"
import { DICT_CODE } from '@/utils/constant'
import caiGou from '@/components/Select/xtpt/caigou'//下拉组件
import Pagination from "@/components/Pagination"
import dialogImport from '@/components/Dialog/dialogImport'
import dialogAdd from './dialog/dialogAdd'
import upload from '@/components/Dialog/upload'//附件上传
import examine from "@/components/Dialog/examine" //方案审核
export default {
    name: "investigatePlan",
    components: { caiGou, Pagination, dialogAdd, dialogImport, upload, examine },
    data () {
        return {
            functionCode: null,
            fileType: null,
            theight: 0,//表格高度
            isSingle: true,//表格是否单选 点击各按钮根据流程逻辑控制单多选
            mtabName: 'tabProject',
            dataList: [],
            itemListp: [],//从表数据
            itemListc: [],
            total: 0,
            totalItem: 0,
            listQuery: {
                //查询
                page: true,
                currentPage: 1,
                pageSize: 10,
                investigateNo: null,
                orgCode: null,
                materielClass: null,
                planSource: null,
                supplierName: null,
                proposalNo: null,
                state: null,
                startDate: null,
                endDate: null
            },
            itemQuery: {
                page: true,
                currentPage: 1,
                pageSize: 10,
                investigateNo: null
            },
            rangeDate: null,
            DICT_CODE: DICT_CODE,
            currentSelectedRow: null,//当前选中的行
            selectedRows: [],//多选时选中的所有行
            tableKey: 0, //表格索引
            filterConfig: [
                { type: 'selectcaiGou', label: '采购组织', prop: 'orgCode', width: '', copmNum: 0, class: 'filter-item' },
                { type: 'commonInput', label: '考察计划号', prop: 'investigateNo', width: '', class: 'filter-item ml_5' },
                { type: 'commonInput', label: '物料分类', prop: 'materielClass', width: '', class: 'filter-item ml_5' },
                { type: 'commonInput', label: '供应商名称', prop: 'supplierName', width: '', class: 'filter-item ml_5' },
                { type: 'commonInput', label: '关联单号', prop: 'proposalNo', width: '', class: 'filter-item ml_5' },
                { type: 'dictSelect', dType: 'dt_srm_pai', label: '计划来源', prop: 'planSource', width: '', optionArr: [], class: 'filter-item ml_5' },
                { type: 'dictSelect', dType: 'dt_srm_p0', label: '状态', prop: 'state', width: '', optionArr: [{ key: '0', value: '编制' }, { key: '1', value: '生效' }], class: 'filter-item ml_5' },
                { type: 'daterange', label: '考察时间', prop: 'rangeDate', width: '250px', startpl: '考察开始时间', endpl: '结束时间', class: 'filter-item' },
            ],
            oprateBtnConfig: [
                { id: 0, class: 'filter-item', icon: 'el-icon-plus', clickMethod: 'handleAdd', name: '计划录入' },
                { id: 1, class: 'filter-item ml_5', icon: 'el-icon-plus', clickMethod: 'handleImport', name: '附件管理' },
                { id: 2, class: 'filter-item ml_5', icon: 'el-icon-plus', clickMethod: 'handleApprove', name: '计划审批' },
                { id: 3, class: 'filter-item ml_5', icon: 'el-icon-edit-outline', clickMethod: 'handlePublish', name: '发布' }
            ],
            tableConfig: [
                { label: '计划来源', prop: 'planSourceCN', align: 'center', width: 'auto' },
                { label: '供应商', prop: 'supplierName', align: 'center', width: '180' },
                { label: '采购组织', prop: 'orgCode', align: 'center', width: 'auto' },
                { label: '考察计划', prop: 'investigateNo', align: 'center', width: '180' },
                { label: '考察时间', prop: 'investigateDate', align: 'center', width: 'auto' },
                { label: '考察地点', prop: 'place', align: 'center', width: '180' },
                { label: '考察原因', prop: 'reason', align: 'center', width: '180' },
                { label: '计划状态', prop: 'stateCN', align: 'center', width: 'auto' },//0编制 1生效
                { label: '流程状态', prop: 'flowStateCN', align: 'center', width: 'auto' },//0未启动
                { label: '创建人', prop: 'creator', align: 'center', width: 'auto' },
                { label: '创建时间', prop: 'createTime', align: 'center', width: '140' },
                { label: '修改人', prop: 'updator', align: 'center', width: 'auto' },
                { label: '修改时间', prop: 'updateTime', align: 'center', width: '140' }
            ],
            tableConfigSub: [
                [
                    { label: '考察项', prop: 'itemCN', align: 'center', width: 'auto' },
                    { label: '是否反馈', prop: 'feedback', align: 'center', width: 'auto' },//true false
                    { label: '是否考察', prop: 'investigate', align: 'center', width: 'auto' },
                    { label: '状态', prop: 'stateCN', align: 'center', width: 'auto' },//0编制 1生效 2反馈
                    { label: '考察部门', prop: 'departmentName', align: 'center', width: 'auto' },
                    { label: '任务接收人', prop: 'taskUserName', align: 'center', width: 'auto' },
                    { label: '考察人', prop: 'investigateUser', align: 'center', width: 'auto' },
                    { label: '评价人', prop: 'evaluateUserName', align: 'center', width: 'auto' },
                    { label: '理由', prop: 'reason', align: 'center', width: '180' },
                    { label: '创建人', prop: 'creator', align: 'center', width: 'auto' },
                    { label: '创建时间', prop: 'createTime', align: 'center', width: '180' },
                    { label: '修改人', prop: 'updator', align: 'center', width: 'auto' },
                    { label: '修改时间', prop: 'updateTime', align: 'center', width: '180' }
                ],
                [
                    { label: '考察计划号', prop: 'investigateNo', align: 'center', width: 'auto' },
                    { label: '提案号', prop: 'proposalNo', align: 'center', width: 'auto' },//无
                    { label: '物料分类', prop: 'materielClass', align: 'center', width: 'auto' },
                    { label: '物料分类名称', prop: 'materielClassName', align: 'center', width: 'auto' },
                    { label: '平台系列', prop: 'platformSeriesCN', align: 'center', width: 'auto' },
                    { label: '平台定位', prop: 'positionCN', align: 'center', width: 'auto' }
                ]
            ],
            dialogConfig: [
                { titleA: '计划新增', title: '计划编辑', curdialogStatus: 'add', unMutiple: false },
                { title: '上传附件' },
                { title: '发布' }
            ],
            modalNo: 'EXTP-BAS-0001',//导入模板编号,
            dialogImportVisible: false
        }
    },
    created () { },
    computed: {
        ...mapState({
            dt_srm_pb1: state => state.dict.dt_srm_pb1,//评价分类
            dt_srm_pb4: state => state.dict.dt_srm_pb4,//考察项
            dt_srm_p0: state => state.dict.dt_srm_p0,//状态
            dt_cate_platformSeries: state => state.srmBisSelect.dt_cate_platformSeries,//平台系列
            dt_srm_pad: state => state.dict.dt_srm_pad,
            dt_srm_pah: state => state.dict.dt_srm_pah,//流程状态
            dt_srm_pai: state => state.dict.dt_srm_pai//计划来源
        })
    },
    mounted () {
        this.$store.dispatch("dict/getDicData", ["dt_srm_pb1", "dt_srm_pb4", "dt_srm_p0", "dt_srm_pad", "dt_srm_pah", "dt_srm_pai"])
        this.$store.dispatch("srmBisSelect/getBisData", ["dt_cate_platformSeries"])
        this.setTableHeight()
        //表格高度自适应
        window.onresize = () => {
            this.setTableHeight()
        }
        this.getList()
    },
    watch: {
        'rangeDate' (value) {
            if (value) {
                this.listQuery.startDate = value[0]
                this.listQuery.endDate = value[1]
            }
        }
    },
    methods: {
        //表格高度计算
        setTableHeight () {
            this.theight = this.$myFun.getSingleTbHeight() - 100
        },
        refresh () {
            this.getList()
        },
        //获取表格数据
        getList () {
            api.queryRecords(this.listQuery).then(res => {
                let options = [this.dt_srm_pah, this.dt_srm_p0, this.dt_srm_pai]
                res.data = this.$myFun.codeToName(res.data, options, ['flowState', 'state', 'planSource'])
                this.dataList = res.data.list
                this.total = res.data.pages.count
                this.setFirstLine()
            })
        },
        //单条数据获取
        getRow () {
            this.mtabName = 'tabProject'
            api.queryRow(this.currentSelectedRow.investigateNo).then(res => {
                this.itemListp = []
                if (res.data.investigateItemDTOs) {
                    let options = [this.dt_srm_pb4, this.dt_srm_p0]
                    res.data.investigateItemDTOs = this.$myFun.codeToName(res.data.investigateItemDTOs, options, ['item', 'state'])
                    res.data.investigateItemDTOs.forEach((item, index) => {
                        this.itemListp.push({})
                        for (let key in item) {
                            this.tableConfigSub[0].forEach((itemsub) => {
                                if (itemsub.prop === key) {
                                    if (item[key] === true) {
                                        this.itemListp[index][key] = '是'
                                    } else if (item[key] === false) {
                                        this.itemListp[index][key] = '否'
                                    } else {
                                        this.itemListp[index][key] = item[key]
                                    }
                                }
                            })
                        }
                    })
                }


                this.itemListc = []
                if (res.data.investigateClassDTOs) {
                    let options = [this.dt_cate_platformSeries.data, this.dt_srm_pad]
                    res.data.investigateClassDTOs = this.$myFun.transCodeToName(res.data.investigateClassDTOs, options, [['platformSeries', 'code', 'name'], 'position'])
                    res.data.investigateClassDTOs.forEach((item, index) => {
                        this.itemListc.push({})
                        for (let key in item) {
                            this.tableConfigSub[1].forEach(() => {
                                this.itemListc[index][key] = item[key]
                            })
                        }
                    })
                }
            })
        },
        //表格查询
        handleQuery () {
            this.listQuery.currentPage = 1
            this.getList()
        },
        //表格显示条数变化
        handleSizeChange (val) {
            this.listQuery.pageSize = val
            this.getList()
        },
        //表格当前页变化
        handleCurrentChange (val) {
            this.listQuery.currentPage = val
            this.getList()
        },
        //操作按钮事件
        oprateClick (id) {
            switch (id) {
                case 0: {
                    this.handleAdd()
                    break
                }
                case 1: {
                    this.handleImport()
                    break
                }
                case 2: {
                    this.handleApprove()
                    break
                }
                case 3: {
                    this.handlePublish()
                    break
                }

            }
        },
        //新增按钮
        handleAdd () {
            this.dialogConfig[0].curdialogStatus = 'add'
            this.$refs.refadd.dialogVisible = true
            this.$refs.refadd.initForm('add')

        },
        //导入按钮
        handleImport () {
            if (this.currentSelectedRow) {
                this.functionCode = this.currentSelectedRow.investigateNo
                this.fileType = 'document'
                this.$nextTick(() => {
                    this.$refs.upload.getList()
                    this.$refs.upload.dialogVisible = true
                })
            } else {
                this.$message({
                    type: 'warning',
                    message: '请先选择一条信息'
                })
            }

        },
        //计划审批
        handleApprove () {
            if (this.currentSelectedRow) {
                this.$confirm(`您确定启动 考察计划号：${this.currentSelectedRow.investigateNo} 的审批流程吗？`, '消息提示', {
                    confirmButtonText: '确定',
                    cancelButtonText: '取消',
                    type: 'warning'
                }).then(() => {
                    api.approve(this.currentSelectedRow.investigateNo).then(() => {
                        this.$message({
                            type: 'success',
                            message: '操作成功'
                        })
                        this.getList()
                    })
                }).catch(() => {
                    this.$message({
                        type: 'info',
                        message: '已取消'
                    })
                })
            } else {
                this.$message({
                    type: 'warning',
                    message: '请先选择一条信息'
                })
            }
        },
        //发布按钮
        handlePublish () {
            if (this.currentSelectedRow) {
                this.$confirm(`您确定发布 考察计划号：${this.currentSelectedRow.investigateNo} 吗？`, '消息提示', {
                    confirmButtonText: '确定',
                    cancelButtonText: '取消',
                    type: 'warning'
                }).then(() => {
                    api.publish(this.currentSelectedRow.investigateNo).then(() => {
                        this.$message({
                            type: 'success',
                            message: '发布成功'
                        })
                        this.getList()
                    })
                }).catch(() => {
                    this.$message({
                        type: 'info',
                        message: '已取消'
                    })
                })
            } else {
                this.$message({
                    type: 'warning',
                    message: '请先选择一条信息'
                })
            }

        },
        //提交按钮
        handleSubmit () {
            if (this.currentSelectedRow) {
                this.$confirm(`您确定提交 提案号：${this.currentSelectedRow.proposalNo} 的审批流程吗？`, '消息提示', {
                    confirmButtonText: '确定',
                    cancelButtonText: '取消',
                    type: 'warning'
                }).then(() => {
                    // api.publish(this.currentSelectedRow.proposalNo).then(() => {
                    //     this.$message({
                    //         type: 'success',
                    //         message: '发布成功'
                    //     })
                    //     this.getList()
                    // })
                }).catch(() => {
                    this.$message({
                        type: 'info',
                        message: '已取消'
                    })
                })
            } else {
                this.$message({
                    type: 'warning',
                    message: '请先选择一条信息'
                })
            }

        },
        //审核按钮单击事件
        handleExamine () {
            if (this.currentSelectedRow) {
                this.$refs.examine.dialogVisible = true
                this.$refs.examine.temp.rowId = this.currentSelectedRow.rowId
            } else {
                this.$message({
                    title: '警告',
                    message: '请选择一条信息',
                    type: 'warning',
                    duration: 2000
                })
            }
        },
        //表格操作编辑
        handleUpdate (index, row) {
            api.queryRow(row.investigateNo).then(res => {
                console.log(res)
                this.dialogConfig[0].curdialogStatus = 'edit'
                this.$refs.refadd.dialogVisible = true
                this.$refs.refadd.isEdit = true
                let options = [this.dt_srm_pb4, this.dt_srm_p0]
                res.data.investigateItemDTOs = this.$myFun.codeToName(res.data.investigateItemDTOs, options, ['item', 'state'])
                let optionsC = [this.dt_cate_platformSeries.data, this.dt_srm_pad]
                res.data.investigateClassDTOs = this.$myFun.transCodeToName(res.data.investigateClassDTOs, optionsC, [['platformSeries', 'code', 'name'], 'position'])
                this.$refs.refadd.initForm('edit', res.data)
            })
        },
        //表格操作删除
        handleDelete (index, row) {
            api.deleteRecords(row.investigateNo).then(res => {
                console.log('删除res', res)
                this.$message({
                    type: 'success',
                    message: '删除成功'
                })
                this.getList()
            })
        },
        // 表格选择框选中 注:参数为选中的所有行的数组
        selectChange (val) {
            if (this.isSingle) {
                if (val.length > 1) {
                    this.$refs.tb.clearSelection() //清除其他行的选中
                    this.$refs.tb.toggleRowSelection(val[val.length - 1], "selected") //单击行绑定点击事件
                } else if (val.length === 1) {
                    this.selectedRows = val
                    this.currentSelectedRow = val[val.length - 1]
                    this.getRow()
                } else if (val.length === 0) {
                    this.selectedRows = val
                    this.currentSelectedRow = null
                    // this.itemListp = []
                }
            } else {
                this.selectedRows = val
                this.currentSelectedRow = val[val.length - 1]
                this.getRow()
            }

        },
        //加载时默认选中第一行
        setFirstLine () {
            this.$nextTick(() => {
                this.$refs.tb.toggleRowSelection(this.dataList[0], "selected")
                this.$refs.tb.setCurrentRow(this.dataList[0])
            })
        },
        //确认添加/编辑
        confirmAddEdit (cdata, investigateItemDTOs, investigateClassDTOs) {
            let investigateNo = cdata.investigateDTO.investigateNo
            if (investigateNo) {
                api.updateRecords(cdata).then(res => {
                    console.log(res)
                    if (this.dialogConfig[0].curdialogStatus === 'add') {
                        setTimeout(() => {
                            this.$message({ type: 'success', message: '新增成功' })
                            this.getList()
                        }, 3000)
                    } else {
                        setTimeout(() => {
                            this.$message({ type: 'success', message: '编辑成功' })
                            this.getList()
                        }, 3000)
                    }
                })
                investigateItemDTOs.forEach((item, index) => {
                    if (item.rowId) {
                        let obj = {
                            investigateItemDTO: item,
                            investigateUser: item.investigateUser,
                            method: "UPDATE"
                        }
                        api.updateProjectRecords(investigateNo, item.rowId, obj).then(res => {
                            console.log(res, `编辑考察项目成功${index}`)
                        })
                    } else {
                        item.investigateNo = investigateNo
                        api.createProject(investigateNo, item).then(res => {
                            console.log(res, `添加考察项目成功${index}`)
                        })
                    }
                })

                let dataArr = []
                investigateClassDTOs.forEach((item) => {
                    if (!item.rowId) {
                        item.investigateNo = investigateNo
                        dataArr.push(item)
                    }
                })
                if (dataArr.length > 0) {
                    api.createClasses(investigateNo, dataArr).then(res => {
                        console.log(res, `添加考察分类成功`)
                    })
                }
            } else {
                api.createRecords(cdata.investigateDTO).then(() => {
                    this.$message({ type: 'success', message: '新增成功' })
                    this.handleQuery()
                })
            }

        }
    }
};
