import * as api from "@/api/project/srm/contractManage/specialPaymentMethod"
import { DICT_CODE } from '@/utils/constant'
import caiGou from '@/components/Select/xtpt/caigou'//下拉组件
import Pagination from "@/components/Pagination"
import addMain from "./dialog/addMain"
import approvalList from "./dialog/approvalList"
import { mapState } from "vuex"
import throttle from 'lodash/throttle'

export default {
    name: "jsgl",
    components: { Pagination, caiGou, addMain, approvalList },
    data () {
        return {
            DICT_CODE: DICT_CODE,
            theight: 0,//表格高度
            isSingle: true,//表格是否单选 点击各按钮根据流程逻辑控制单多选
            list: [],
            total: 0,
            listQuery: {
                //查询
                page: true,
                currentPage: 1,
                pageSize: 15
            },
            currentSelectedRow: null,//当前选中的行
            selectedRows: null,//多选时选中的所有行
            tableKey: 0 //表格索引
        }
    },
    created () { },
    computed: {
        ...mapState({
            dt_srm_p0: state => state.dict.dt_srm_p0,
            dt_srm_pdr: state => state.dict.dt_srm_pdr,
            dt_srm_pdt: state => state.dict.dt_srm_pdt,
            dt_srm_pdu: state => state.dict.dt_srm_pdu,
        })
    },
    filters: {
        statusFilter: function (a) {
            if (a === '1') {
                return "同意"
            } else if (a == '0') {
                return "不同意"
            }
        },
    },
    mounted () {
        this.$store.dispatch("dict/getDicData", ["dt_srm_p0", 'dt_srm_pdr', 'dt_srm_pdt', 'dt_srm_pdu'])
        this.setTableHeight()
        //表格高度自适应
        window.onresize = () => {
            this.setTableHeight()
        }
        this.getList()
    },
    methods: {
        //表格高度计算
        setTableHeight () {
            this.theight = this.$myFun.getSingleTbHeight()
        },
        refresh () {
            this.getList()
        },
        //获取表格数据
        getList () {
            api.queryRecords(this.listQuery).then(res => {
                let options = [this.dt_srm_p0, this.dt_srm_pdr, this.dt_srm_pdt, this.dt_srm_pdu, this.dt_srm_pdr, this.dt_srm_pdt, this.dt_srm_pdu]
                res.data.list = this.$myFun.srmCodeToName(res.data.list, options, ['state', 'regularPaymentType', 'regularSettlementType', 'regularPaymentPeriod', 'feedbackPaymentType', 'feedbackSettlementType', 'feedbackPaymentPeriod'])
                this.list = res.data.list.map(v => {
                    v.regularPayment = v.regularPaymentTypeCN + '/' + v.regularSettlementTypeCN + '/' + v.regularPaymentPeriodCN
                    if (v.feedbackPayment) {
                        v.feedbackPayment = v.feedbackPaymentTypeCN + '/' + v.feedbackSettlementTypeCN + '/' + v.feedbackPaymentPeriodCN
                    }
                    return v
                })
                this.total = res.data.pages.count
            })
        },
        //表格查询
        handleQuery () {
            this.listQuery.currentPage = 1
            this.getList()
        },
        handleCreate () {
            //新增按钮单击事件方法
            this.$refs.addMain.resetTemp()
            this.$refs.addMain.list = []
            this.$refs.addMain.dialogVisible = true
            if (this.$refs.addMain.temp.purchaseOrg === undefined) {
                this.$refs.addMain.Edit = true
            }
        },
        //审批按钮单击事件
        handleApproval () {
            if (this.selectlistRow && this.selectlistRow.length > 0) {
                let status = this.selectlistRow.every(val => {
                    return val.state === 'P0A0' && val.feedbackResult === '0'
                })
                if (status) {
                    this.$refs.approvalList.dataList = this.selectlistRow
                    this.$refs.approvalList.dialogVisible = true
                } else {
                    this.$message({
                        title: '警告',
                        message: '只有已反馈和反馈意见不同意才可审批',
                        type: 'warning',
                        duration: 2000
                    })
                }
            } else {
                this.$message({
                    title: '警告',
                    message: '请勾选记录',
                    type: 'warning',
                    duration: 2000
                })
            }
        },
        //发布按钮单击事件
        handlePublish: throttle(function () {
            if (this.selectlistRow && this.selectlistRow.length > 0) {
                if (this.selectlistRow.state === 'P0B2' && this.selectlistRow.feedbackResult === '1') {
                    let dto = {
                        method: 'publish',
                        rowIds: []
                    }
                    this.selectlistRow.forEach((val, index) => {
                        dto.rowIds[index] = val.rowId
                    })
                    api.updateEffect(dto).then(() => {
                        this.$message({
                            title: '成功',
                            message: '发布成功',
                            type: 'success',
                            duration: 2000
                        })
                        this.getList()
                    })
                } else {
                    this.$message({
                        title: '警告',
                        message: '仅已审批且反馈意见为同意的可发布',
                        type: 'warning',
                        duration: 2000
                    })
                }
            } else {
                this.$message({
                    title: '警告',
                    message: '请勾选记录',
                    type: 'warning',
                    duration: 2000
                })
            }
        }, 2000,
            {
                leading: true,
                trailing: false
            }
        ),
        handleUpdate () {
            //编辑按钮单击事件方法
        },
        handleDelete () {
            //删除按钮单击事件方法
            if (this.selectlistRow && this.selectlistRow.length > 0) {
                this.$confirm("确认要删除该数据项吗?", "提示", {
                    confirmButtonText: "确定",
                    cancelButtonText: "取消",
                    type: "warning"
                }).then(() => {
                    let rowIds = []
                    this.selectlistRow.forEach((val, index) => {
                        rowIds[index] = val.rowId
                    })
                    api.deleteRecord(rowIds).then(() => {
                        this.$message({
                            title: '成功',
                            message: '删除成功',
                            type: 'success',
                            duration: 2000
                        })
                        this.getList()
                    })
                }).catch(() => {
                    this.$message({
                        type: "info",
                        message: "已取消"
                    })
                })
            } else {
                this.$message({
                    title: '警告',
                    message: '请勾选记录',
                    type: 'warning',
                    duration: 2000
                })
            }
        },
        //生效按钮单击事件
        handleEffect: throttle(function () {
            if (this.selectlistRow && this.selectlistRow.length > 0) {
                let dto = {
                    method: 'commit',
                    rowIds: []
                }
                this.selectlistRow.forEach((val, index) => {
                    dto.rowIds[index] = val.rowId
                })
                api.updateEffect(dto).then(() => {
                    this.$message({
                        title: '成功',
                        message: '生效成功',
                        type: 'success',
                        duration: 2000
                    })
                    this.getList()
                })
            } else {
                this.$message({
                    title: '警告',
                    message: '请勾选记录',
                    type: 'warning',
                    duration: 2000
                })
            }
        }, 2000,
            {
                leading: true,
                trailing: false
            }
        ),
        // 表格选择框选中 注:参数为选中的所有行的数组
        selectChange (val) {
            this.selectlistRow = val
        },
        //点击表格某一行
        rowClick (val) {
            this.$refs.tb.toggleRowSelection(val)
        }
    }
};
