import listCar from '../../../components/listCar'
import * as api from "@/api/project/iot/IOT/em"
import { mapState } from 'vuex'
export default {
    name: "accountInfo",
    props: {
        itemData: {
            type: Object,
            default: undefined
        },
        code: {
            type: String,
            default: undefined
        },
        row: {
            type: Object,
            default: undefined
        }
    },
    data () {
        return {
            form: {
                deviceCode: undefined,
                deviceName: undefined,
                brand: undefined,
                typeCode: undefined,
                manufacturersCode: undefined,
                modelNumber: undefined,
                leaveFactoryDate: undefined,
                remark: undefined,
            },
            temp: {
                deviceCode: undefined,
                deviceName: undefined,
                brand: undefined,
                typeCode: undefined,
                manufacturersCode: undefined,
                modelNumber: undefined,
                leaveFactoryDate: undefined,
                remark: undefined,
            },
            namePlate: null,
            namePlateFrom: {
                data: null
            },
        }
    },
    components: { listCar },
    computed: {
        ...mapState({
            iot_attribute_type: state => state.iotDict.iot_attribute_type,
            iot_nameplate_state: state => state.iotDict.iot_nameplate_state // 铭牌参数单位
        }),
        listLength: function () {
            return this.namePlate ? this.namePlate.length : false
        }
    },
    mounted () {
        this.getList()
    },
    methods: {
        getList () {
            Promise.all([api.queryProjectCode(this.code, this.itemData.deviceCode), api.queryNamePlate(this.itemData.projectCode, this.itemData.deviceCode)]).then(
                val => {
                    if (val[0].data && val[0].data.length) {
                        this.form = val[0].data[0]
                    } else {
                        this.form = {}
                    }
                    val[1].data = this.$myFun.srmCodeToName(val[1].data, [this.iot_nameplate_state], ['company'])
                    this.namePlate = val[1].data
                }
            )
        },
        resTemp () {
            this.temp = {}
        }
    }
};
