export default {
    name: 'tabZYcost',
    props: ['tabZYcostList'],
    data () {
        return {
            type: null,
            tableData: [],
        }
    },
    watch: {
        tabZYcostList (val) {
            if (this.type == 'add') {
                let arr = JSON.parse(JSON.stringify(val))
                arr.forEach(i => {
                    i.actuarialUnitPrice = i.unitPrice ? i.unitPrice : 0 //金额精算
                    i.actuarialApportionmentQuantity = i.apportionmentQuantity ? i.apportionmentQuantity : 0 //分摊精算
                    i.actuarialRejectRate = i.rejectRate ? i.rejectRate : 0 //废品率精算

                    i.actuarialApportionmentFee = this.checkValue(i.actuarialUnitPrice / i.actuarialApportionmentQuantity) //单件摊销精算
                    i.actuarialWasteLoss = this.checkValue(i.actuarialUnitPrice / i.actuarialApportionmentQuantity * i.actuarialRejectRate) //废品损失精算
                })
                this.tableData = arr
            } else {
                let arr = JSON.parse(JSON.stringify(val))
                this.tableData = arr
            }

        }
    },
    methods: {
        checkValue (val) {
            if (isNaN(val) || val == Infinity) {
                return undefined
            } else {
                if (val.toString().indexOf('.') !== -1) {
                    val = val.toFixed(2)
                }
                return val
            }
        },
        getSummaries (param) {
            const { columns, data } = param
            const sums = []
            columns.forEach((column, index) => {
                if (index === 0) {
                    sums[index] = '总计'
                    return
                }
                const values = data.map(item => Number(item[column.property]))
                if (!values.every(value => isNaN(value))) {
                    if (column.property == 'partName' || column.property == 'actuarialUnitPrice' || column.property == 'actuarialApportionmentQuantity' || column.property == 'actuarialApportionmentFee' || column.property == 'actuarialRejectRate' || column.property == 'actuarialWasteLoss') {
                        sums[index] = values.reduce((prev, curr) => {
                            const value = Number(curr)
                            if (!isNaN(value)) {
                                return prev + curr
                            } else {
                                return prev
                            }
                        }, 0)
                        // sums[index] += ' 元'
                    } else {
                        sums[index] = '--'
                    }
                } else {
                    sums[index] = 0
                }
            })
            columns.forEach((val, index) => {
                //单件摊销精算
                if (val.property == 'actuarialApportionmentFee') {
                    this.$store.dispatch('getData/getAfee', sums[index])
                }
                //废品损失精算
                if (val.property == 'actuarialWasteLoss') {
                    this.$store.dispatch('getData/getWasteLossZY', sums[index])
                }
            })
            return sums
        },
        getPriceCost (row) {
            this.tableData.forEach(i => {
                if (i.rowId == row.rowId) {
                    i.actuarialApportionmentFee = this.checkValue(i.actuarialUnitPrice / i.actuarialApportionmentQuantity) //单件摊销精算
                    i.actuarialWasteLoss = this.checkValue(i.actuarialUnitPrice / i.actuarialApportionmentQuantity * i.actuarialRejectRate) //废品损失精算
                }
            })
        },
        initData (val) {
            this.type = val
            this.tableData = []
        }
    }
}
