import * as api from '@/api/project/mes/dataBase/unqualifiedProduct'
import Pagination from '@/components/Pagination'
import { mapState } from 'vuex'

export default {
    name: 'productStateManagement',
    components: { Pagination },
    data() {
        return {
            list: null,
            total: 0,
            theight: 0, //表格高度
            listQuery: {
                page: true,
                currentPage: 1,
                pageSize: 10,
                workOrderCode: undefined,
                state: undefined
            },
            selected: undefined,
            tableKey: 0
        }
    },
    created() {
        this.getList()
    },
    computed: {
        ...mapState({
            dt_quality_inspection_state: state =>
                state.mesDict.dt_quality_inspection_state
        })
    },
    mounted() {
        this.setTableHeight()
        //表格高度自适应
        window.onresize = () => {
            this.setTableHeight()
        }
        this.getList()
        this.$store.dispatch('mesDict/getDicData', [
            'dt_quality_inspection_state'
        ])
    },
    methods: {
        getList() {
            api.queryRecords(this.listQuery).then(res => {
                this.list = res.data.list
                this.total = res.data.pages.count
            })
        },
        //表格高度计算
        setTableHeight() {
            this.theight = this.$myFun.getSingleTbHeight()
        },
        handleQuery() {
            //查询
            this.listQuery.currentPage = 1
            this.getList()
        },
        selectList(val) {
            this.selected = val[val.length - 1]
            if (val.length > 1) {
                this.$refs.list.clearSelection()
                this.$refs.list.toggleRowSelection(
                    val[val.length - 1],
                    'selected'
                )
            }
        },
        selectListRow(val) {
            this.selected = val
            this.$refs.list.clearSelection()
            this.$refs.list.toggleRowSelection(val, 'selected')
        },
        handleStatus(status) {
            if (this.selected == undefined) {
                this.$message({
                    title: '警告',
                    message: '请选择产品',
                    type: 'warning'
                })
                return false
            }
            api.patchRecords(this.selected.rowId, status).then(res => {
                if (res.status == 201) {
                    this.$message({
                        title: '成功',
                        message: '状态已修改',
                        type: 'success'
                    })
                    this.getList()
                }
            })
        }
    }
}
