import Pagination from "@/components/Pagination"; // Secondary package based on el-pagination
import { selectlists, creat, deletelist, updatalist } from "@/api/system/resource";

// import waves from "@/directive/waves"; // Waves directive
// import { parseTime } from "@/utils";
// import { selecttree } from "@/api/system/organization";
export default {
    name: "systemResource",
    components: { Pagination },
    // directives: { waves },
    data () {
        return {
            tableKey: 0,
            list: [],
            total: 0,
            listLoading: true,
            theight: 0,//表格高度
            listQuery: {
                currentPage: 1,
                pageSize: 15,
                page: 1
            },
            readonly: false,
            temp: {
                endpointCode: undefined,
                endpointName: undefined,
                url: undefined,
                method: undefined,
                remark: undefined
            },
            dialogFormVisible: false,
            dialogFormUser: false,
            dialogStatus: "",
            rules: {
                endpointCode: [
                    { required: true, message: "资源编码不能为空", trigger: "blur" },
                    { max: 100, message: "组织编码长度不能超过100", trigger: "blur" }
                ],
                endpointName: [
                    { required: true, message: "资源名称不能为空", trigger: "blur" },
                    { max: 100, message: "编码名称长度不能超过100", trigger: "blur" }
                ],
                method: [
                    { required: true, message: this.$t("validate.required"), trigger: "blur" }
                ],
                url: [
                    { required: true, message: this.$t("validate.required"), trigger: "blur" }
                ]
            },
            dialogPvVisible: false,
            pvData: []
        };
    },
    created () {
        this.getList();
    },
    mounted () {
        this.setTableHeight();
        //表格高度自适应
        window.onresize = () => {
            this.setTableHeight()
        };
        this.getList();
    },
    methods: {
        getList () {//查询数据
            this.listLoading = true;
            selectlists(this.listQuery).then(response => {
                this.list = response.data.list;
                this.total = response.data.pages.count;

                // Just to simulate the time of the request
                setTimeout(() => {
                    this.listLoading = false;
                }, 1.5 * 100);
            });
        },
        //表格高度计算
        setTableHeight () {
            this.theight = this.$myFun.getSingleTbHeight();
        },
        handleFilter () {//查询按钮
            this.getList();
        },
        resetTemp () {//重置
            this.temp = {
                endpointCode: undefined,
                endpointName: undefined,
                url: undefined,
                method: undefined,
                remark: undefined
            };
        },
        handleCreate () {
            this.resetTemp();
            this.dialogStatus = "create";
            this.dialogFormVisible = true;
            this.readonly = false; //可以编写
            this.$nextTick(() => {
                this.$refs["dataForm"].clearValidate();
            });
        },
        createData () {
            this.$refs["dataForm"].validate(valid => {
                if (valid) {
                    creat(this.temp).then(() => {
                        this.getList();
                        this.dialogFormVisible = false;
                        this.$message({
                            title: "成功",
                            message: "创建成功",
                            type: "success",
                            duration: 2000
                        });
                    });
                }
            });
        },
        handleUpdate () {
            if (this.selectlistRow && this.selectlistRow.length == 1) {
                this.readonly = true; //不可以编写
                this.temp = this.selectlistRow[0]; // copy obj
                this.dialogStatus = "update";
                this.dialogFormVisible = true;
                this.$nextTick(() => {
                    this.$refs["dataForm"].clearValidate();
                });
            } else {
                this.$message({
                    title: "警告",
                    message: "请选择一条信息",
                    type: "warning"
                });
            }
        },
        updateData () {
            this.$refs["dataForm"].validate(valid => {
                if (valid) {
                    updatalist(this.temp.endpointCode, this.temp).then(() => {
                        this.getList();
                        this.dialogFormVisible = false;
                        this.$message({
                            title: "成功",
                            message: "更新成功",
                            type: "success",
                            duration: 2000
                        });
                    });
                }
            });
        },
        // 获取表格选中时的数据
        selectRow (val) {
            this.selectlistRow = val;
        },
        handleDelete () {//删除列表
            if (this.selectlistRow && this.selectlistRow.length == 1) {
                let ids = [];
                this.selectlistRow.forEach((val, index) => {
                    ids[index] = val.endpointCode;
                });
                deletelist(ids).then(() => {
                    this.getList(),
                        this.$message({
                            title: "成功",
                            message: "删除成功",
                            type: "success",
                            duration: 2000
                        });
                });
            } else {
                this.$message({
                    title: "警告",
                    message: "请选择一条信息",
                    type: "warning"
                });
            }
        }
    }
};
