import * as api from "@/api/project/iot/IOT/ga"
import Pagination from "@/components/Pagination"
import gatewaySelect from "../../components/gatewaySelect"
import { mapState } from 'vuex'
import throttle from 'lodash/throttle'
export default {
    name: "GRMain",
    props: ['code', 'projectList'],
    components: { Pagination, gatewaySelect },
    computed: {
        ...mapState({
            iot_operator: state => state.iotDict.iot_operator,
            iot_networking_way: state => state.iotDict.iot_networking_way,
            iot_reporting_period: state => state.iotDict.iot_reporting_period,
            iot_state_gateway: state => state.iotDict.iot_state_gateway,
        })
    },
    watch: {
        projectList (val) {
            if (!this.projectCode) {
                this.projectCode = val[0].projectCode
                this.getList()
            }
        },
        code (val) {
            if (val) {
                this.projectCode = val
                this.getList()
            }
        }
    },
    data () {
        return {
            keyList: [],
            listLoading: false,
            theight: 0,//表格高度
            projectCode: this.code,
            list: [],
            total: 0,
            listQuery: {
                page: true,
                currentPage: 1,
                pageSize: 10,
                gatewayName: undefined
            },
            dialogFormVisible: false,
            temp: {
                gatewayName: undefined,
                gatewaySn: undefined,
                gatewayType: undefined,
                installationAddress: undefined,
                networkingWay: undefined,
                operator: undefined,
                projectCode: undefined,
                remark: undefined,
                reportingPeriod: undefined
            },
            rules: {
                gatewayName: [
                    {
                        required: true,
                        message: '网关名称不能为空',
                        trigger: 'blur'
                    },
                    { max: 100, message: '长度不能超过100', trigger: 'blur' },
                    {
                        pattern: /^[a-zA-Z0-9#\u4e00-\u9fa5]+$/,
                        message: '支持大小写中英文及#符号',
                        trigger: 'blur'
                    }
                ],
                gatewaySn: [
                    {
                        required: true,
                        message: '序列号名称不能为空',
                        trigger: ['blur', 'chnage']
                    },
                ],
                gatewayType: [
                    {
                        required: true,
                        message: '请选择产品型号',
                        trigger: ['blur', 'chnage']
                    },
                ],
                networkingWay: [
                    {
                        required: true,
                        message: '请选择联网方式',
                        trigger: ['blur', 'chnage']
                    },
                ],
                operator: [
                    {
                        required: true,
                        message: '请选择上报周期',
                        trigger: ['blur', 'chnage']
                    },
                ],
                reportingPeriod: [
                    {
                        required: true,
                        message: '请选择产品型号',
                        trigger: ['blur', 'chnage']
                    },
                ],
            },
            tableKey: 0 //表格索引
        }
    },
    mounted () {
        this.setTableHeight()
        //表格高度自适应
        window.onresize = () => {
            this.setTableHeight()
        }
        this.getList()
    },
    methods: {
        stateClass (state) {
            if (state == '0') {
                return 'iot-warning'
            } else if (state == '1') {
                return 'iot-success'
            } else if (state == '2') {
                return 'iot-info'
            }
        },
        //获取表格数据
        getList () {
            this.listLoading = true
            api.queryGateways(this.projectCode, this.listQuery).then(response => {
                this.list = response.data.list
                this.list = this.$myFun.srmCodeToName(response.data.list, [this.iot_networking_way, this.iot_state_gateway], ['networkingWay', 'iotState'])
                this.total = response.data.pages.count
                setTimeout(() => {
                    this.listLoading = false
                }, 1.5 * 100)
            })
        },
        // 生成SN
        getSN: throttle(
            function () {
                api.getGenerate(this.projectCode).then(res => {
                    this.temp.gatewaySn = res.data
                })
            },
            3000,
            {
                leading: true,
                trailing: false
            }
        ),
        // 获取型号
        getGatewayType (val) {
            this.temp.gatewayType = val
        },
        //表格查询
        handleQuery: throttle(
            function () {
                this.listQuery.currentPage = 1
                this.getList()
            },
            2000,
            {
                leading: true,
                trailing: false
            }
        ),
        //新增按钮单击事件方法
        handleCreate () {
            this.dialogFormVisible = true
        },
        create: throttle(
            function () {
                this.$refs.tempRef.validate(valid => {
                    if (valid) {
                        this.temp.projectCode = this.projectCode
                        api.addGateway(this.projectCode, this.temp).then(() => {
                            this.getList()
                            this.dialogFormVisible = false
                            this.resTemp()
                            this.$notify({
                                title: '成功',
                                message: '新增成功',
                                type: 'success',
                                duration: 2000
                            })
                        })
                    } else {
                        return false
                    }
                })
            },
            2000,
            {
                leading: true,
                trailing: false
            }
        ),
        //删除按钮单击事件方法
        handleDelete (gatewayCode, iotState) {
            if (iotState !== '2') {
                return this.$message({
                    title: this.$t('message.warning'),
                    message: "当前网关已物联，不能删除！",
                    type: "warning"
                })
            }
            this.$confirm('此操作将删除所选中数据, 是否继续?', '提示', {
                confirmButtonText: '确定',
                cancelButtonText: '取消',
                type: 'warning'
            })
                .then(() => {
                    api.deleteGateway(this.projectCode, gatewayCode).then(() => {
                        this.getList()
                        this.$message({
                            title: '成功',
                            message: '删除成功',
                            type: 'success',
                            duration: 2000
                        })
                    }).catch(() => {
                        this.getList()
                    })
                })
                .catch(() => {
                    this.$message({
                        type: 'info',
                        message: '已取消删除'
                    })
                })
            // 
        },
        resTemp () {
            this.temp = {
                gatewayName: undefined,
                gatewaySn: undefined,
                gatewayType: undefined,
                installationAddress: undefined,
                networkingWay: undefined,
                operator: undefined,
                projectCode: undefined,
                remark: undefined,
                reportingPeriod: undefined
            }
        },
        //表格高度计算
        setTableHeight () {
            this.theight = this.$myFun.getSingleTbHeight()
        },
        toInfo (row) {
            this.$emit('setRow', row)
            this.$emit('setCode', row.projectCode)
            this.$emit('setPage', 'GR-Info')
        },
    }
};
