.app-container {
    background-color: #fff;
    margin: 0;
    box-sizing: border-box;
}
.el-row {
    position: relative;
    height: 100%;
    .el-col {
        display: flex;
        flex-direction: column;
        justify-content: space-between;
        height: 100%;
        &.left {
            justify-content: flex-start;
        }
        &.center {
            display: flex;
            flex-direction: column;
            justify-content: center;
            .img {
                flex: 6;
                display: flex;
                img {
                    flex: 1;
                    background-size: 100%;
                }
            }
            .bar {
                flex: 1;
                background-color: #000;
            }
            .table {
                flex: 3;
            }
        }
        &.right {
            display: flex;
            flex-direction: column;
            justify-content: center;
            .line {
                flex: 1;
                display: flex;
                flex-direction: column;
                border-bottom: 1px solid #cecece;
                .card-box {
                    padding: 0 20px 0 10px;
                    flex: 1;
                    display: flex;
                    justify-content: space-between;
                    align-items: center;
                    span:nth-of-type(2) {
                        font-size: 24px;
                        font-weight: 700;
                        background-image: -webkit-gradient(
                            linear,
                            0 0,
                            0 bottom,
                            from(#0568e1f5),
                            to(#05b4f7)
                        );
                        background-clip: text;
                        -webkit-text-fill-color: transparent;
                        text-shadow: 5px 5px 4px #909399;
                    }
                }
                .title {
                    font-size: 12px;
                    border-bottom: 1px solid #cecece;
                    padding: 5px;
                    box-sizing: border-box;
                }
            }
        }
    }
}
