import { queryRecords } from "@/api/project/iot/PM/equipmentProducts"
import { queryManufacturers } from "@/api/project/iot/IOT/em"
import { getDevice } from "@/api/project/iot/MOM/deviceQuery"
import { queryProjects } from "@/api/project/iot/IOT/project"
import Pagination from "@/components/Pagination"
import { mapState } from 'vuex'
import throttle from 'lodash/throttle'
export default {
    name: "GPMain",
    components: { Pagination },
    computed: {
        ...mapState({
            iot_state_device: state => state.iotDict.iot_state_device,
        })
    },
    watch: {
        projectList (val) {
            if (!this.code) {
                this.code = val[0].projectCode
                this.getList()
            }
        }
    },
    data () {
        return {
            theight: 0,//表格高度
            listLoading: false,
            projectList: null,
            deviceList: null,
            manufacturersList: null,
            list: null,
            total: 0,
            code: undefined,
            listQuery: {
                page: true,
                currentPage: 1,
                pageSize: 15,
                projectCode: undefined,
                typeCode: undefined,
                deviceName: undefined,
                iotState: undefined
            },
            tableKey: 0 //表格索引
        }
    },
    mounted () {
        this.setTableHeight()
        window.onresize = () => {
            this.setTableHeight()
        }
        this.getProject()
    },
    methods: {
        //获取表格数据
        getList () {
            this.listLoading = true
            getDevice(this.listQuery).then(response => {
                this.list = response.data.list
                let options = [this.iot_state_device]
                this.list = this.$myFun.srmCodeToName(
                    response.data.list,
                    options,
                    ['iotState']
                )
                if (response.data.list && this.manufacturersList) {
                    for (let i = 0; i < response.data.list.length; i++) {
                        for (let j = 0; j < this.manufacturersList.length; j++) {
                            if (response.data.list[i].manufacturersCode == this.manufacturersList[j].manufacturersCode) {
                                response.data.list[i].manufacturersName = this.manufacturersList[j].manufacturersName
                                break
                            }
                        }
                    }
                }

                this.total = response.data.pages.count
                setTimeout(() => {
                    this.listLoading = false
                }, 1.5 * 100)
            })
        },
        getProject () {
            Promise.all([queryManufacturers(), queryProjects(), queryRecords()]).then(res => {
                this.manufacturersList = res[0].data.list
                this.projectList = res[1].data
                this.deviceList = res[2].data
            }
            )
        },
        //表格查询
        handleQuery: throttle(
            function () {
                this.listQuery.currentPage = 1
                this.getList()
            },
            2000,
            {
                leading: true,
                trailing: false
            }
        ),
        toInfo (row) {
            this.$emit('setRow', row)
            this.$emit('setCode', row.projectCode)
            this.$emit('setPage', 'info')
        },
        //表格高度计算
        setTableHeight () {
            this.theight = this.$myFun.getSingleTbHeight()
        }
    }
};
