import * as api from "@/api/project/srm/procurementDemand/newproductWlwh"
import { DICT_CODE } from '@/utils/constant'
import caiGou from '@/components/Select/xtpt/caigou'//下拉组件
import Pagination from "@/components/Pagination"
import { mapState } from "vuex"
import addMain from "./dialog/addMain"
import ImportFile from "./dialog/importFile"
import upload from "./dialog/upload"

export default {
    name: "newproductWlwh",
    components: { Pagination, caiGou, addMain, ImportFile, upload },
    data () {
        return {
            DICT_CODE: DICT_CODE,
            theight: 0,//表格高度
            isSingle: true,//表格是否单选 点击各按钮根据流程逻辑控制单多选
            list: [],
            total: 0,
            listQuery: {
                //查询
                page: true,
                currentPage: 1,
                pageSize: 15
            },
            dialogVisible: false,
            importDialogVisible: false,
            dialogVisibleUpload: false,
            currentSelectedRow: null,//当前选中的行
            selectedRows: null,//多选时选中的所有行
            tableKey: 0, //表格索引
            modalNo1: 'EXTP-BAS-0019',  // 导入模板编号
            modalNo2: 'EXTP-BAS-0020',  // 导入模板编号
        }
    },
    created () { },
    computed: {
        ...mapState({
            dt_materiel_type: state => state.dict.dt_materiel_type,
            dt_srm_p0: state => state.dict.dt_srm_p0,
            dt_unit_type: state => state.dict.dt_unit_type,
            dt_yes_or_no: state => state.dict.dt_yes_or_no,
            dt_srm_pb9: state => state.dict.dt_srm_pb9,
        })
    },
    mounted () {
        this.$store.dispatch("dict/getDicData", ['dt_materiel_type', 'dt_srm_p0', 'dt_unit_type', 'dt_yes_or_no', 'dt_srm_pb9'])
        this.setTableHeight()
        //表格高度自适应
        window.onresize = () => {
            this.setTableHeight()
        }
        this.getList()
    },
    watch: {
        'dt_srm_pb9': function () {
            this.getList()
        }
    },
    methods: {
        //表格高度计算
        setTableHeight () {
            this.theight = this.$myFun.getSingleTbHeight()
        },
        //新增编辑成功之后刷新数据
        refresh () {
            this.getList()
        },
        //获取表格数据
        getList () {
            // console.log(JSON.stringify(this.listQuery))
            api.queryRecords(this.listQuery).then(res => {
                let options = [this.dt_materiel_type, this.dt_srm_p0, this.dt_unit_type, this.dt_yes_or_no, this.dt_srm_pb9]
                res.data.list = this.$myFun.srmCodeToName(res.data.list, options, ['materialType', 'state', 'measurement', 'heavyParts', 'sourceType'])
                this.list = res.data.list
                this.total = res.data.pages.count
                this.list.forEach(v => {
                    if(!v.creatorName && !v.materialTypeCN && !v.measurementCN && !v.heavyPartsCN && !v.sourceTypeCN) {
                        v.creatorName = v.creator
                        v.materialTypeCN = v.materialType
                        v.measurementCN = v.measurement
                        v.heavyPartsCN = v.heavyParts
                        v.sourceTypeCN = v.sourceType
                    }
                })
            })
        },
        //表格查询
        handleQuery () {
            this.listQuery.currentPage = 1
            this.getList()
        },
        //新增按钮单击事件方法
        handleCreate () {
            this.$refs.addMain.resetTemp()
            this.$refs.addMain.dialogStatus = 'create'
            this.$refs.addMain.dialogVisible = true
        },
        //编辑按钮单击事件方法
        handleUpdate (index, val) {
            console.log(index, val)
            this.$refs.addMain.dialogStatus = 'update'
            this.$refs.addMain.dialogVisible = true
            this.$refs.addMain.temp = { ...val }
            this.$refs.addMain.rowId = val.rowId
        },
        //删除按钮单击事件方法
        handleDelete (index, val) {
            this.$confirm("是否删除该信息", "提示", {
                confirmButtonText: "确定",
                cancelButtonText: "取消",
                type: "warning"
            }).then(() => {
                api.deleteRecord(val.rowId).then(() => {
                    this.$message({
                        title: '成功',
                        message: '删除成功',
                        type: 'success',
                        duration: 2000
                    })
                    this.getList()
                })
            }).catch(() => {
                this.$message({
                    type: "info",
                    message: "已取消"
                })
            })
        },
        //打包
        handlePack () {
            if (this.selectlistRow && this.selectlistRow.length > 0) {
                let dto = {
                    method: 'pack',
                    rowIds: []
                }
                this.selectlistRow.forEach((val, index) => {
                    dto.rowIds[index] = val.rowId
                })
                api.publish(dto).then(() => {
                    this.$message({
                        title: '成功',
                        message: '打包成功',
                        type: 'success',
                        duration: 2000
                    })
                    this.getList()
                })
            } else {
                this.$message({
                    title: '警告',
                    message: '请勾选记录',
                    type: 'warning',
                    duration: 2000
                })
            }
        },
        //取消打包
        handleCancelPack () {
            if (this.selectlistRow && this.selectlistRow.length > 0) {
                let dto = {
                    method: 'unpack',
                    rowIds: []
                }
                this.selectlistRow.forEach((val, index) => {
                    dto.rowIds[index] = val.rowId
                })
                api.publish(dto).then(() => {
                    this.$message({
                        title: '成功',
                        message: '取消打包成功',
                        type: 'success',
                        duration: 2000
                    })
                    this.getList()
                })
            } else {
                this.$message({
                    title: '警告',
                    message: '请勾选记录',
                    type: 'warning',
                    duration: 2000
                })
            }
        },
        //发布
        handlePublish () {
            if (this.selectlistRow && this.selectlistRow.length > 0) {
                let dto = {
                    method: 'publish',
                    rowIds: []
                }
                this.selectlistRow.forEach((val, index) => {
                    dto.rowIds[index] = val.rowId
                })
                api.publish(dto).then(() => {
                    this.$message({
                        title: '成功',
                        message: '发布成功',
                        type: 'success',
                        duration: 2000
                    })
                    this.getList()
                })
            } else {
                this.$message({
                    title: '警告',
                    message: '请勾选记录',
                    type: 'warning',
                    duration: 2000
                })
            }
        },
        //导入
        handleImport () {
            this.importDialogVisible = true
            this.$nextTick(() => {
                // 传表格数据
                this.$refs.importDialog.listQuery = this.listQuery
                // 传递模板下载文件名称
                this.$refs.importDialog.excelName = '新品物料维护.xlsx'
                this.$refs.importDialog.getList()
            })
        },
        // 弹框右上角关闭
        handleClose () {
            this.importDialogVisible = false
            // 清空已选文件列表
            this.$refs.importDialog.fileList = []
            this.getList()
        },
        //上传
        handleUpload () {
            // this.dialogVisibleUpload = true
        },
        // 表格选择框选中 注:参数为选中的所有行的数组
        selectChange (val) {
            this.selectlistRow = val
        },
        //点击表格某一行
        rowClick (val) {
            this.$refs.tb.toggleRowSelection(val)
        }
    }
};
