import * as importApi from '@/api/project/mes/import/index'
import Pagination from '@/components/Pagination'

export default {
    name: 'Customer',
    components: { Pagination },
    props: ['modalNo'], //导入弹框名称,模板号
    data() {
        return {
            listData: {
                title: [],
                data: []
            },
            modelDetailparI: {
                excelCode: this.modalNo,
                currentPage: 0,
                pageSize: 10
            },
            modelDetailpar: {
                excelCode: this.modalNo,
                timestamp: null,
                currentPage: 0,
                pageSize: 10
            },
            total: 0,
            fileList: [],
            files: null,
            uploadurl: ''
        }
    },
    mounted() {},
    computed: {
        myHeader() {
            return {
                authToken: window.sessionStorage.getItem('authToken')
            }
        }
    },
    methods: {
        getUploadData() {
            let noError = true
            importApi.queryTempDetail(this.modelDetailpar).then(res => {
                res = res.data
                this.listData.data = []
                res.list.forEach(itemd => {
                    this.listData.data.push(itemd)
                    this.total = res.pages.count
                    if (itemd.errorMsg && noError) {
                        noError = false
                    }
                })

                if (noError) {
                    this.$message({
                        message: '上传成功',
                        type: 'success'
                    })
                } else {
                    this.$message.error(
                        '上传文件内容有误，见表格最后一行，请导出修改正确后重新进行导入操作'
                    )
                }
            })
        },
        getList() {
            importApi.queryModelDetail(this.modelDetailparI).then(res => {
                res = res.data
                res.list.forEach(item => {
                    this.listData.title.push(item)
                })
            })
        },
        fileExceed() {
            this.$message.error('一次只能上传一个文件')
        },
        beforeUpload(file) {
            this.files = file
            const extension = file.name.split('.')[1] === 'xls'
            const extension2 = file.name.split('.')[1] === 'xlsx'
            const isLt2M = file.size / 1024 / 1024 < 5
            if (!extension && !extension2) {
                this.$message.warning('上传模板只能是 xls、xlsx格式!')
                return
            }
            if (!isLt2M) {
                this.$message.warning('上传模板大小不能超过 5MB!')
                return
            }
            this.fileName = file.name
            return false // 返回false不会自动上传
        },
        submitUpload() {
            this.modelDetailpar.timestamp = new Date().getTime()
            // this.uploadurl = process.env.IWMS_BASE_API_V1+`/excels/${this.modalNo}/template`
            // this.$refs.upload.submit();
            if (!this.fileList) {
                this.$message.warning('请选择要上传的文件！')
                return false
            }

            let fileFormData = new FormData()
            fileFormData.append('file', this.fileList[0]) //filename是键，file是值，就是要传的文件，test.zip是要传的文件名
            importApi
                .uploadExcel(
                    this.modalNo,
                    this.fileList[0],
                    this.modelDetailpar.timestamp
                )
                .then(res => {
                    if (res.status === 200) {
                        this.$message.success('上传成功')
                    }
                    this.listData.title.push({
                        fieldCode: 'errorMsg',
                        fieldName: '错误信息'
                    })
                    this.getUploadData()
                })
        },
        // 自定义上传
        uploadFile(item) {
            this.modelDetailpar.timestamp = new Date().getTime()
            const form = new FormData()
            form.append('file', item.file)
            let fileFormData = new FormData()
            fileFormData.append('file', this.fileList[0])
            importApi
                .uploadExcel(
                    this.modalNo,
                    fileFormData,
                    this.modelDetailpar.timestamp
                )
                .then(res => {
                    if (res.status === 200) {
                        this.$message.success('上传成功')
                    }
                    this.listData.title.push({
                        fieldCode: 'errorMsg',
                        fieldName: '错误信息'
                    })
                    this.getUploadData()
                })
        },
        handleRemove(file, fileList) {
            this.fileList = fileList
        },
        handleChangeFile(file, fileList) {
            this.fileList = fileList
        },
        //模板下载
        handleDownload() {
            importApi.downloadModal(this.modalNo).then(res => {
                this.download(res.data, 'excel.xlsx')
            })
        },
        // 下载文件
        download(data, filename) {
            if (!data) {
                return
            }
            let url = window.URL.createObjectURL(new Blob([data]))
            let link = document.createElement('a')
            link.style.display = 'none'
            link.href = url
            link.setAttribute('download', filename)

            document.body.appendChild(link)
            link.click()
        },
        //excel文件导出
        handleExport() {
            if (this.modelDetailpar.timestamp) {
                importApi
                    .downloadExcel(this.modalNo, {
                        timestamp: this.modelDetailpar.timestamp.toString()
                    })
                    .then(res => {
                        let filename =
                            decodeURI(
                                res.headers['content-disposition']
                                    .split(';')[1]
                                    .split('=')[1]
                            ) || 'excel.xlsx'
                        this.download(res.data, filename)
                    })
            } else {
                this.$message.warning('请先上传文件解析后再导出！')
            }
        }
    }
}
