import * as api from "@/api/system/role";
import Pagination from "@/components/Pagination";
import { mapState } from "vuex";
export default {
    name: "jsgl",
    components: { Pagination },
    props: ['unMutiple'],
    data () {
        return {
            modalnum: null,//模板编号，非模板页面可删
            theight: 0,//表格高度
            isSingle: true,//表格是否单选 点击各按钮根据流程逻辑控制单多选
            list: [],
            listSelected: [],
            total: 0,
            listQuery: {
                //查询
                page: true,
                currentPage: 1,
                pageSize: 15
            },
            currentSelectedRow: null,//当前选中的行
            selectedRows: null,//多选时选中的所有行
            tableKey: 0 //表格索引
        };
    },
    created () { },
    computed: {
        ...mapState({
            dt_role_type: state => state.dict.dt_role_type
        })
    },
    mounted () {
        this.$store.dispatch("dict/getDicData", ["dt_role_type"]);

        this.getList();
    },
    methods: {
        //模板切换事件，非模板页面可删
        changeMoudle (val) {
            this.$emit('changeSelect', val)
        },

        //获取表格数据
        getList () {
            api.selectrole(this.listQuery).then(response => {
                this.list = response.data.list;
                this.total = response.data.pages.count;
                // Just to simulate the time of the request
                setTimeout(() => {
                    this.listLoading = false;
                }, 1.5 * 100);
            });
        },
        //表格查询
        handleQuery () {
            this.listQuery.currentPage = 1;
            this.getList();
        },
        //表格显示条数变化
        handleSizeChange (val) {
            this.listQuery.pageSize = val;
            this.getList();
        },
        //表格当前页变化
        handleCurrentChange (val) {
            this.listQuery.currentPage = val;
            this.getList();
        },
        handleCreate () {
            //新增按钮单击事件方法
        },
        handleUpdate () {
            //编辑按钮单击事件方法
        },
        handleDelete () {
            //删除按钮单击事件方法
        },
        //检查是否取消勾选
        checkUnsel (val, row) {
            let isuncheck = true;
            val.forEach(item => {
                if (item.materielCode === row.materielCode) {
                    isuncheck = false;
                }
            });
            return isuncheck;
        },
        // 表格选择框选中 注:参数为选中的所有行的数组
        selectMain (val, row) {
            if (this.unMutiple) {
                let isuncheck = this.checkUnsel(val, row);
                if (!isuncheck) {
                    let arr = [];
                    arr.push(val[val.length - 1])
                    this.listSelected = arr;
                    if (val.length > 1) {
                        this.$refs.tb.clearSelection()
                        this.$refs.tb.toggleRowSelection(val[val.length - 1], 'selected')
                    }
                } else {
                    this.listSelected = [];
                }
            } else {
                let isuncheck = this.checkUnsel(val, row);
                if (!isuncheck) {
                    if (this.listSelected) {
                        val.forEach((items, indexv) => {
                            let isSelected = false;
                            this.listSelected.forEach((item) => {
                                if (item.roleCode === items.roleCode) {
                                    isSelected = true
                                }
                            })
                            if (!isSelected) {
                                this.listSelected.push(val[indexv]);
                            }
                        })
                    } else {
                        this.listSelected = val;
                    }
                } else {
                    this.listSelected = this.listSelected.filter(item => {
                        return item.roleCode !== row.roleCode;
                    })
                }
            }
        }

    }
};
