import { queryDeviceBinds, updateDeliver, queryCurve } from "@/api/project/iot/IOT/db"
export default {
    name: "DBTimeData",
    props: {
        itemData: {
            type: Object,
            default: undefined
        },
        code: {
            type: String,
            default: undefined
        }
    },
    watch: {
        itemData (val) {
            if (val) {
                this.getList()
            }
        }
    },
    data () {
        return {
            listLoading: false,
            theight: 0,//表格高度
            list: [],
            tableKey: 0,//表格索引
            pointCode: undefined,
            listQuery: {
                pointEndTime: undefined,
                pointStartTime: undefined
            },
            dialogFormVisible: false,
            chartList: null
        }
    },
    mounted () {
        this.setTableHeight()
        //表格高度自适应
        window.onresize = () => {
            this.setTableHeight()
        }
    },
    methods: {
        //获取表格数据
        getList () {
            this.listLoading = true
            queryDeviceBinds(this.code, this.itemData.gatewayCode, this.itemData.deviceCode).then(response => {
                this.$emit('getSubList', response.data)
                this.list = response.data
                setTimeout(() => {
                    this.listLoading = false
                }, 1.5 * 100)
            })
        },
        // 交付
        pay (row, pointCode) {
            this.$confirm('此操作将交付所选中数据, 是否继续?', '提示', {
                confirmButtonText: '确定',
                cancelButtonText: '取消',
                type: 'warning'
            })
                .then(() => {
                    updateDeliver(this.code, this.itemData.gatewayCode, this.itemData.deviceCode, pointCode).then(() => {
                        this.getList()
                        this.$message({
                            title: '成功',
                            message: '交付成功',
                            type: 'success',
                            duration: 2000
                        })
                    })

                })
                .catch(() => {
                    this.$message({
                        type: 'info',
                        message: '已取消交付'
                    })
                })

        },
        // 查看曲线
        handleViewCurves (pointCode) {
            this.pointCode = pointCode
            this.dialogFormVisible = true
            this.handleQuery()
        },
        // 搜索曲线
        handleQuery () {
            queryCurve(this.code, this.itemData.gatewayCode, this.itemData.deviceCode, this.pointCode, this.listQuery).then(res => {
                this.chartList = res.data
                this.drawLine()
            })
        },
        drawLine () {
            let xAxisData = this.chartList.map(item => {
                return item.pointTime
            })
            let data = this.chartList.map(item => {
                return {
                    value: item.pointValue,
                    name: item.pointName,
                }
            })
            // 基于准备好的dom，初始化echarts实例
            let myChart = this.$echarts.init(this.$refs.myChart)
            // 绘制图表
            myChart.setOption({
                title: { text: '' },
                tooltip: {
                    formatter: '{b}<br/>{c}'
                },
                xAxis: {
                    data: xAxisData
                },
                yAxis: {},
                series: [
                    {
                        name: '',
                        type: 'line',
                        data: data
                    }
                ]
            })
        },
        //表格高度计算
        setTableHeight () {
            this.theight = this.$myFun.getSingleTbHeight() - 30
        },
    }
};
