.box {
    margin: 100px auto 0;
    width: 80%;
}
.clear:after {
    content: '';
    display: block;
    clear: both;
}
.slider {
    position: relative;
    margin: 20px 0;
    height: 10px;
    background: #e4e7ed;
    border-radius: 5px;
    cursor: pointer;
}
.slider .process {
    position: absolute;
    left: 0;
    top: 0;
    width: 112px;
    height: 10px;
    border-radius: 5px;
    background: #409eff;
}
.slider .thunk {
    position: absolute;
    left: 100px;
    top: -7px;
    width: 20px;
    height: 20px;
}
.slider .block {
    width: 20px;
    height: 20px;
    border-radius: 50%;
    border: 2px solid #409eff;
    background: rgba(255, 255, 255, 1);
    transition: 0.2s all;
}
.slider .tips {
    position: absolute;
    left: -13px;
    bottom: 20px;
    min-width: 32px;
    text-align: center;
    height: 24px;
    line-height: 24px;
}
.slider .tips span {
    padding: 0 4px;
    min-width: 45px;
    display: inline-block;
    white-space: normal;
}
.slider .block:hover {
    transform: scale(1.1);
    opacity: 0.6;
}
