import { mapState } from "vuex"
import dialogWlxzbyzsj from '@/components/Dialog/dialogMaterial.vue'//主数据物料选择
import throttle from 'lodash/throttle'

export default {
    components: { dialogWlxzbyzsj },
    data () {
        return {
            dialogStatus: "",
            dialogVisible: false,
            person: "",
            dialogPersonVisible: false,
            titlePerson: "",
            temp: {
                rowId: undefined
            },
        }
    },
    computed: {
        ...mapState({
            dt_srm_pc6: state => state.dict.dt_srm_pc6,
            dt_unit_type: state => state.dict.dt_unit_type,
            dt_srm_pdk: state => state.dict.dt_srm_pdk,
            dt_srm_pca: state => state.dict.dt_srm_pca,
            dt_srm_pc9: state => state.dict.dt_srm_pc9,
        })
    },
    mounted () {
        this.$store.dispatch("dict/getDicData", ["dt_srm_pc6", 'dt_unit_type', 'dt_srm_pdk', 'dt_srm_pca', 'dt_srm_pc9'])
    },
    methods: {
        handleClose () {
            this.$refs['dataForm'].resetFields()
            this.$refs['dataForm'].clearValidate()
        },
        //物料选择
        handleMaterialCode () {
            this.$refs.refZsjWlxz.dialogVisible = true
        },
        //物料选择确定
        confirmMateriel (data) {
            this.$set(this.temp, 'materialCode', data[0].materielCode)
            this.$set(this.temp, 'materialName', data[0].materielName)
            this.$set(this.temp, 'materielEngName', data[0].materielEngName)//物料英文名
            this.$set(this.temp, 'materielType', data[0].materielType)//物料类型
            this.$set(this.temp, 'materielTypeName', data[0].materielTypeName)//物料类型名称
            this.$set(this.temp, 'materielGroup', data[0].materielGroup)//物料组
            this.$set(this.temp, 'materielGroupName', data[0].materielGroupName)//物料组名称
        },
        //编辑按钮确定事件
        updateData: throttle(function () {
            this.dialogVisible = false
            let options = [this.dt_unit_type, this.dt_unit_type, this.dt_srm_pdk, this.dt_srm_pca]
            let dataTemp = this.$myFun.srmCodeToName([this.temp], options, ['measureUnit', 'baseUnit', 'currency', 'priceUnit'])
            this.$emit('newData', dataTemp[0])
        }, 2000,
            {
                leading: true,
                trailing: false
            }
        ),
        listSelected (val) {
            this.temp.parentRowId = val[0].parentRowId
            this.$refs.classify.dialogCodeVisible = false
        },
    }
}
