import { queryDeviceBinds, updateDeliver } from "@/api/project/iot/IOT/db"
export default {
    name: "DBTimeData",
    props: {
        itemData: {
            type: Object,
            default: undefined
        },
        code: {
            type: String,
            default: undefined
        }
    },
    watch: {
        itemData () {
            this.getList()
        }
    },
    data () {
        return {
            listLoading: false,
            theight: 0,//表格高度
            list: [],
            tableKey: 0,//表格索引
            listQuery: {
                time: undefined
            },
            dialogFormVisible: false,
            chartList: [
                {
                    yers: '2010',
                    value: 2500,
                    name: '出口1'
                },
                {
                    yers: '2011',
                    value: 4500,
                    name: '出口3'
                },
                {
                    yers: '2012',
                    value: 3500,
                    name: '出口4'
                },
                {
                    yers: '2013',
                    value: 4500,
                    name: '出口5'
                },

            ]
        }
    },
    mounted () {
        this.setTableHeight()
        //表格高度自适应
        window.onresize = () => {
            this.setTableHeight()
        }
    },
    methods: {
        //获取表格数据
        getList () {
            this.listLoading = true
            queryDeviceBinds(this.code, this.itemData.gatewayCode, this.itemData.deviceCode).then(response => {
                this.$emit('getSubList', response.data)
                this.list = response.data
                setTimeout(() => {
                    this.listLoading = false
                }, 1.5 * 100)
            })
        },
        // 交付
        pay (pointCode) {
            this.$confirm('此操作将交付所选中数据, 是否继续?', '提示', {
                confirmButtonText: '确定',
                cancelButtonText: '取消',
                type: 'warning'
            })
                .then(() => {
                    updateDeliver(this.code, this.itemData.gatewayCode, this.itemData.deviceCode, pointCode).then(() => {
                        this.getList()
                        this.$message({
                            title: '成功',
                            message: '交付成功',
                            type: 'success',
                            duration: 2000
                        })
                    })

                })
                .catch(() => {
                    this.$message({
                        type: 'info',
                        message: '已取消交付'
                    })
                })

        },
        // 查看曲线
        handleViewCurves () {
            return this.$message({
                title: this.$t('message.warning'),
                message: "此功能正在进行中，敬请期待！",
                type: "warning"
            })
            // this.dialogFormVisible = true
            // this.drawLine()
        },
        handleQuery () {

        },
        drawLine () {
            // 基于准备好的dom，初始化echarts实例
            let myChart = this.$echarts.init(this.$refs.myChart)
            // 绘制图表
            myChart.setOption({
                title: { text: '空压机' },
                xAxis: {
                    data: this.chartList.map(item => item.yers)
                },
                yAxis: {},
                series: [
                    {
                        name: '空压机',
                        type: 'line',
                        data: this.chartList
                    }
                ]
            })
        },
        //表格高度计算
        setTableHeight () {
            this.theight = this.$myFun.getSingleTbHeight() - 30
        },
    }
};
