import Pagination from "@/components/Pagination"
import { DICT_CODE } from '@/utils/constant'
import caiGou from '@/components/Select/xtpt/caigou'//下拉组件
import dialogWlxzbyzsj from '@/components/Dialog/dialogWlxzbyzsj.vue'//主数据物料选择
import dialogGysxz from '@/components/Dialog/dialogsGysxz'//供应商选择
import addMain from '../dialog/addMain'//供应商选择
import { mapState } from "vuex"
export default {
    components: { caiGou, addMain, Pagination, dialogWlxzbyzsj, dialogGysxz },
    data () {
        return {
            total: 0,
            DICT_CODE: DICT_CODE,
            formData: {
                materielCode: undefined,
                materielName: undefined,
                supplierCode: undefined,
                supplierName: undefined
            },
            tabName: 'first',
            projectList: [
                {
                    rowId: '1', Description: '要求SLOT提交时间', jhstartTime: '', jhendTime: '2019-05-21', quantity: '1'
                }
            ],
            mainList: [
                {
                    rowId: '1', taskDescription: '基本日程', jhstartTime: '', jhendTime: '', sjstartTime: '', sjcompleteTime: '', description: ''
                }
            ],
            listQuery: {}
        }
    },
    computed: {
        ...mapState({
            dt_srm_p0: state => state.dict.dt_srm_p0,
            dt_srm_pch: state => state.dict.dt_srm_pch,
        })
    },
    mounted () {
        this.$store.dispatch("dict/getDicData", ["dt_srm_p0", 'dt_srm_pch'])
    },
    methods: {
        initForm () {

        },
        handleClose () {
            this.$emit('changeState', 'projectPlan')
        },
        getList () {

        },
        //物料选择
        handleMaterial () {
            this.$refs.refZsjWlxz.dialogVisible = true
        },
        confirmMateriel (val) {
            console.log(val, 'val')
            this.formData.materielCode = val[0].materialCode
            this.formData.materielName = val[0].materialName
        },
        //供应商
        showGys () {
            this.$refs.refGys.dialogVisible = true
            this.$refs.refGys.getList()
        },
        //供应商选择确定
        handleGysClick (val) {
            console.log(val)
            this.$set(this.formData, 'supplierName', val[0].supplierName)
            this.$set(this.formData, 'supplierCode', val[0].supplierCode)
        },
        //新增任务
        handleCreate () {
            this.$refs.addMain.dialogStatus = 'create'
            this.$refs.addMain.dialogVisible = true
        },
        handleUpdate (index, val) {
            this.$refs.addMain.dialogStatus = 'update'
            this.$refs.addMain.dialogVisible = true
            this.$refs.addMain.temp = { ...val }
        },
        handleDelete (index) {
            this.$confirm("确认要删除该数据项吗?", "提示", {
                confirmButtonText: "确定",
                cancelButtonText: "取消",
                type: "warning"
            }).then(() => {
                this.mainList.splice(index, 1)
                this.$message({
                    type: "success",
                    message: "删除成功"
                })
            }).catch(() => {
                this.$message({
                    type: "info",
                    message: "已取消"
                })
            })
        },
        //新增子任务
        handleItemCreate () {

        },
        //附件上传
        handleUpload () {

        },
        //附件下载
        handleDownload () {

        }
    }
}
