import * as api from '@/api/project/wms/useSpareparts/requisitionApplication'
import Pagination from "@/components/Pagination"
import { mapState } from "vuex"
import addMain from './dialog/addMain'
import dialogOrgSelect from '@/components/Dialog/dialogOrgSelect'
import AuthoType from "@/components/Select/AuthoType.vue" //权限类型下拉组件
import dialogPersonSelect from '@/components/Dialog/dialogPersonSelect.vue'//选择人

export default {
    name: "requisitionApplication",
    components: { Pagination, addMain, AuthoType, dialogOrgSelect, dialogPersonSelect },
    data () {
        return {
            rangeDate: null,
            theight: 0,
            isSingle: true,//表格是否单选 点击各按钮根据流程逻辑控制单多选
            currentSelectedRow: null,//当前选中的行
            selectedRows: null,//多选时选中的所有行
            list: null,
            listSub: null,
            total: 0,
            selectedrow: null,//主表选中行
            listQuery: {
                page: true,
                currentPage: 1,
                pageSize: 10,
                supplierName: undefined
            },
            sublistQuery: {
                page: true,
                currentPage: 1,
                pageSize: 10
            },
            tableKey: [0, 1],//表格索引，虚拟dom渲染表格时使用
            queryStateParam: ['ADD', 'PART'],
        }
    },
    computed: {
        ...mapState({
            dt_data_state: state => state.dict.dt_data_state,
            dt_use_type_screenGroupNum: state =>
                state.businessComponent.dt_use_type_screenGroupNum

        })
    },
    mounted () {
        this.$store.dispatch("dict/getDicData", ["dt_srm_pdv", 'dt_srm_pdw', 'dt_srm_pdx', 'dt_data_state'])
        this.$store.dispatch("businessComponent/getBusinessComponentData", [
            "dt_use_type_screenGroupNum"
        ])
        this.setTableHeight()
        //表格高度自适应
        window.onresize = () => {
            this.setTableHeight()
        }

        this.getList()
    },
    watch: {
        'rangeDate' (value) {
            if (value) {
                this.listQuery.applyTimeStart = value[0]
                this.listQuery.applyTimeEnd = value[1]
            } else {
                this.listQuery.applyTimeStart = undefined
                this.listQuery.applyTimeEnd = undefined
            }
        }
    },
    methods: {
        //表格高度计算
        setTableHeight () {
            this.theight = this.$myFun.getSingleTbHeight() - 185
        },
        setdefault (val, i) {
            if (i === 1) {
                this.listQuery.orgCode = val
            } else if (i === 3) {
                this.listQuery.warehouseCode = val
                this.$set(this.listQuery, 'businessType', this.dt_use_type_screenGroupNum.options[0].wareRuleCode)
            }
        },
        //申请单位选择
        showOrg () {
            this.$refs.refOrg.dialogVisible = true
        },
        //申请单位确定
        confirmOrg (data) {
            console.log(data)
            if (data) {
                this.$set(this.listQuery, 'departmentCode', data[0].organizationCode)
                this.$set(this.listQuery, 'departmentName', data[0].organizationName)
            }
        },
        //人员选择
        showPerson () {
            this.$refs.person.dialogVisible = true
        },
        //人员选择确定
        personConfirm (val) {
            this.$set(this.listQuery, 'applyEmp', val[0].employeeCode)
            this.$set(this.listQuery, 'applyEmpName', val[0].employeeName)
        },
        checked () {
            this.$refs.tb.toggleRowSelection(this.list[0], true)//官网方法，重点
        },
        refresh () {
            this.getList()
        },
        //显示主表
        getList () {
            api.queryRecords(this.listQuery).then(res => {
                this.list = res.data.list
                this.total = res.data.pages.count
                this.$nextTick(function () {
                    this.checked()
                })
            })
        },
        //显示从表
        getSubList () {
            let dto = {
                queryStateParam: this.queryStateParam.join(",")
            }
            api.queryRecordItems(this.selectlistRow.useOrderCode, dto).then(res => {
                this.listSub = res.data
            })
        },
        //新增按钮单击事件
        handleCreate () {
            this.$refs.addMain.resetTemp()
            this.$refs.addMain.dialogStatus = 'create'
            this.$refs.addMain.dialogVisible = true
        },
        //编辑按钮单击事件
        handleUpdate () {
            if (this.selectlistRow) {
                this.$refs.addMain.dialogStatus = 'update'
                this.$refs.addMain.dialogVisible = true
                this.$refs.addMain.temp = { ...this.selectlistRow }
                this.$nextTick(() => {
                    this.$refs.addMain.getList()
                })
            } else {
                this.$message({
                    title: "警告",
                    message: "请选择一条信息",
                    type: "warning"
                })
            }

        },
        //删除
        handleDelete () {
            if (this.selectlistRow) {
                this.$confirm("确认要删除该数据项吗?", "提示", {
                    confirmButtonText: "确定",
                    cancelButtonText: "取消",
                    type: "warning"
                }).then(() => {
                    api.deleteRecord(this.selectlistRow.useOrderCode).then(() => {
                        this.$message({
                            title: '成功',
                            message: '删除成功',
                            type: 'success',
                            duration: 2000
                        })
                        this.getList()
                    })
                }).catch(() => {
                    this.$message({
                        type: "info",
                        message: "已取消"
                    })
                })
            } else {
                this.$message.warning("请勾选记录！")
                return
            }
        },
        //提交按钮单击事件
        handleSubmit () {
            if (this.selectlistRow) {
                this.$confirm("确认要提交该数据项吗?", "提示", {
                    confirmButtonText: "确定",
                    cancelButtonText: "取消",
                    type: "warning"
                }).then(() => {
                    api.queryRecordone(this.selectlistRow.useOrderCode).then(res => {
                        res.data.method = 'PUBLISH'
                        api.updateRecord(this.selectlistRow.useOrderCode, res.data).then(() => {
                            this.$message({
                                title: '成功',
                                message: '提交成功',
                                type: 'success',
                                duration: 2000
                            })
                            this.getList()
                        })
                    })
                }).catch(() => {
                    this.$message({
                        type: "info",
                        message: "已取消"
                    })
                })
            } else {
                this.$message.warning("请勾选记录！")
                return
            }
        },
        //查询
        handleQuery () {
            this.listSub = []
            this.listQuery.currentPage = 1
            this.getList()
        },
        // 表格选择框选中 注:参数为选中的所有行的数组
        selectChange (val) {
            this.selectlistRow = val[val.length - 1]
            if (val.length > 1) {
                this.$refs.tb.clearSelection()
                this.$refs.tb.toggleRowSelection(val.pop())
            }
            if (this.selectlistRow) {
                this.getSubList()
            }
            this.$refs.tb.setCurrentRow(this.selectlistRow)
        },
        //点击表格某一行
        rowClick (val) {
            this.$refs.tb.clearSelection()
            this.$refs.tb.toggleRowSelection(val)
        },
    }
};
