import { queryDeviceAlarm } from "@/api/project/iot/PM/equipmentProducts"
import { queryAttributes } from "@/api/project/iot/PM/equipmentProducts"
import { queryProjectCodes as queryDevice } from "@/api/project/iot/IOT/em"
import Pagination from "@/components/Pagination"
import throttle from 'lodash/throttle'
export default {
    name: "DAInfo",
    components: { Pagination },
    props: ['projectList', 'code', 'row', 'alarmStartTime', 'alarmEndTime'],
    watch: {
        'listQuery.projectName' (val) {
            if (!val) {
                this.listQuery.projectCode = undefined
            } else {
                this.projectList.forEach(item => {
                    if (item.projectName == val) {
                        return this.listQuery.projectCode = item.projectCode
                    }
                })
            }
        },
        'listQuery.deviceName' (val) {
            if (!val) {
                this.listQuery.deviceCode = undefined
            }
        },
        'listQuery.deviceCode' (val) {
            if (val) {
                console.log(val)
                this.getAttributel()
            }
        },
        'listQuery.projectCode' (val) {
            if (val) {
                queryDevice(val).then(res => {
                    this.deviceList = res.data
                })
            }
        },
    },
    data () {
        return {
            theight: 0,//表格高度
            listLoading: false,
            list: [],
            deviceList: [],
            AttributesList: [],
            total: 0,
            listQuery: {
                page: true,
                currentPage: 1,
                pageSize: 15,
                deviceCode: this.code,
                projectName: this.row.projectName,
                projectCode: undefined,
                deviceName: this.row.deviceName,
                alarmStartTime: this.alarmStartTime,
                alarmEndTime: this.alarmEndTime,
                deciveAlarmName: this.row.attributeName
            },
            tableKey: 0 //表格索引
        }
    },
    mounted () {
        this.setTableHeight()
        window.onresize = () => {
            this.setTableHeight()
        }
        this.getList()
        this.getDeviceList()
        this.getAttributel()
    },
    methods: {
        getAttributel () {
            if (this.code) {
                queryAttributes(this.listQuery.deviceCode).then(res => {
                    this.AttributesList = res.data
                })
            }
        },
        getDeviceList () {
            this.projectList.forEach(item => {
                if (item.projectName == this.listQuery.projectName) {
                    return this.listQuery.projectCode = item.projectCode
                }
            })
            queryDevice(this.listQuery.projectCode).then(res => {
                this.deviceList = res.data
            })
        },
        getList () {
            queryDeviceAlarm(this.listQuery).then(res => {
                this.list = res.data.list
                this.total = res.data.pages.count
            })
        },
        //表格查询
        handleQuery: throttle(
            function () {
                this.listQuery.currentPage = 1
                this.getList()
            },
            2000,
            {
                leading: true,
                trailing: false
            }
        ),
        //表格高度计算
        setTableHeight () {
            this.theight = this.$myFun.getSingleTbHeight() - 30
        },
        querySearchProject (queryString, cb) {
            var restaurants = this.projectList
            var results = queryString ? restaurants.filter(this.createFilterProject(queryString)) : restaurants
            cb(results)
        },
        createFilterProject (queryString) {
            return (restaurant) => {
                return (restaurant.projectName.toLowerCase().indexOf(queryString.toLowerCase()) !== -1)
            }
        },
        handleSelectProject (item) {
            this.listQuery.projectCode = item ? item.projectCode : undefined
            this.listQuery.projectName = item ? item.projectName : undefined
        },
        querySearchDevice (queryString, cb) {
            var restaurants = this.deviceList
            var results = queryString ? restaurants.filter(this.createFilterDevice(queryString)) : restaurants
            cb(results)
        },
        createFilterDevice (queryString) {
            return (restaurant) => {
                return (restaurant.deviceName.toLowerCase().indexOf(queryString.toLowerCase()) !== -1)
            }
        },
        handleSelectDevice (item) {
            this.listQuery.deviceCode = item ? item.deviceCode : undefined
            this.listQuery.deviceName = item ? item.deviceName : undefined
        }
    }
};
