import * as api from "@/api/project/srm/dataBase/specialDistinction"
import Pagination from '@/components/Pagination'
import { DICT_CODE } from '@/utils/constant'
import caiGou from '@/components/Select/xtpt/caigou'//下拉组件
export default {
    components: { Pagination, caiGou },
    props: ['purchaseOrg', 'title'],
    data () {
        return {
            DICT_CODE: DICT_CODE,
            dialogVisible: false,
            listQuery: {
                page: true,
                currentPage: 1,
                pageSize: 15,
                purchaseOrg: this.purchaseOrg
            },
            isSingle: false,
            tableKey: 0,
            dataList: [],
            total: 0,
            rowId: null
        }
    },
    mounted () {
        // this.getList()
    },
    watch: {
        purchaseOrg: {
            handler (newValue) {
                this.listQuery.purchaseOrg = newValue
                this.getList()
            },
            deep: true
        },
    },
    methods: {
        //获取采购组织名称
        selectName (val) {
            this.listQuery.purchaseOrgName = val
        },
        getList () {
            api.queryRecords(this.listQuery).then(res => {
                this.dataList = res.data.list
                this.total = res.data.pages.count
            })
        },
        handleQuery () {
            this.getList()
        },
        confirm () {
            if (this.selectlistRow) {
                this.$emit('handleConfirm', this.selectlistRow)
                this.dialogVisible = false
            } else {
                this.$message({
                    title: '警告',
                    message: '请勾选记录',
                    type: 'warning',
                    duration: 2000
                })
            }
        },
        // 表格选择框选中 注:参数为选中的所有行的数组
        selectChange (val) {
            this.selectlistRow = val[val.length - 1]
            if (val.length > 1) {
                this.$refs.tb.clearSelection()
                this.$refs.tb.toggleRowSelection(val.pop())
            }
            this.$refs.tb.setCurrentRow(this.selectlistRow)
        },
        //点击表格某一行
        rowClick (val) {
            this.$refs.tb.clearSelection()
            this.$refs.tb.toggleRowSelection(val)
        },

    }
}
