import { mapState } from "vuex"
import * as api from "@/api/project/srm/supplierSelect/priceReview"
export default {
    data () {
        return {
            list: [],
            subList: [],
            dialogVisible: false,
            Edit: false,
            temp: {},
            rules: {
                validDateEnd: [{ required: true, message: '报价截止日期不能为空', trigger: 'blur' }],
                businessOpeningTime: [{ required: true, message: '价格开标日期不能为空', trigger: 'blur' }],
                isPriceLimit: [{ required: true, message: '是否限价不能为空', trigger: 'blur' }],
            }
        }
    },
    computed: {
        ...mapState({
            dt_yes_or_no: state => state.dict.dt_yes_or_no,
        })
    },
    mounted () {
        this.$store.dispatch("dict/getDicData", ['dt_yes_or_no'])
        this.getList()
    },
    methods: {
        getList () {

        },
        changeData (val) {
            console.log(val, '98765')
            if (val == 'Y') {
                this.Edit = false
            } else {
                this.Edit = true
            }
        },
        confirmData () {
            let dto = {
                businessOpeningTime: this.temp.businessOpeningTime,
                highestPrice: this.temp.highestPrice,
                isPriceLimit: this.temp.isPriceLimit,
                quotationRequirements: this.temp.quotationRequirements,
                validDateEnd: this.temp.validDateEnd,
                planNo: this.list[0].planNo,
                materielCode: this.list[0].materielCode,
                rfqItemRowIds: []
            }
            this.subList.forEach((val, index) => {
                dto.rfqItemRowIds[index] = val.rowId
            })
            this.$refs.dataForm.validate(valid => {
                if (valid) {
                    api.twoPrice(dto).then(() => {
                        this.$message({
                            title: '成功',
                            message: '二次报价成功',
                            type: 'success',
                            duration: 2000
                        })
                        this.dialogVisible = false
                        this.$emit('refresh')
                    })
                } else {
                    return false
                }
            })
        },
        handleClose () {
            this.resetTemp()
            this.$refs.dataForm.resetFields()
        },
        handleDelete (index, val) {
            console.log(index, val)
        },
        resetTemp () {
            this.temp = {
                validDateEnd: undefined,
                businessOpeningTime: undefined,
                isPriceLimit: undefined,
                highestPrice: undefined,
                quotationRequirements: undefined
            }
        }
    }
}
