import { getGateway } from "@/api/project/iot/MOM/gatewayQuery"
import Pagination from "@/components/Pagination"
import { mapState } from 'vuex'
import throttle from 'lodash/throttle'
export default {
    name: "GRMain",
    props: ['projectList'],
    components: { Pagination },
    computed: {
        ...mapState({
            iot_networking_way: state => state.iotDict.iot_networking_way,
            iot_state_gateway: state => state.iotDict.iot_state_gateway
        })
    },
    data () {
        return {
            keyList: [],
            listLoading: false,
            theight: 0,//表格高度
            list: [],
            total: 0,
            listQuery: {
                page: true,
                currentPage: 1,
                pageSize: 10,
                iotState: undefined,
                projectName: undefined,
                gatewayName: undefined
            },
            tableKey: 0 //表格索引
        }
    },
    mounted () {
        this.setTableHeight()
        //表格高度自适应
        window.onresize = () => {
            this.setTableHeight()
        }
        this.getList()
    },
    methods: {
        //获取表格数据
        getList () {
            this.listLoading = true
            getGateway(this.listQuery).then(response => {
                this.list = response.data.list
                this.list = this.$myFun.srmCodeToName(response.data.list, [this.iot_networking_way, this.iot_state_gateway], ['networkingWay', 'iotState'])
                this.total = response.data.pages.count
                setTimeout(() => {
                    this.listLoading = false
                }, 1.5 * 100)
            })
        },
        //表格查询
        handleQuery: throttle(
            function () {
                this.listQuery.currentPage = 1
                this.getList()
            },
            2000,
            {
                leading: true,
                trailing: false
            }
        ),
        //表格高度计算
        setTableHeight () {
            this.theight = this.$myFun.getSingleTbHeight()
        },
        toInfo (row) {
            this.$emit('setRow', row)
            this.$emit('setCode', row.projectCode)
            this.$emit('setPage', 'GR-Info')
        },
        querySearchProject (queryString, cb) {
            var restaurants = this.projectList
            var results = queryString ? restaurants.filter(this.createFilterProject(queryString)) : restaurants
            cb(results)
        },
        createFilterProject (queryString) {
            return (restaurant) => {
                return (restaurant.projectName.toLowerCase().indexOf(queryString.toLowerCase()) !== -1)
            }
        },
        handleSelectProject (item) {
            this.listQuery.projectName = item ? item.projectName : undefined
        },
    }
};
