import getPageTitle from '@/utils/getPageName'
import { queryProjects } from "@/api/project/iot/IOT/project"
import mainBox from "./main"
import GRTabs from "./GRTabs" //设备建模 详细tab页
export default {
    name: "IOTGR",
    components: { mainBox, GRTabs },
    watch: {
        '$route.params' (newval) {
            if (newval && newval.code) {
                if (this.code !== newval.code) {
                    this.pages = 'main'
                    this.code = newval.code
                }
            }
        }
    },
    data () {
        return {
            pages: 'main',
            code: this.$route.params.code, //一级页面项目编码
            row: undefined, // 二级页面选择的行
            title: getPageTitle(this.$route.name),
            projectList: null
        }
    },
    created () {
        this.$store.dispatch('iotDict/getDicData', ['iot_operator', 'iot_networking_way', 'iot_reporting_period', 'iot_state_gateway'])
        this.getProject()
    },
    methods: {
        setPage (pages) {
            this.pages = pages
        },
        setCode (code) {
            this.code = code
        },
        setRow (row) {
            this.row = row
        },
        getProject () {
            queryProjects().then(res => {
                this.projectList = res.data
            })
        },
    },
};
