import * as api from "@/api/project/srm/contractManage/specialPaymentMethod"
import Pagination from "@/components/Pagination"
import { DICT_CODE } from '@/utils/constant'
import caiGou from '@/components/Select/xtpt/caigou'//下拉组件
import { mapState } from "vuex"
import print from './dialog/print'//下拉组件
export default {
    name: "delivery_Publish",
    components: { Pagination, caiGou, print },
    data () {
        return {
            DICT_CODE: DICT_CODE,
            theight: 0,
            isSingle: true,//表格是否单选 点击各按钮根据流程逻辑控制单多选
            currentSelectedRow: null,//当前选中的行
            selectedRows: null,//多选时选中的所有行
            list: null,
            listSub: null,
            total: 0,
            totalSub: 0,
            selectedrow: null,//主表选中行
            listQuery: {
                page: true,
                currentPage: 1,
                pageSize: 10
            },
            sublistQuery: {
                page: true,
                currentPage: 1,
                pageSize: 10
            },
            tableKey: [0, 1],//表格索引，虚拟dom渲染表格时使用
            dialogVisiblesh: false,
            dialogVisiblecancel: false,
            approvalCode: ''

        }
    },
    computed: {
        ...mapState({
            dt_srm_p0: state => state.dict.dt_srm_p0,
            dt_srm_pax: state => state.dict.dt_srm_pax,
            dt_srm_pdr: state => state.dict.dt_srm_pdr,
            dt_srm_pdt: state => state.dict.dt_srm_pdt,
            dt_srm_pdu: state => state.dict.dt_srm_pdu,
        })
    },
    mounted () {
        this.$store.dispatch("dict/getDicData", ["dt_srm_p0", 'dt_srm_pax', 'dt_srm_pdr', 'dt_srm_pdt', 'dt_srm_pdu'])
        this.setTableHeight()
        //表格高度自适应
        window.onresize = () => {
            this.setTableHeight()
        }
        this.getList()
    },
    methods: {
        //表格高度计算
        setTableHeight () {
            this.theight = this.$myFun.getSingleTbHeight() - 185
        },
        //显示主表
        getList () {
            api.queryRecordsMain(this.listQuery).then(res => {
                let options = [this.dt_srm_p0]
                res.data.list = this.$myFun.srmCodeToName(res.data.list, options, ['state'])
                this.list = res.data.list
                this.total = res.data.pages.count
            })
        },
        //显示从表
        getSubList () {
            this.sublistQuery.approvalNo = this.selectlistRow.approvalNo
            api.queryRecordsSub(this.sublistQuery).then(res => {
                let options = [this.dt_srm_pax, this.dt_srm_pdr, this.dt_srm_pdt, this.dt_srm_pdu, this.dt_srm_pdr, this.dt_srm_pdt, this.dt_srm_pdu]
                res.data.list = this.$myFun.srmCodeToName(res.data.list, options, ['supplyClassification', 'lastYearPaymentType', 'lastYearSettlementType', 'lastYearPaymentPeriod', 'thisYearPaymentType', 'thisYearSettlementType', 'thisYearPaymentPeriod'])
                this.listSub = res.data.list.map(v => {
                    v.lastYearPayment = v.lastYearPaymentTypeCN + '/' + v.lastYearSettlementTypeCN + '/' + v.lastYearPaymentPeriodCN
                    v.thisYearPayment = v.thisYearPaymentTypeCN + '/' + v.thisYearSettlementTypeCN + '/' + v.thisYearPaymentPeriodCN
                    return v
                })
                this.totalSub = res.data.pages.count
            })
        },
        //审按钮单击事件
        handleApproval (index, val) {
            this.$confirm(`是否启动审批流程？审批单号${val.approvalNo}`, "提示", {
                confirmButtonText: "确定",
                cancelButtonText: "取消",
                type: "warning"
            }).then(() => {
                let dto = {
                    method: 'check'
                }
                api.updateCancel(val.approvalNo, dto).then(() => {
                    this.$message({
                        title: '成功',
                        message: '审核成功',
                        type: 'success',
                        duration: 2000
                    })
                    this.getList()
                })
            }).catch(() => {
                this.$message({
                    type: "info",
                    message: "已取消"
                })
            })
        },
        //打印按钮单击事件
        handlePrint () {
            this.$refs.print.dialogVisible = true
        },
        //撤销审核按钮单击事件
        handleCancel (index, val) {
            this.$confirm(`是否撤销审批单？审批单号${val.approvalNo}，撤销后，数据恢复至供应商反馈状态`, "提示", {
                confirmButtonText: "确定",
                cancelButtonText: "取消",
                type: "warning"
            }).then(() => {
                let dto = {
                    method: 'revoke'
                }
                api.updateCancel(val.approvalNo, dto).then(() => {
                    this.$message({
                        title: '成功',
                        message: '撤销成功',
                        type: 'success',
                        duration: 2000
                    })
                    this.getList()
                })
            }).catch(() => {
                this.$message({
                    type: "info",
                    message: "已取消"
                })
            })
        },
        //发布
        handlePublish () {

        },
        //删除
        handleDelete () {

        },
        //查询
        handleQuery () {
            this.listSub = []
            this.listQuery.currentPage = 1
            this.getList()
        },
        //每页大小变化
        handleSizeChange (val) {
            this.listQuery.pageSize = val
            this.getList()
        },
        //当前页变化
        handleCurrentChange (val) {
            this.listQuery.currentPage = val
            this.getList()
        },
        // 表格选择框选中 注:参数为选中的所有行的数组
        selectChange (val) {
            this.selectlistRow = val[val.length - 1]
            if (val.length > 1) {
                this.$refs.tb.clearSelection()
                this.$refs.tb.toggleRowSelection(val.pop())
            }
            if (this.selectlistRow) {
                this.getSubList()
            }
            this.$refs.tb.setCurrentRow(this.selectlistRow)
        },
        //点击表格某一行
        rowClick (val) {
            this.$refs.tb.clearSelection()
            this.$refs.tb.toggleRowSelection(val)
        },
    }
};
