import * as api from "@/api/project/iot/IOT/em"
import { queryRecords } from "@/api/project/iot/PM/equipmentProducts"
import Pagination from "@/components/Pagination"
import throttle from 'lodash/throttle'
import { mapState } from 'vuex'
export default {
    name: "GPSubMain",
    props: ['code', 'manufacturersList'],
    components: { Pagination },
    data () {
        return {
            listLoading: false,
            theight: 0,//表格高度
            list: null,
            total: 0,
            listQuery: {
                page: true,
                currentPage: 1,
                pageSize: 15,
                typeCode: undefined,
                typeName: undefined,
                deviceName: undefined,
                iotState: undefined
            },
            deviceTypeList: null,
            tableKey: 0 //表格索引
        }
    },
    watch: {
        'listQuery.typeName' (val) {
            if (!val) {
                this.listQuery.typeCode = undefined
            }
        }
    },
    computed: {
        ...mapState({
            iot_state_device: state => state.iotDict.iot_state_device,
            iot_project_type: state => state.iotDict.iot_project_type,
        })
    },
    mounted () {
        this.setTableHeight()
        //表格高度自适应
        window.onresize = () => {
            this.setTableHeight()
        }
        this.getList()
        this.getDeviceTypeList()
    },
    methods: {
        stateClass (state) {
            if (state == '0') {
                return 'iot-warning'
            } else if (state == '1') {
                return 'iot-success'
            } else if (state == '2') {
                return 'iot-info'
            }
        },
        //表格高度计算
        setTableHeight () {
            this.theight = this.$myFun.getSingleTbHeight()
        },
        toInfo (row) {
            this.$emit('setRow', row)
            this.$emit('setPage', 'info')
        },
        getDeviceTypeList () {
            let listQuery = {
                currentPage: 1,
                pageSize: 1000,
                page: 1,
            }
            queryRecords(listQuery).then(response => {
                this.deviceTypeList = response.data.list
            })
        },
        //获取表格数据
        getList () {
            this.listLoading = true
            api.queryProjectCodes(this.code, this.listQuery).then(response => {
                this.list = response.data.list
                let options = [this.iot_state_device]
                this.list = this.$myFun.srmCodeToName(
                    response.data.list,
                    options,
                    ['iotState']
                )
                if (response.data.list && this.manufacturersList) {
                    for (let i = 0; i < response.data.list.length; i++) {
                        for (let j = 0; j < this.manufacturersList.length; j++) {
                            if (response.data.list[i].manufacturersCode == this.manufacturersList[j].manufacturersCode) {
                                response.data.list[i].manufacturersName = this.manufacturersList[j].manufacturersName
                                break
                            }
                        }
                    }
                }

                this.total = response.data.pages.count
                setTimeout(() => {
                    this.listLoading = false
                }, 1.5 * 100)
            })
        },
        //表格查询
        handleQuery: throttle(
            function () {
                this.listQuery.currentPage = 1
                this.getList()
            },
            2000,
            {
                leading: true,
                trailing: false
            }
        ),
        querySearch (queryString, cb) {
            var restaurants = this.deviceTypeList
            var results = queryString ? restaurants.filter(this.createFilter(queryString)) : restaurants
            cb(results)
        },
        createFilter (queryString) {
            return (restaurant) => {
                return (restaurant.typeName.toLowerCase().indexOf(queryString.toLowerCase()) !== -1)
            }
        },
        handleSelect (item) {
            this.listQuery.typeCode = item ? item.typeCode : undefined
            this.listQuery.typeName = item ? item.typeName : undefined
        }
    }
};
