import { DICT_CODE } from '@/utils/constant'
import caiGou from '@/components/Select/xtpt/caigou'//下拉组件
import * as api from "@/api/project/srm/quotaManagement/quotaSettings"
import Pagination from "@/components/Pagination"
import { mapState } from "vuex"
export default {
    name: "quotaSettings",
    components: { Pagination, caiGou },
    data () {
        return {
            DICT_CODE: DICT_CODE,
            theight: 0,//表格高度
            isSingle: true,//表格是否单选 点击各按钮根据流程逻辑控制单多选
            list: [],
            total: 0,
            listQuery: {
                //查询
                orgCode: 'dtcg0001',
                page: true,
                currentPage: 1,
                pageSize: 15
            },
            currentSelectedRow: null,//当前选中的行
            selectedRows: null,//多选时选中的所有行
            tableKey: 0, //表格索引
            rangeDate: null
        }
    },
    computed: {
        ...mapState({
            // dt_srm_p0: state => state.dict.dt_srm_p0,
            // dt_srm_pdr: state => state.dict.dt_srm_pdr,
            // dt_srm_pdt: state => state.dict.dt_srm_pdt,
            // dt_srm_pdu: state => state.dict.dt_srm_pdu,
        })
    },
    watch: {
        'rangeDate' (value) {
            if (value) {
                this.listQuery.firstExecuteTime = value[0]
                this.listQuery.endExecuteTime = value[1]
            } else {
                this.listQuery.firstExecuteTime = undefined
                this.listQuery.endExecuteTime = undefined
            }
        },
    },
    mounted () {
        // this.$store.dispatch("dict/getDicData", ["dt_srm_p0", 'dt_srm_pdr', 'dt_srm_pdt', 'dt_srm_pdu'])
        this.setTableHeight()
        //表格高度自适应
        window.onresize = () => {
            this.setTableHeight()
        }
        this.getList()
    },
    methods: {
        //表格高度计算
        setTableHeight () {
            this.theight = this.$myFun.getSingleTbHeight()
        },
        //获取表格数据
        getList () {
            api.queryRecords(this.listQuery).then(res => {
                this.list = res.data.list
                this.total = res.data.pages.count
            })
        },
        //生成按钮单击事件
        handleCreate () {
            console.log('生成')
            if (this.selectlistRow && this.selectlistRow.length > 0) {
                if (this.rangeDate) {
                    this.$confirm('此操作将生成该条信息, 是否继续?', '提示', {
                        confirmButtonText: '确定',
                        cancelButtonText: '取消',
                        type: 'warning'
                    })
                        .then(() => {
                            let dto = {
                                endExecuteTime: this.listQuery.endExecuteTime,
                                firstExecuteTime: this.listQuery.firstExecuteTime,
                                quotaSetAddDTO: this.selectlistRow
                            }
                            api.addRecord(dto).then(() => {
                                this.$message({
                                    title: '成功',
                                    message: '生成成功',
                                    type: 'success'
                                })
                                this.getList()
                            })
                        })
                        .catch(() => {
                            this.$message({
                                type: 'info',
                                message: '已取消已读'
                            })
                        })
                } else {
                    this.$message({
                        title: '警告',
                        message: '请选择执行时间',
                        type: 'warning'
                    })
                }

            } else {
                this.$message({
                    title: '警告',
                    message: '请选择信息',
                    type: 'warning'
                })
            }
        },
        //全生成按钮单击事件
        handleAllCreate () {
            console.log('全生成')
            if (this.selectlistRow && this.selectlistRow.length == 1) {
                if (this.rangeDate) {
                    this.$confirm('此操作将全生成该条信息, 是否继续?', '提示', {
                        confirmButtonText: '确定',
                        cancelButtonText: '取消',
                        type: 'warning'
                    })
                        .then(() => {
                            this.selectlistRow[0].endExecuteTime = this.listQuery.endExecuteTime
                            this.selectlistRow[0].firstExecuteTime = this.listQuery.firstExecuteTime
                            api.alladdRecord(this.selectlistRow[0]).then(() => {
                                this.$message({
                                    title: '成功',
                                    message: '生成成功',
                                    type: 'success'
                                })
                                this.getList()
                            })
                        })
                        .catch(() => {
                            this.$message({
                                type: 'info',
                                message: '已取消已读'
                            })
                        })
                } else {
                    this.$message({
                        title: '警告',
                        message: '请选择执行时间',
                        type: 'warning'
                    })
                }
            } else {
                this.$message({
                    title: '警告',
                    message: '请选择一条信息',
                    type: 'warning'
                })
            }
        },
        //表格查询
        handleQuery () {
            this.listQuery.currentPage = 1
            this.getList()
        },
        // 表格选择框选中 注:参数为选中的所有行的数组
        selectChange (val) {
            this.selectlistRow = val
        },
        //点击表格某一行
        rowClick (val) {
            this.$refs.tb.clearSelection()
            this.$refs.tb.toggleRowSelection(val)
        }
    }
};
