import * as api from "@/api/project/wms/productionMaterial/pullMaterial";
import AuthoType from "@/components/Select/AuthoType.vue"; //权限类型下拉组件
import Pagination from "@/components/Pagination";
import gysxz from "@/components/Select/gysxz"; //供应商选择
import wlxzbySourceList from "@/components/Select/wlxzbySourceList"; //物料信息模块
import dialogAddMain from "./dialog/DialogAddMain";
import dialogClose from "./dialog/DialogClose";
import { mapState, mapGetters } from "vuex";

export default {
    name: "pullMaterial",
    components: { Pagination, gysxz, wlxzbySourceList, dialogAddMain, dialogClose, AuthoType },
    data () {
        return {
            theight: 0,
            isSingle: true,//表格是否单选 点击各按钮根据流程逻辑控制单多选
            tableKey: [0, 1],
            isGysRequired: false,
            gysMutiple: true,
            list: null,
            listSub: null,
            wlsupplierCode: "",
            wlorgCode: "",
            wlarrivalOrgCode: "",
            AddMainlist: {},
            total: 0,
            rangeTime: "",
            selectRows: [],
            dialogStatus: 0,
            wlxzDialogFormVisible: false,
            supplierRows: [],
            wlxzRows: [],
            isChangeAddS: false,
            closeData: {},
            pullOrderDetails: null,
            listQuery: {
                page: true,
                currentPage: 1,
                pageSize: 10,
                supplierName: "", //供应商,
                supplierCode: "",
                arrivalOrgCode: "",
                warehouseCode: "",
                pullOrderCode: undefined, //拉料单号
                arrivalTimeStart: undefined, //送达时间
                arrivalTimeEnd: undefined,
                pullMaterialType: undefined, //拉料类型
                pullMaterialStatus: undefined //拉料状态
            },
            sublistQuery: {
                page: true,
                currentPage: 1,
                pageSize: 10,
                pullOrderCode: undefined //订单号
            },
        };
    },
    computed: {
        ...mapGetters(["name", "superRoles"]),
        ...mapState({
            dt_pullmaterial_type: state => state.dict.dt_pullmaterial_type,
            dt_pullmaterial_state: state => state.dict.dt_pullmaterial_state
        })
    },
    mounted () {
        this.setTableHeight();
        //表格高度自适应
        window.onresize = () => {
            this.setTableHeight()
        };
        this.$store.dispatch("dict/getDicData", ["dt_pullmaterial_type", "dt_pullmaterial_state"]);
        this.getList();
    },
    methods: {
        //表格高度计算
        setTableHeight () {
            this.theight = this.$myFun.getSingleTbHeight() - 195;
        },
        //主表数据获取
        getListData () {
            if (this.superRoles) {
                this.listQuery.creator = null;
            } else {
                this.listQuery.creator = this.name;
            }

            api.queryRecords(this.listQuery).then(res => {
                res = res.data;
                this.list = res.list;
                this.total = res.pages.count;
                if (this.total == 0) {
                    this.listSub = []
                } else {
                    this.$nextTick(() => {
                        this.list.forEach((row, index) => {
                            if (index === 0) {
                                this.$refs.tbmain.toggleRowSelection(row, true);
                            }
                        })
                    })
                }
            });
        },
        //显示主表
        getList () {
            if (this.rangeTime) {
                var d1 = this.rangeTime[0];
                var d2 = this.rangeTime[1];
                this.listQuery.arrivalTimeStart = d1;
                this.listQuery.arrivalTimeEnd = d2;
            } else {
                this.listQuery.arrivalTimeStart = "";
                this.listQuery.arrivalTimeEnd = "";
            }
            this.getListData();
        },
        //显示从表
        getSubList (row) {
            api.queryRowRecords(row.pullOrderCode).then(res => {
                res = res.data;

                this.sublistQuery.pullOrderCode = res.pullOrderCode;
                this.listSub = res.pullOrderDetails;
            });
        },
        //查询
        handleQuery () {
            this.getList();
        },
        //每页大小变化
        handleSizeChange (val) {
            this.listQuery.pageSize = val;
            this.getList();
        },
        //当前页变化
        handleCurrentChange (val) {
            this.listQuery.currentPage = val;
            this.getList();
        },
        //主表勾选框选中
        selectRow (val) {
            this.selectRows = val[val.length - 1];
            if (val.length > 1) {
                this.$refs.tbmain.clearSelection();
                this.$refs.tbmain.toggleRowSelection(val[val.length - 1], "selected");
            }

            if (val.length !== 0) {
                this.getSubList(val[val.length - 1]);
            }
        },
        //主表点击显示从表
        showSubTable (val) {
            this.selectRows = val;
            this.$refs.tbmain.clearSelection();
            this.$refs.tbmain.toggleRowSelection(val, "selected");
            this.getSubList(val);
        },
        //新增
        handleCreate () {
            this.$refs.addMain.resetForm();
            this.$refs.addMain.selectedDetails = null;
            this.$refs.addMain.titleShow(0);
            this.$refs.addMain.dialogFormVisible = true;
        },
        //删除
        handleDelete () {
            if (this.selectRows) {
                if (this.selectRows.pullMaterialStatus) {
                    // let pullMaterialStatus = this.selectRows.pullMaterialStatus;
                    this.$confirm(
                        this.$t("message.isDeleteContinue"),
                        this.$t("message.prompt"),
                        {
                            confirmButtonText: this.$t("message.confirm"),
                            cancelButtonText: this.$t("message.cancel"),
                            type: "warning"
                        }
                    ).then(() => {
                        api.deleteRecords(this.selectRows.pullOrderCode).then(() => {
                            this.getList();
                            this.$message({
                                type: "success",
                                message: this.$t("message.deleteSuccessed")
                            });
                        });
                    })
                        .catch(() => {
                            this.$message({
                                type: "info",
                                message: this.$t("message.deleteCanceled")
                            });
                        });
                } else {
                    this.$message.warning(this.$t("message.checkedplease"));
                    return false;
                }
            } else {
                this.$message.warning(this.$t("message.checkedplease"));
                return false;
            }
        },
        //修改
        handleUpdate () {
            if (this.selectRows) {
                if (this.selectRows.pullMaterialStatus) {
                    api.queryRowRecords(this.selectRows.pullOrderCode).then(res => {
                        res = res.data;
                        this.$refs.addMain.titleShow(1);
                        this.$refs.addMain.AddMainlist = res;
                        this.$refs.addMain.setGys(res.supplierCode, this.selectRows.supplierName);
                        this.$refs.addMain.dialogFormVisible = true;
                    });
                } else {
                    this.$message.warning(this.$t("message.checkedplease"));
                    return false;
                }
            } else {
                this.$message.warning(this.$t("message.checkedplease"));
                return false;
            }
        },
        //作废
        handleCancel () {
            if (this.selectRows) {
                if (this.selectRows.pullMaterialStatus) {
                    this.$confirm(
                        this.$t("message.isCancellation"),
                        this.$t("message.prompt"),
                        {
                            confirmButtonText: this.$t("message.confirm"),
                            cancelButtonText: this.$t("message.cancel"),
                            type: "warning"
                        }
                    ).then(() => {
                        api.queryRowRecords(this.selectRows.pullOrderCode).then(res => {
                            res = res.data;
                            this.AddMainlist = res;
                            this.AddMainlist.method = "CANCELLATION";
                            api.updateRecords(
                                this.AddMainlist.pullOrderCode,
                                this.AddMainlist
                            ).then(() => {
                                this.getList();
                                this.$message({
                                    type: "success",
                                    message: this.$t("message.successed")
                                });
                            });
                        });
                    })
                        .catch(() => {
                            this.$message({
                                type: "info",
                                message: this.$t("message.canceled")
                            });
                        });
                } else {
                    this.$message.warning(this.$t("message.checkedplease"));
                    return false;
                }
            } else {
                this.$message.warning(this.$t("message.checkedplease"));
                return false;
            }
        },
        //关闭
        handleClose () {
            if (this.selectRows) {
                if (this.selectRows.pullMaterialStatus) {
                    this.$confirm(
                        this.$t("message.isCloseOrderContinue"),
                        this.$t("message.prompt"),
                        {
                            confirmButtonText: this.$t("message.confirm"),
                            cancelButtonText: this.$t("message.cancel"),
                            type: "warning"
                        }
                    ).then(() => {
                        api.queryRowRecords(this.selectRows.pullOrderCode).then(res => {
                            res = res.data;
                            this.AddMainlist = res;
                            this.AddMainlist.method = "CLOSE";
                            api.updateRecords(
                                this.AddMainlist.pullOrderCode,
                                this.AddMainlist
                            ).then(() => {
                                this.getList();
                                this.$message({
                                    type: "success",
                                    message: this.$t("message.successed")
                                });
                            });
                        });
                    })
                        .catch(() => {
                            this.$message({
                                type: "info",
                                message: this.$t("message.canceled")
                            });
                        });
                } else {
                    this.$message.warning(this.$t("message.checkedplease"));
                    return false;
                }
            } else {
                this.$message.warning(this.$t("message.checkedplease"));
                return false;
            }
        },
        //发布
        handlePublish () {
            if (this.selectRows) {
                if (this.selectRows.pullMaterialStatus) {
                    this.$confirm(
                        this.$t("message.isPublishOrder"),
                        this.$t("message.prompt"),
                        {
                            confirmButtonText: this.$t("message.confirm"),
                            cancelButtonText: this.$t("message.cancel"),
                            type: "warning"
                        }
                    ).then(() => {
                        api.queryRowRecords(this.selectRows.pullOrderCode).then(res => {
                            res = res.data;
                            this.AddMainlist = res;
                            this.AddMainlist.method = "PUBLISH";
                            api.updateRecords(
                                this.AddMainlist.pullOrderCode,
                                this.AddMainlist
                            ).then(() => {
                                this.getList();
                                this.$message({
                                    type: "success",
                                    message: this.$t("message.successed")
                                });
                            });
                        });
                    })
                        .catch(() => {
                            this.$message({
                                type: "info",
                                message: this.$t("message.canceled")
                            });
                        });
                } else {
                    this.$message.warning(this.$t("message.checkedplease"));
                    return false;
                }
            } else {
                this.$message.warning(this.$t("message.checkedplease"));
                return false;
            }
        },
        showWlxz (wlsupplierCode, wlorgCode, wlarrivalOrgCode, selectedDetails) {
            this.wlxzDialogFormVisible = true;
            this.$nextTick(() => {
                this.wlsupplierCode = wlsupplierCode;
                this.wlorgCode = wlorgCode;
                this.wlarrivalOrgCode = wlarrivalOrgCode
                this.$refs.wlxzRef.listQuery.wlarrivalOrgCode = wlarrivalOrgCode;
                this.$refs.wlxzRef.listQuery.orgCode = wlorgCode;
                this.$refs.wlxzRef.listQuery.supplierCode = wlsupplierCode;
                this.$refs.wlxzRef.listSelected = selectedDetails;
                this.$refs.wlxzRef.getList();
            })
        },
        update2 () {
            //物料选中中弹窗确认按钮
            if (this.$refs.wlxzRef.listSelected.length > 0) {
                this.$refs.addMain.setDetails(this.$refs.wlxzRef.listSelected);
                this.wlxzDialogFormVisible = false;
            } else {
                this.$message({
                    title: this.$t("message.warning"),
                    message: this.$t("message.checkedplease"),
                    type: "warning"
                });
            }
        },
        //供应商显示
        showGys (val) {
            this.gysValue = val;
            this.$refs.dialogRefgys.dialogVisible = true;
        },
        //供应商确认
        handleGysClick (gysRows) {
            this.supplierRows = gysRows;
            if (this.gysValue === 'query') {
                if (!this.supplierRows[0]) {
                    this.listQuery.supplierCode = "";
                    this.listQuery.supplierName = "";
                    this.$refs.dialogRefgys.dialogVisible = false;
                } else if (this.supplierRows.length === 1) {
                    this.listQuery.supplierCode = this.supplierRows[0].supplierCode;
                    this.listQuery.supplierName = this.supplierRows[0].supplierName;
                    this.$refs.dialogRefgys.dialogVisible = false;
                }
            } else if (this.gysValue === 'add') {
                if (!this.supplierRows[0]) {
                    this.$message({
                        title: this.$t("message.warning"),
                        message: this.$t("message.checkedplease"),
                        type: "warning"
                    });
                } else if (this.supplierRows.length === 1) {
                    this.$refs.addMain.setGys(
                        this.supplierRows[0].supplierCode,
                        this.supplierRows[0].supplierName
                    );
                    this.$refs.dialogRefgys.dialogVisible = false;
                }
            }
        }
    }
};
