import { queryRecords } from "@/api/data-base/source-list"
import * as api from "@/api/project/srm/priceManage/actuarialDetailCost"
import Pagination from "@/components/Pagination"
import { mapState } from 'vuex'
import { DICT_CODE } from '@/utils/constant'
import caiGou from '@/components/Select/xtpt/caigou'//下拉组件
import dialogGysxz from '@/components/Dialog/dialogsGysxz'//供应商选择
import costActuarial from '@/components/Dialog/costActuarial'//供应商选择
export default {
    name: "wlxz",
    components: { Pagination, caiGou, dialogGysxz, costActuarial },
    props: ['unMutiple', 'title', 'isRequired'],
    data () {
        return {
            radio: 'PCB0',
            list: null,
            listSelected: [],
            total: 0,
            parentsRows: null,
            listQuery: {
                orgCode: undefined,
                currentPage: 1,
                pageSize: 10,
                materielCode: undefined,//物料编码
                materielName: undefined,//物料名称
                materielType: undefined,//类型
                materielClassify: undefined,//分类
                materielGroup: undefined//物料组
            },
            selectCode: [],
            tableKey: [0, 1, 2],
            activeName2: 'first',
            DICT_CODE: DICT_CODE,
            dialogVisible: false
        }
    },
    computed: {
        ...mapState({
            dt_srm_pcb: state => state.dict.dt_srm_pcb,
        })
    },
    mounted () {
        this.$store.dispatch("dict/getDicData", ["dt_srm_pcb"])
    },

    methods: {
        checked (i) {

            if (this.radio == 'PCB0') {
                this.$refs.tbselect.toggleRowSelection(this.list[i], true)
            } else if (this.radio == 'PCB2') {
                this.$refs.tbselectBJ.toggleRowSelection(this.list[i], true)
            } else {
                this.$refs.tbselectJS.toggleRowSelection(this.list[i], true)
            }
        },
        changeStatus () {
            this.list = []
            this.getList()
        },
        handlePreview () {

        },
        handleSee (index, row) {
            this.$refs.cost.initForm(row)
            this.$refs.cost.dialogVisible = true
        },
        getData (val) {
            // this.listQuery.orgCode = val[0].orgCode
            this.$set(this.listQuery, 'orgCode', val[0].organizationCode)
            this.getList()
        },
        getList () {
            if (this.radio == 'PCB0') {
                queryRecords(this.listQuery).then(res => {
                    res = res.data
                    res.list = res.list.map(v => {
                        // v.materialCode = v.materielCode
                        // v.materialName = v.materielName
                        v.rowId = v.rowId.toString()
                        v.dataSource = this.radio
                        v.sourceNo = v.rowId.toString()
                        return v
                    })
                    let options = [this.dt_srm_pcb]
                    res.list = this.$myFun.codeToName(res.list, options, ['dataSource'])
                    this.list = res.list
                    this.total = res.pages.count
                    if (this.listSelected) {
                        this.listSelected.forEach(items => {
                            // debugger
                            this.list.forEach((item, index) => {
                                if (item.rowId == items.sourceNo) {
                                    this.$nextTick(function () {
                                        this.checked(index)
                                    })
                                }
                            })
                        })
                    }
                })
            } else if (this.radio == 'PCB2') {
                let dto = {
                    orgCode: this.listQuery.orgCode,
                    supplierCode: this.listQuery.supplierCode,
                    materielCode: this.listQuery.materielCode,
                    materielName: this.listQuery.materielName,
                    isFixed: '0',
                    isValid: '1',
                    currentPage: 1,
                    pageSize: 10,
                }
                api.queryRecords(dto).then(res => {
                    res = res.data
                    res.list = res.list.map(v => {
                        // v.materialCode = v.materielCode
                        // v.materialName = v.materielName
                        v.dataSource = this.radio
                        v.quotePrice = v.taxFreePrice
                        v.sourceNo = v.quotationNo
                        return v
                    })
                    let options = [this.dt_srm_pcb]
                    res.list = this.$myFun.codeToName(res.list, options, ['dataSource'])
                    this.list = res.list
                    this.total = res.pages.count
                    if (this.listSelected) {
                        this.listSelected.forEach(items => {
                            this.list.forEach((item, index) => {
                                if (item.sourceNo === items.sourceNo) {
                                    this.$nextTick(function () {
                                        this.checked(index)
                                    })
                                }
                            })
                        })
                    }
                })
            } else if (this.radio == 'PCB1') {
                let dto = {
                    state: 'P003',
                    orgCode: this.listQuery.orgCode,
                    supplierCode: this.listQuery.supplierCode,
                    materielCode: this.listQuery.materielCode,
                    materielName: this.listQuery.materielName,
                }
                api.queryRecordsMain(dto).then(res => {
                    res = res.data
                    res.list = res.list.map(v => {
                        // v.materialCode = v.materielCode
                        // v.materialName = v.materielName
                        v.dataSource = this.radio
                        v.quotePrice = v.supplierQuote
                        v.sourceNo = v.actuarialNo
                        return v
                    })
                    let options = [this.dt_srm_pcb]
                    res.list = this.$myFun.codeToName(res.list, options, ['dataSource'])
                    this.list = res.list
                    this.total = res.pages.count
                    if (this.listSelected) {
                        this.listSelected.forEach(items => {
                            this.list.forEach((item, index) => {
                                if (item.sourceNo === items.sourceNo) {
                                    this.$nextTick(function () {
                                        this.checked(index)
                                    })
                                }
                            })
                        })
                    }
                })
            }

        },
        showGys () {
            this.$refs.refGys.dialogVisible = true
            this.$refs.refGys.getList()
        },
        //供应商选择确定
        handleGysClick (val) {
            this.$set(this.listQuery, 'supplierName', val[0].supplierName)
            this.$set(this.listQuery, 'supplierCode', val[0].supplierCode)
        },
        handleQuery () {
            this.listQuery.currentPage = 1
            this.getList()
        },
        handleSizeChange (val) {
            this.listQuery.pageSize = val
            this.getList()
        },
        handleCurrentChange (val) {
            this.listQuery.currentPage = val
            this.getList()
        },
        //检查是否取消勾选
        checkUnsel (val, row) {
            let isuncheck = true
            val.forEach(item => {
                if (item.rowId === row.rowId) {
                    isuncheck = false
                }
            })
            return isuncheck
        },
        selectMain (val, row) {
            if (this.unMutiple) {
                let isuncheck = this.checkUnsel(val, row)
                if (!isuncheck) {
                    let arr = []
                    arr.push(val[val.length - 1])
                    this.listSelected = arr
                    if (val.length > 1) {
                        this.$refs.tbselect.clearSelection()
                        this.$refs.tbselect.toggleRowSelection(val[val.length - 1], 'selected')
                    }
                } else {
                    this.listSelected = []
                }
            } else {
                let isuncheck = this.checkUnsel(val, row)
                if (!isuncheck) {
                    if (this.listSelected) {
                        val.forEach((items, indexv) => {
                            let isSelected = false
                            this.listSelected.forEach((item) => {
                                if (item.sourceNo === items.sourceNo) {
                                    isSelected = true
                                }
                            })
                            if (!isSelected) {
                                this.listSelected.push(val[indexv])
                            }
                        })
                    } else {
                        this.listSelected = val
                    }
                } else {
                    this.listSelected = this.listSelected.filter(item => {
                        return item.sourceNo !== row.sourceNo
                    })
                }
            }
        },
        //供应商选择确定
        dialogConfirm () {
            if (this.isRequired && this.listSelected) {
                if (this.listSelected.length === 0) {
                    this.$message({ type: 'warning', message: '请选择一条数据' })
                } else {
                    this.$emit('handleConfirm', this.listSelected)
                    this.dialogVisible = false
                }
            } else {
                if (this.listSelected) {
                    if (this.listSelected.length === 0) {
                        if (this.valueType === 'code') {
                            this.$emit('handleConfirm', [{ materielCode: null, materielName: null }])
                        } else {
                            this.$emit('handleConfirm', [{ materielCode: null, materielName: null }])
                        }
                    } else {
                        this.$emit('handleConfirm', this.listSelected)
                    }
                } else {
                    if (this.valueType === 'code') {
                        this.$emit('handleConfirm', [{ materielCode: null, materielName: null }])
                    } else {
                        this.$emit('handleConfirm', null)
                    }
                }
                this.dialogVisible = false
            }
        },
    }
};
