export default {
    name: "bar",
    props: ['id', 'data'],
    data () {
        return {
            mycharts: undefined,
        }
    },
    watch: {
        data () {
            this.getPie()
        }
    },
    mounted () {
        window.onresize = () => {
            this.mycharts.resize()
            this.getPie()
        }
        this.getPie()
    },
    methods: {
        getPie () {
            if (!this.data) return false
            let legend = this.data.legend ? this.data.legend : []
            let xAxisData = this.data.xAxisData ? this.data.xAxisData : []
            let data = this.data.data.map(
                res => {
                    return {
                        name: res.name,
                        type: 'bar',
                        stack: '总量',
                        label: {
                            show: true,
                            position: 'insideRight'
                        },
                        data: res.data
                    }
                }
            )
            this.myChart = this.$echarts.init(document.getElementById(this.id))
            let option = {
                tooltip: {
                    trigger: 'axis',
                    axisPointer: {            // 坐标轴指示器，坐标轴触发有效
                        type: 'shadow'        // 默认为直线，可选为：'line' | 'shadow'
                    }
                },
                legend: {
                    data: legend
                },
                grid: {
                    left: '3%',
                    right: '4%',
                    bottom: '3%',
                    containLabel: true
                },
                yAxis: {
                    type: 'value'
                },
                xAxis: {
                    type: 'category',
                    data: xAxisData
                },
                series: data
            }
            this.myChart.setOption(option, true)
        }
    }
};
