import * as api from "@/api/project/srm/contractManage/templateMaintenance"
import Pagination from "@/components/Pagination"
import { DICT_CODE } from '@/utils/constant'
import caiGou from '@/components/Select/xtpt/caigou'//下拉组件
import { mapState } from "vuex"
export default {
    components: { caiGou, Pagination },
    data () {
        return {
            dialogVisible: false,
            tableKey: 0,
            list: [],
            DICT_CODE: DICT_CODE,
            total: 0,
            listQuery: {
                page: true,
                currentPage: 1,
                pageSize: 10,
                state: 'P003'
            },

        }
    },
    computed: {
        ...mapState({
            dt_srm_p0: state => state.dict.dt_srm_p0,
            dt_srm_pdv: state => state.dict.dt_srm_pdv,
        })
    },
    mounted () {
        this.$store.dispatch("dict/getDicData", ["dt_srm_p0", 'dt_srm_pdv'])
        this.getList()
    },
    methods: {
        getList () {
            api.queryRecords(this.listQuery).then(res => {
                let options = [this.dt_srm_p0, this.dt_srm_pdv]
                res.data.list = this.$myFun.srmCodeToName(res.data.list, options, ['state', 'contractType'])
                this.list = res.data.list
                this.total = res.data.pages.count
            })
        },
        handleQuery () {
            this.getList()
        },
        handlePreview () {

        },
        confirm () {
            if (this.selectlistRow) {
                console.log(this.selectlistRow, 'kkk')
                this.$emit('handeTemplate', this.selectlistRow)
                this.dialogVisible = false
            } else {
                this.$message({
                    title: '警告',
                    message: '请选择一条信息',
                    type: 'warning',
                    duration: 2000
                })
            }
        },
        // 表格选择框选中 注:参数为选中的所有行的数组
        selectChange (val) {
            this.selectlistRow = val[val.length - 1]
            if (val.length > 1) {
                this.$refs.tb.clearSelection()
                this.$refs.tb.toggleRowSelection(val.pop())
            }
            this.$refs.tb.setCurrentRow(this.selectlistRow)
        },
        //点击表格某一行
        rowClick (val) {
            this.$refs.tb.clearSelection()
            this.$refs.tb.toggleRowSelection(val)
        }
    }
}
