import * as api from "@/api/project/iot/IOT/ga"
export default {
    name: "info",
    props: ['code', 'row'],
    data () {
        return {
            dialogFormVisible: false,
            form: {
                "gatewayName": undefined,
                "gatewaySn": undefined,
                "gatewayType": undefined,
                "installationAddress": undefined,
                "networkingWay": undefined,
                "operator": undefined,
                "projectCode": undefined,
                "remark": undefined,
                "reportingPeriod": undefined
            },
            mqttUser: {},
        }
    },
    mounted () {
        this.getList()
    },
    methods: {
        getList () {
            api.queryGateway(this.code, this.row.gatewayCode).then(res => {
                this.form = res.data
                this.temp = res.data
                this.mqttUser = res.data.mqttUser ? res.data.mqttUser : {}
            })
        },
        handleDownload () {
            return this.$message({
                title: this.$t('message.warning'),
                message: "暂无该功能！",
                type: "warning"
            })
        }
    }
};
