import * as api from "@/api/project/srm/contractManage/evaluationModel"
import { mapState } from 'vuex'
import throttle from 'lodash/throttle'

export default {
    data () {
        return {
            isSingle: true,//表格是否单选 点击各按钮根据流程逻辑控制单多选
            list: [],
            dialogVisible: false,
            params: {}
        }
    },
    computed: {
        ...mapState({
            dt_srm_pb5: state => state.dict.dt_srm_pb5,
            dt_srm_pb4: state => state.dict.dt_srm_pb4,
            dt_srm_pas: state => state.dict.dt_srm_pas,

        })
    },
    mounted () {
        this.$store.dispatch('dict/getDicData', ['dt_srm_pb5', 'dt_srm_pb4', 'dt_srm_pas'])
    },
    methods: {
        getList () {
            api.queryRecordTree(this.params).then(res => {
                let options = [this.dt_srm_pb4]
                res.data = this.$myFun.srmCodeToName(res.data, options, ['evaluationItem'])
                this.list = res.data
            })
        },
        save: throttle(function () {
            if (this.selectlistRow && this.selectlistRow.length > 0) {
                api.setWeight(this.selectlistRow).then(() => {
                    this.$message({
                        title: '成功',
                        message: '配置成功',
                        type: 'success',
                        duration: 2000
                    })
                    this.dialogVisible = false
                    this.$emit('refresh')
                })
            } else {
                this.$message({
                    title: '警告',
                    message: '请勾选记录',
                    type: 'warning'
                })
            }
        }, 2000,
            {
                leading: true,
                trailing: false
            }
        ),
        // 表格选择框选中 注:参数为选中的所有行的数组
        selectRow (val) {
            this.selectlistRow = val
        },

    }
}
