import { DICT_CODE } from '@/utils/constant'
import caiGou from '@/components/Select/xtpt/caigou'//下拉组件
import dialogPersonSelect from '@/components/Dialog/dialogPersonSelect.vue'//选择人
import dialogGysxz from '@/components/Dialog/dialogsGysxz'//供应商选择
import { mapState } from "vuex"
import * as api from "@/api/project/srm/procurementDemand/divisionRules"
import throttle from 'lodash/throttle'

export default {
    components: { caiGou, dialogPersonSelect, dialogGysxz },
    data () {
        return {
            DICT_CODE: DICT_CODE,
            dialogStatus: '',
            dialogVisible: false,
            temp: {
                supplierCode: undefined,
                supplierName: undefined,
                rowId: undefined
            },
            rowId: null,
            Edit: null
        }
    },
    computed: {
        ...mapState({
            dt_srm_pbb: state => state.dict.dt_srm_pbb,
            dt_srm_pbc: state => state.dict.dt_srm_pbc,
        })
    },
    mounted () {
        this.$store.dispatch("dict/getDicData", ["dt_srm_pbb", 'dt_srm_pbc'])
    },
    methods: {
        //人员选择
        handlePersonCode () {
            this.$refs.person.dialogVisible = true
        },
        //人员选择确定
        personConfirm (val) {
            this.$set(this.temp, 'responsiblePeople', val[0].employeeCode)
            this.$set(this.temp, 'responsiblePeopleName', val[0].employeeName)
        },
        //供应商选择
        handleSupplier () {
            this.$refs.refGys.initSelect()
            this.$refs.refGys.dialogVisible = true
        },
        //供应商选择确定
        handleGysClick (val) {
            console.log(val)
            this.$set(this.temp, 'supplierCode', val[0].supplierCode)
            this.$set(this.temp, 'supplierName', val[0].supplierName)
        },
        //获取采购组织名称
        selectName (val) {
            this.temp.purchaseOrgName = val
        },
        //新增弹框确定事件
        createData: throttle(function () {
            this.$refs.dataForm.validate(valid => {
                if (valid) {
                    api.addRecordSub(this.rowId, this.temp).then(() => {
                        this.$message({
                            title: '成功',
                            message: '新增成功',
                            type: 'success',
                            duration: 2000
                        })
                        this.dialogVisible = false
                        this.$emit('refreshSub')
                    })
                } else {
                    return false
                }
            })
        }, 2000,
            {
                leading: true,
                trailing: false
            }
        ),
        //编辑弹框确定事件
        updateData: throttle(function () {
            api.updateRecordSub(this.temp.ruleId, this.temp.rowId, this.temp).then(() => {
                this.$message({
                    title: '成功',
                    message: '编辑成功',
                    type: 'success',
                    duration: 2000
                })
                this.dialogVisible = false
                this.$emit('refreshSub')
            })
        }, 2000,
            {
                leading: true,
                trailing: false
            }
        ),
        resetTemp () {
            this.temp = {
                featuresCode: undefined,
                supplierName: undefined,
                responsiblePeopleName: undefined
            }
        }
    }
}
