import DAInfo from "./DAInfo"
import GAInfo from "./GAInfo"
import mainBox from "./main"
import getPageTitle from '@/utils/getPageName'
import { queryProjects } from "@/api/project/iot/IOT/project"
import { queryRecords as queryDevice } from "@/api/project/iot/PM/equipmentProducts"
import { queryRecords as queryAateway } from "@/api/project/iot/PM/gatewayProducts"
export default {
    name: "alarmCenter",
    data () {
        return {
            pages: 'main',
            code: undefined, //一级页面项目编码
            title: getPageTitle(this.$route.name),
            projectList: null,
            deviceList: null,
            gatewayList: null,
        }
    },
    components: { mainBox, DAInfo, GAInfo },
    created () {
        this.getList()
    },
    methods: {
        setPage (pages) {
            this.pages = pages
        },
        setCode (code) {
            this.code = code
        },
        getList () {
            let listQuery = {
                page: true,
                currentPage: 1,
                pageSize: 1000,
            }
            Promise.all([queryProjects(listQuery), queryDevice(listQuery), queryAateway(listQuery)])
                .then(res => {
                    this.projectList = res[0].data.list
                    this.deviceList = res[1].data.list
                    this.gatewayList = res[2].data.list
                })

        }
    },
};
