import * as api from "@/api/project/srm/materialClassify/materialMaintain"
import { DICT_CODE } from '@/utils/constant'
import caiGou from '@/components/Select/xtpt/caigou'//下拉组件
import dialogPersonSelect from '@/components/Dialog/dialogPersonSelect.vue'//选择人
import dialogWlxzbyzsj from '@/components/Dialog/dialogMaterial.vue'//主数据物料选择
import throttle from 'lodash/throttle'

export default {
    components: { caiGou, dialogPersonSelect, dialogWlxzbyzsj },
    data () {
        return {
            dialogStatus: "",
            dialogVisible: false,
            person: "",
            DICT_CODE: DICT_CODE,
            dialogPersonVisible: false,
            titlePerson: "",
            parentsRows: [],
            rowId: null,
            temp: {
                purchaseSupporter: undefined,
                parentRowId: undefined,
                platform: true,
            },
            rules: {
                // orgCode: [{ required: true, message: '组织编码不能为空', trigger: 'blur' }],
                // parentRowId: [{ required: true, message: '上级分类编码不能为空', trigger: 'blur' }],
                // classCode: [{ required: true, message: '分类编码不能为空', trigger: 'blur' }],
                // className: [{ required: true, message: '分类名称不能为空', trigger: 'blur' }],
            }
        }
    },
    methods: {
        handleClassifyCode () {
            this.$refs.classify.dialogCodeVisible = true
        },
        //物料选择
        handleMaterialCode () {
            this.$refs.refZsjWlxz.dialogVisible = true
        },
        //物料选择确定
        confirmMateriel (data) {
            console.log(data, 'bbbvvv')
            this.$set(this.temp, 'materialCode', data[0].materielCode)
            this.$set(this.temp, 'materialName', data[0].materielName)
            this.$set(this.temp, 'materielEngName', data[0].materielEngName)//物料英文名
            this.$set(this.temp, 'materielType', data[0].materielType)//物料类型
            this.$set(this.temp, 'materielTypeName', data[0].materielTypeName)//物料类型名称
            this.$set(this.temp, 'materielGroup', data[0].materielGroup)//物料组
            this.$set(this.temp, 'materielGroupName', data[0].materielGroupName)//物料组名称
        },
        //人员选择
        handlePersonCode (val) {
            this.person = val
            if (this.person == 'cdPerson') {
                this.titlePerson = '裁定责任人'
            } else if (this.person == 'jsPerson') {
                this.titlePerson = '技术责任人'
            } else if (this.person == 'cgPerson') {
                this.titlePerson = '采购责任人'
            } else {
                this.titlePerson = '财会责任人'
            }
            this.$refs.person.dialogVisible = true
        },
        //人员选择确定
        personConfirm (val) {
            console.log(val[0].employeeName)
            if (this.titlePerson === '裁定责任人') {
                this.$set(this.temp, 'purchaseSupporter', val[0].employeeCode)
                this.$set(this.temp, 'purchaseSupporterName', val[0].employeeName)
            }
            if (this.titlePerson === '技术责任人') {
                this.$set(this.temp, 'purchaseAdmin', val[0].employeeCode)
                this.$set(this.temp, 'purchaseAdminName', val[0].employeeName)
            }
            if (this.titlePerson === '采购责任人') {
                this.$set(this.temp, 'businessPeople', val[0].employeeCode)
                this.$set(this.temp, 'businessPeopleName', val[0].employeeName)
            }
            if (this.titlePerson === '财会责任人') {
                this.$set(this.temp, 'businessPeoplez', val[0].employeeCode)
                this.$set(this.temp, 'businessPeopleNamez', val[0].employeeName)
            }
        },
        //获取组织编码名称
        selectName (val) {
            this.temp.orgName = val
        },
        //获取采购组名称
        selectorgGroupName (val) {
            this.temp.purchaseOrgName = val
        },
        handleClose () {
            this.$refs.dataForm.resetFields()
        },
        createData: throttle(function () {
            this.$refs.dataForm.validate(valid => {
                if (valid) {
                    api.addRecord(this.temp).then(() => {
                        this.$message({
                            title: '成功',
                            message: '新增成功',
                            type: 'success',
                            duration: 2000
                        })
                        this.dialogVisible = false
                        this.$emit('refresh')
                    })
                } else {
                    return false
                }
            })
        }, 2000,
            {
                leading: true,
                trailing: false
            }
        ),
        updateData: throttle(function () {
            let dto = {
                method: 'UPDATE',
                materielClassDTO: this.temp
            }
            api.updateRecord(this.rowId, dto).then(() => {
                this.$message({
                    title: '成功',
                    message: '编辑成功',
                    type: 'success',
                    duration: 2000
                })
                this.dialogVisible = false
                this.$emit('refresh')
            })
        }, 2000,
            {
                leading: true,
                trailing: false
            }
        ),
        listSelected (val) {
            this.temp.parentRowId = val[0].parentRowId
            this.$refs.classify.dialogCodeVisible = false
        },
        resetTemp () {
            this.temp = {
                orgCode: undefined,
                classCode: undefined,
                className: undefined,
                purchaseOrgCode: undefined,
                purchaseSupporter: undefined,
                purchaseAdmin: undefined,
                businessPeople: undefined,
                platform: true,
                remark: undefined

            }
        }
    }
}
