import QRCode from 'qrcode'//二维码
var canvas = ''
export default {
    props: ['imgindex'],
    methods: {
        qrCreate (orderNum) {
            QRCode.toCanvas(canvas, orderNum, (error) => {
                if (!error) {
                    document.getElementById("imgQr" + this.imgindex).src = canvas.toDataURL()
                }
            })
        }
    },
    mounted () {
        canvas = document.getElementById('qrccode-canvas' + this.imgindex)
    }
}
