// import *  as api from '@/api/project/eas/el/equipmentArchives'
import * as api from "@/api/data-base/materiel"
import dictItemSelect from "@/components/Select/dictItemSelect.vue"
import Pagination from '@/components/Pagination'
import { DICT_CODE } from "@/utils/constant"

export default {
    name: 'equipmentSpotCheck',
    components: { Pagination, dictItemSelect },
    props: {

        // 父组件中已选记录集合
        currentSelect: {
            type: Array,
            default: () => {
                return []
            }
        }
    },
    data () {
        return {
            list: null,
            total: 0,
            options: [],
            DICT_CODE: DICT_CODE,
            supplierData: null,
            listQuery: {
                currentPage: 1,
                pageSize: 10
            },
            activeName2: 'first',
            listSelected: [],
            tableKey: 0,
            allowSelect: false
        }
    },
    mounted () {
        this.getEquipmentData()
        // this.suppierRecords()
    },
    methods: {
        handleQuery () {
            this.listQuery.currentPage = 1
            this.getEquipmentData()
            this.currentSelect = []
        },
        selectList (val) {
            this.selectListRow = val
            this.$emit("change", this.selectListRow)
        },
        //检查是否取消勾选
        checkUnsel (val, row) {
            let isuncheck = true
            val.forEach(item => {
                if (item.materielCode === row.materielCode) {
                    isuncheck = false
                }
            })
            return isuncheck
        },
        selectMain (val, row) {
            let isuncheck = this.checkUnsel(val, row)
            if (!isuncheck) {
                if (this.listSelected) {
                    val.forEach((items, indexv) => {
                        let isSelected = false
                        this.listSelected.forEach(item => {
                            if (item.materielCode === items.materielCode) {
                                isSelected = true
                            }
                        })
                        if (!isSelected) {
                            this.listSelected.push(val[indexv])
                        }
                    })
                } else {
                    this.listSelected = val
                }
            } else {
                this.listSelected = this.listSelected.filter(item => {
                    return item.materielCode !== row.materielCode
                })
            }
        },

        selectAll (val) {
            if (val.length == 0) {
                this.listSelected = []
                this.$refs.tb_bx.clearSelection()
                return false
            }
            val.forEach((items, indexv) => {
                let isSelected = false
                this.listSelected.forEach(item => {
                    if (item.materielCode === items.materielCode) {
                        isSelected = true
                    }
                })
                if (!isSelected) {
                    this.listSelected.push(val[indexv])
                }
            })
        },
        getList () {
            api.queryRecords(this.listQuery).then(res => {
                this.list = res.data.list
                this.total = res.data.pages.count            })

        },
        getData () {
            this.$refs.tb_bx.clearSelection()
            this.currentSelect.forEach(item => {
                this.list = this.list.filter(v => v.materielCode != item.materielCode)
            })
            this.listSelected = []
        },
        // 在备选中把已选打钩
        getEquipmentData () {
            api.queryRecords(this.listQuery).then(res => {
                this.list = res.data.list
                this.total = res.data.pages.count
                // 已选中的勾选状态
                setTimeout(() => {
                    this.getData()
                }, 0)
            })
        }
    }
}
