import * as api from '@/api/project/eas/ESC/definePointInspectionPlan'
import Pagination from '@/components/Pagination'
import { mapState } from 'vuex' // 基本数据引入

export default {
    name: 'tab2',
    components: { Pagination },
    data () {
        return {
            list: null,
            total: 0,
            theight: 0, //表格高度
            listQuery: {
                currentPage: 1,
                pageSize: 10
            },
            temp: {
                holidayName: undefined,
                holidayType: undefined,
                inspectionPlanHolItems: [],
                tempTime: undefined,
                remark: undefined
            },
            weekDays: [
                { label: '星期一', value: '1' },
                { label: '星期二', value: '2' },
                { label: '星期三', value: '3' },
                { label: '星期四', value: '4' },
                { label: '星期五', value: '5' },
                { label: '星期六', value: '6' },
                { label: '星期日', value: '7' }
            ],
            tableKey: [0],
            dialogVisible: false,
            dialogStatus: '',
            rules: {
                holidayName: [
                    {
                        required: true,
                        message: this.$t('validate.required'),
                        trigger: 'blur'
                    }
                ],
                holidayType: [
                    {
                        required: true,
                        message: this.$t('validate.required'),
                        trigger: 'blur'
                    }
                ]
            },
            tmp: []
        }
    },
    watch: {
        'temp.holidayType' () {
            this.tmp = []
        }
    },
    computed: {
        // 过滤器下拉框item数据
        ...mapState({
            dt_holiday_type: state => state.eamDict.dt_holiday_type
        })
    },
    mounted () {
        this.$store.dispatch('eamDict/getDicData', ['dt_holiday_type'])
        this.setTableHeight()
        //表格高度自适应
        window.onresize = () => {
            this.setTableHeight()
        }
        this.getList()
    },
    methods: {
        getList () {
            if (this.$parent.$parent.$parent.selectlistRow.length) {
                api.queryHoliday(
                    this.$parent.$parent.$parent.selectlistRow[0]
                        .inspectionPlanCode,
                    this.listQuery
                ).then(res => {
                    this.list = res.data.list
                    this.total = res.data.pages.count
                })
            }
            this.resetTemp()
        },
        //表格高度计算
        setTableHeight () {
            this.theight = this.$myFun.getSingleTbHeight()
        },
        handleCreate () {
            if (!this.$parent.$parent.$parent.selectlistRow.length) {
                this.$message({
                    title: '警告',
                    message: '请选择计划',
                    type: 'warning'
                })
                return false
            }
            this.resetTemp()
            this.dialogVisible = true
            this.dialogStatus = 'create'
        },
        create () {
            if (!this.$parent.$parent.$parent.selectlistRow.length) {
                this.$message({
                    title: '警告',
                    message: '请选择计划',
                    type: 'warning'
                })
                return false
            }
            this.$refs['temp'].validate(valid => {
                if (valid) {
                    if (!this.tmp.length) {
                        this.$message({
                            title: '警告',
                            message: '请添加时间',
                            type: 'warning'
                        })
                        return false
                    }
                    this.formatData()
                    api.addHoliday(
                        this.$parent.$parent.$parent.selectlistRow[0]
                            .inspectionPlanCode,
                        this.temp
                    ).then(res => {
                        if (res.status == 201) {
                            this.$message({
                                title: '成功',
                                message: '创建成功',
                                type: 'success',
                                duration: 2000
                            })
                            this.getList()
                            this.dialogVisible = false
                            this.resetTemp()
                        }
                    })
                }
            })
        },
        handleUpdate (row) {
            if (!this.$parent.$parent.$parent.selectlistRow.length) {
                this.$message({
                    title: '警告',
                    message: '请选择计划',
                    type: 'warning'
                })
                return false
            }
            api.getHoliday(
                this.$parent.$parent.$parent.selectlistRow[0]
                    .inspectionPlanCode,
                row.holidayCode
            ).then(res => {
                this.dialogStatus = 'update'
                this.dialogVisible = true
                this.temp = res.data
                setTimeout(() => {
                    this.reformatData()
                }, 0)
            })
        },
        update () {
            if (!this.$parent.$parent.$parent.selectlistRow.length) {
                this.$message({
                    title: '警告',
                    message: '请选择计划',
                    type: 'warning'
                })
                return false
            }
            this.formatData()
            api.patchHoliday(
                this.$parent.$parent.$parent.selectlistRow[0]
                    .inspectionPlanCode,
                this.temp.holidayCode,
                this.temp
            ).then(res => {
                if (res.status == 201) {
                    this.$message({
                        title: '成功',
                        message: '修改成功',
                        type: 'success',
                        duration: 2000
                    })
                    this.getList()
                    this.dialogVisible = false
                    this.resetTemp()
                }
            })
        },
        formatData () {
            var tmp = []
            if (this.temp.holidayType == 'holiday_week') {
                this.tmp.forEach(item => {
                    tmp.push({
                        itemValue: item
                    })
                })
            } else {
                this.tmp.forEach(item => {
                    tmp.push({
                        itemValue:
                            this.temp.holidayType == 'holiday_time_section'
                                ? item.join(',')
                                : item
                    })
                })
            }
            this.temp.inspectionPlanHolItems = tmp
        },
        reformatData () {
            if (this.temp.holidayType == 'holiday_week') {
                this.temp.inspectionPlanHolItems.forEach(item => {
                    this.tmp.push(item.itemValue)
                })
            } else if (this.temp.holidayType == 'holiday_time_section') {
                this.temp.inspectionPlanHolItems.forEach(item => {
                    this.tmp.push(item.itemValue.split(','))
                })
            } else {
                this.temp.inspectionPlanHolItems.forEach(item => {
                    this.tmp.push(item.itemValue)
                })
            }
        },
        handleAddDate () {
            if (!this.temp.tempTime) {
                return false
            }
            if (this.temp.holidayType == 'holiday_time_section') {
                this.tmp.push([this.temp.tempTime[0], this.temp.tempTime[1]])
                this.temp.tempTime = undefined
                return false
            }
            this.tmp.push(this.temp.tempTime)
            this.temp.tempTime = undefined
        },
        handleDeleteDate (index) {
            this.tmp.splice(index, 1)
        },
        handleDelete (row) {
            if (!this.$parent.$parent.$parent.selectlistRow.length) {
                this.$message({
                    title: '警告',
                    message: '请选择计划',
                    type: 'warning'
                })
                return false
            }
            //删除列表
            api.deleteHoliday(
                this.$parent.$parent.$parent.selectlistRow[0]
                    .inspectionPlanCode,
                row.holidayCode
            ).then(res => {
                if (res.status == 204) {
                    this.getList()
                    this.$message({
                        title: '成功',
                        message: '删除成功',
                        type: 'success',
                        duration: 2000
                    })
                }
            })
        },
        resetTemp () {
            this.temp = {
                holidayName: undefined,
                holidayType: undefined,
                inspectionPlanHolItems: [],
                tempTime: undefined,
                remark: undefined
            }
            this.tmp = []
        }
    }
}
