import * as api from "@/api/project/srm/supplierSelection/RFQconfigure"
import { DICT_CODE } from '@/utils/constant'
import caiGou from '@/components/Select/xtpt/caigou'//下拉组件
import Pagination from "@/components/Pagination"
import { mapState } from "vuex"
import addMain from "./dialog/addMain"
export default {
    name: "RFQconfigure",
    components: { Pagination, caiGou, addMain },
    data () {
        return {
            DICT_CODE: DICT_CODE,
            theight: 0,//表格高度
            isSingle: true,//表格是否单选 点击各按钮根据流程逻辑控制单多选
            list: [],
            total: 0,
            listQuery: {
                //查询
                page: true,
                currentPage: 1,
                pageSize: 15
            },
            currentSelectedRow: null,//当前选中的行
            selectedRows: null,//多选时选中的所有行
            tableKey: 0 //表格索引
        }
    },
    created () { },
    computed: {
        ...mapState({
            dt_srm_pdf: state => state.dict.dt_srm_pdf,
            dt_srm_pdg: state => state.dict.dt_srm_pdg,
            dt_srm_pdh: state => state.dict.dt_srm_pdh,
        })
    },
    mounted () {
        this.$store.dispatch("dict/getDicData", ["dt_srm_pdf", 'dt_srm_pdg', 'dt_srm_pdh'])
        this.setTableHeight()
        //表格高度自适应
        window.onresize = () => {
            this.setTableHeight()
        }
        this.getList()
    },
    watch: {
        'dt_srm_pdf': function () {
            this.getList()
        }
    },
    methods: {
        //表格高度计算
        setTableHeight () {
            this.theight = this.$myFun.getSingleTbHeight()
        },
        refresh () {
            this.getList()
        },
        //获取表格数据
        getList () {
            api.queryRecords(this.listQuery).then(res => {
                let options = [this.dt_srm_pdf, this.dt_srm_pdg, this.dt_srm_pdg, this.dt_srm_pdh]
                res.data.list = this.$myFun.srmCodeToName(res.data.list, options, ['businessType', 'attachmentType', 'replyAttachmentType', 'reviewNoticeType'])
                this.list = res.data.list
                this.total = res.data.pages.count
            })
        },
        //表格查询
        handleQuery () {
            this.listQuery.currentPage = 1
            this.getList()
        },
        //新增按钮单击事件方法
        handleCreate () {
            this.$refs.addMain.resetTemp()
            this.$refs.addMain.dialogStatus = 'create'
            this.$refs.addMain.dialogVisible = true
        },
        //编辑按钮单击事件方法
        handleUpdate (index, val) {
            console.log(index, val)
            this.$refs.addMain.dialogStatus = 'update'
            this.$refs.addMain.temp = { ...val }
            this.$refs.addMain.dialogVisible = true
        },
        //删除按钮单击事件方法
        handleDelete (index, val) {
            console.log(index, val)
            api.deleteRecord(val.rowId).then(() => {
                this.$message({
                    title: '成功',
                    message: '删除成功',
                    type: 'success',
                    duration: 2000
                })
                this.getList()
            })
        },
        // 表格选择框选中 注:参数为选中的所有行的数组
        selectChange (val) {
            if (this.isSingle) {
                if (val.length > 1) {
                    this.$refs.tb.clearSelection() //清除其他行的选中
                    this.$refs.tb.toggleRowSelection(val[val.length - 1], "selected") //单击行绑定点击事件
                } else if (val.length === 1) {
                    this.selectedRows = val
                    this.currentSelectedRow = val[val.length - 1]
                }
            } else {
                this.selectedRows = val
                this.currentSelectedRow = val[val.length - 1]
            }
        },
        //点击表格某一行
        rowClick (val) {
            this.$refs.tb.toggleRowSelection(val, "selected") //单击行绑定点击事件
        }
    }
};
