import * as api from "@/api/project/srm/supplierManagement/supplierRegister"
import { mapState } from "vuex"

export default {
    data () {
        let validateArea = (rule, value, callback) => {
            const reg = /(^[1-9]\d{0,9}$)/
            if (value == '' || value == undefined || value == null) {
                callback()
            } else {
                if ((!reg.test(value)) && value != '') {
                    callback(new Error('请输入首位不为0的1-10位数'))
                } else {
                    callback()
                }
            }
        }
        return {
            dialogStatus: '',
            dialogVisible: false,
            createValid: false,
            list: [],
            temp: {},
            rules: {
                type: [{ required: true, message: '类型不能为空', trigger: 'blur' }],
                name: [{ required: true, message: '工厂名称不能为空', trigger: 'blur' }],
                address: [{ required: true, message: '工厂地址不能为空', trigger: 'blur' }],
                contacts: [{ required: true, message: '联系人不能为空', trigger: 'blur' }],
                telephone: [{ required: true, message: '联系电话不能为空', trigger: 'blur' }],
                email: [
                    { required: false, message: '请输入邮箱地址', trigger: 'blur' },
                    { type: 'email', message: '请输入正确的邮箱地址', trigger: ['blur', 'change'] }
                ],
                area: [
                    { validator: validateArea, trigger: "blur" }
                ]
            }
        }
    },
    computed: {
        ...mapState({
            dt_srm_pa7: state => state.dict.dt_srm_pa7,
        })
    },
    mounted () {
        this.$store.dispatch("dict/getDicData", ["dt_srm_pa7"])
    },
    methods: {
        create () {
            this.list.forEach(v => {
                if (v.type === 'PA70') {
                    if (v.type === this.temp.type) {
                        this.createValid = true
                    } else {
                        this.createValid = false
                    }
                }
            })
            this.$refs.dataForm.validate(valid => {
                if (valid) {
                    if (this.createValid === false) {
                        api.addRecordfactory(this.temp).then(() => {
                            this.$message({
                                title: '成功',
                                message: '新增成功',
                                type: 'success',
                                duration: 2000
                            })
                            this.dialogVisible = false
                            this.$emit('getFreshList')
                        })
                    } else {
                        this.$message({
                            title: '警告',
                            message: '工厂信息中只能有一个主要工厂',
                            type: 'warning',
                            duration: 2000
                        })
                    }

                } else {
                    return false
                }
            })
        },
        update () {
            this.list.forEach(v => {
                if (v.type === 'PA70') {
                    if (v.type === this.temp.type) {
                        this.createValid = true
                    } else {
                        this.createValid = false
                    }
                }
            })
            if (this.createValid === false) {
                api.updateRecordfactory(this.temp.rowId, this.temp).then(() => {
                    this.$message({
                        title: '成功',
                        message: '编辑成功',
                        type: 'success',
                        duration: 2000
                    })
                    this.dialogVisible = false
                    this.$emit('getFreshList')
                })
            } else {
                this.$message({
                    title: '警告',
                    message: '工厂信息中只能有一个主要工厂',
                    type: 'warning',
                    duration: 2000
                })
            }
        },
        resetTemp () {
            this.temp = {
                type: undefined,
                name: undefined,
                address: undefined,
                contactAddress: undefined,
                contacts: undefined,
                telephone: undefined,
                email: undefined,
                fax: undefined,
                area: undefined,
                supplierId: this.temp.supplierId
            }
        }
    }
}
