import Pagination from "@/components/Pagination"
import dialogFactory from "./dialog/dialogFactory"
import * as api from "@/api/project/srm/supplierManagement/supplierRegister"
import { mapState } from "vuex"

export default {
    name: "jsgl",
    components: { Pagination, dialogFactory },
    data () {
        return {
            theight: 0,//表格高度
            isSingle: true,//表格是否单选 点击各按钮根据流程逻辑控制单多选
            list: [],
            total: 0,
            rowId: null,
            listQuery: {
                //查询
                page: true,
                currentPage: 1,
                pageSize: 15
            },
            currentSelectedRow: null,//当前选中的行
            selectedRows: null,//多选时选中的所有行
            tableKey: 0 //表格索引
        }
    },
    computed: {
        ...mapState({
            dt_srm_pa7: state => state.dict.dt_srm_pa7,
        })
    },
    mounted () {
        this.setTableHeight()
        //表格高度自适应
        window.onresize = () => {
            this.setTableHeight()
        }
        this.$store.dispatch("dict/getDicData", ["dt_srm_pa7"])
    },
    methods: {
        //表格高度计算
        setTableHeight () {
            this.theight = this.$myFun.getSingleTbHeight() - 50
        },
        setTabData (val) {
            this.list = val
        },
        //新增编辑删除成功之后刷新表格数据
        getFreshList () {
            let b = {
                rowId: this.rowId
            }
            api.updateRecords(b).then(res => {
                let options = [this.dt_srm_pa7]
                res.data.factoryInfoDTOList = this.$myFun.srmCodeToName(res.data.factoryInfoDTOList, options, ['type'])
                this.list = res.data.factoryInfoDTOList
            })
        },
        //获取表格数据
        getList (row) {
            this.$refs.factory.temp.supplierId = row.rowId
            this.rowId = row.rowId
            let a = {
                rowId: row.rowId
            }
            api.updateRecords(a).then(res => {
                let options = [this.dt_srm_pa7]
                res.data.factoryInfoDTOList = this.$myFun.srmCodeToName(res.data.factoryInfoDTOList, options, ['type'])
                this.list = res.data.factoryInfoDTOList
            })
        },
        handleCreate () {
            //新增按钮单击事件方法
            this.$refs.factory.resetTemp()
            this.$nextTick(() => {
                this.$refs.factory.$refs['dataForm'].clearValidate()
            })
            this.$refs.factory.list = this.list
            this.$refs.factory.dialogStatus = 'create'
            this.$refs.factory.dialogVisible = true
        },
        handleUpdate (index, val) {
            //编辑按钮单击事件方法
            this.$refs.factory.dialogStatus = 'update'
            this.$refs.factory.dialogVisible = true
            this.$refs.factory.list = this.list
            this.$refs.factory.temp = val
        },
        handleDelete (index, val) {
            //删除按钮单击事件方法
            api.deleteRecordfactory(this.$refs.factory.temp.supplierId, val.rowId).then(() => {
                this.$message({
                    title: '成功',
                    message: '删除成功',
                    type: 'success',
                    duration: 2000
                })
                this.getFreshList()
            })
        },
        // 表格选择框选中 注:参数为选中的所有行的数组
        selectChange (val) {
            if (this.isSingle) {
                if (val.length > 1) {
                    this.$refs.tb.clearSelection() //清除其他行的选中
                    this.$refs.tb.toggleRowSelection(val[val.length - 1], "selected") //单击行绑定点击事件
                } else if (val.length === 1) {
                    this.selectedRows = val
                    this.currentSelectedRow = val[val.length - 1]
                }
            } else {
                this.selectedRows = val
                this.currentSelectedRow = val[val.length - 1]
            }
        },
        //点击表格某一行
        rowClick (val) {
            this.$refs.tb.toggleRowSelection(val, "selected") //单击行绑定点击事件
        }
    }
};
