// import * as api from "@/api/project/srm/dataBase/specialDistinction"
import * as api from '@/api/project/eas/el/equipmentRequisition'
import { DICT_CODE } from '@/utils/constant'
import { mapState } from 'vuex'
import vendorMainSelect from '@/components/Select/vendorMainSelect.vue' //厂家
import personSelect from '@/components/Select/personSelect.vue'
import { getDomainCode, getDomainCodeName } from '@/utils/auth'
export default {
    components: { vendorMainSelect, personSelect },
    data() {
        return {
            DICT_CODE: DICT_CODE,
            temp: {
                rowId: undefined,
                acceptanceDate: undefined,
                acceptorName: getDomainCodeName(),
                acceptor: getDomainCode()
            },
            dialogStatus: '',
            dialogVisible: false,
            rules: {
                deviceCodes: [
                    {
                        required: true,
                        message: '设备编号不能为空',
                        trigger: 'blur'
                    }
                ]
            },
            dialogVisibleVendorMain: false,
            dialogVisiblePerson: false,
            parentRows: [],
            parentRowsperson: []
        }
    },
    computed: {
        ...mapState({
            dt_device_state: state => state.eamDict.dt_device_state
        })
    },
    mounted() {
        this.$store.dispatch('eamDict/getDicData', ['dt_device_state'])
    },
    methods: {
        handleClose() {
            this.resetTemp()
            this.$refs.dataForm.resetFields()
        },
        //获取采购组织名称
        selectName(val) {
            this.temp.purchaseOrgName = val
        },
        handleEquipment() {
            this.dialogVisibleVendorMain = true
        },
        handleVendorMain(val) {
            this.parentRows = val
        },
        confirmVendorMain() {
            if (this.parentRows) {
                this.$set(
                    this.temp,
                    'manufacturersCode',
                    this.parentRows.manufacturersCode
                )
                this.$set(
                    this.temp,
                    'manufacturersName',
                    this.parentRows.manufacturersName
                )
                this.dialogVisibleVendorMain = false
            } else {
                this.$message({
                    title: '警告',
                    message: '请选择一条信息',
                    type: 'warning'
                })
            }
        },
        //请购人选择
        handlePerson() {
            this.dialogVisiblePerson = true
        },
        //请购人选择
        person(val) {
            this.parentRowsperson = val
        },
        //请购人确定
        confirmPerson() {
            if (this.parentRowsperson && this.parentRowsperson.length === 1) {
                this.$set(
                    this.temp,
                    'acceptor',
                    this.parentRowsperson[0].employeeCode
                )
                this.$set(
                    this.temp,
                    'acceptorName',
                    this.parentRowsperson[0].employeeName
                )
                this.dialogVisiblePerson = false
            } else {
                this.$message({
                    title: '警告',
                    message: '请选择一条信息',
                    type: 'warning'
                })
            }
        },
        //过滤标签
        removeTAG(str) {
            let a = str.replace(/\n/g, ',')
            a = a.replace(new RegExp(',+', 'gm'), ',')
            // var reg = /,$/gi
            return a.replace(/^,+/, '').replace(/,+$/, '')
        },
        createData() {
            this.$refs.dataForm.validate(valid => {
                if (valid) {
                    let dto = {
                        acceptanceDate: this.temp.acceptanceDate,
                        acceptor: this.temp.acceptor,
                        acceptorName: this.temp.acceptorName,
                        accountQuantity: parseInt(this.temp.accountQuantity),
                        arrivalQuantity: this.temp.arrivalQuantity,
                        category: this.temp.category,
                        deviceCodes: this.removeTAG(this.temp.deviceCodes),
                        deviceName: this.temp.deviceName,
                        manufacturersCode: this.temp.manufacturersCode,
                        modelNumber: this.temp.modelNumber,
                        state: this.temp.stateSub,
                        typeCode: this.temp.typeCode
                    }
                    if (dto.deviceCodes.match(/,/g) !== null) {
                        if (
                            dto.deviceCodes.match(/,/g).length ==
                            dto.accountQuantity - 1
                        ) {
                            api.account(
                                this.temp.requisitionsOrderCode,
                                this.temp.rowNo,
                                dto
                            ).then(() => {
                                this.$message({
                                    title: '成功',
                                    message: '到货成功',
                                    type: 'success',
                                    duration: 2000
                                })
                                this.dialogVisible = false
                                this.$emit('refresh')
                            })
                        } else {
                            this.$message({
                                title: '警告',
                                message: '设备编号数量需与入台账数量一致',
                                type: 'warning'
                            })
                        }
                    } else {
                        if (
                            dto.deviceCodes !== null &&
                            dto.deviceCodes !== ''
                        ) {
                            api.account(
                                this.temp.requisitionsOrderCode,
                                this.temp.rowNo,
                                dto
                            ).then(() => {
                                this.$message({
                                    title: '成功',
                                    message: '到货成功',
                                    type: 'success',
                                    duration: 2000
                                })
                                this.dialogVisible = false
                                this.$emit('refresh')
                            })
                        } else {
                            this.$message({
                                title: '警告',
                                message:
                                    '设备编号不能为空，设备编号数量需与入台账数量一致',
                                type: 'warning'
                            })
                        }
                    }
                } else {
                    return false
                }
            })
        },
        updateData() {
            // api.updateRecord(this.temp.rowId, this.temp).then(() => {
            //     this.$message({
            //         title: '成功',
            //         message: '编辑成功',
            //         type: 'success',
            //         duration: 2000
            //     })
            //     this.dialogVisible = false
            //     this.$emit('refresh')
            // })
        },
        resetTemp() {
            this.temp = {
                purchaseOrg: undefined,
                distinctionCode: undefined,
                distinctionName: undefined,
                remark: undefined,
                acceptanceDate: undefined,
                acceptorName: getDomainCodeName(),
                acceptor: getDomainCode()
            }
        }
    }
}
