import Pagination from "@/components/Pagination" // 分页组件
import * as api from "@/api/project/wms/outSourcing/outSourcingReceipt"
import { DT_ORG_TYPE } from "@/utils/constant"
import AuthoType from "@/components/Select/AuthoType.vue" //权限类型下拉组件
import * as api1 from "@/api/project/wms/productionMaterial/pullerStorage"
import { mapState } from "vuex"
import qrcode from "@/components/QRCode"
import gysxz from "@/components/Select/gysxz" //供应商选择
export default {
    name: "outSourcingReceipt",
    components: { Pagination, AuthoType, qrcode, gysxz },
    data () {
        return {
            list: null,
            subList: null,
            proofMainList: null,
            proofSubList: null,
            areaList: null,
            locatorList: [],
            value3: "",
            valuedate: "",
            total: 0,
            total1: 0,
            total2: 0,
            total3: 0,
            theight: 0,
            lheight: 0,
            rheight: 0,
            activeName2: "first",
            Edit: false,
            state: true,
            listQuery: {
                page: true,
                currentPage: 1,
                pageSize: 10,
                outOrderPlans: undefined,
                itemStates: undefined,
                supplierName: undefined,
                supplierCode: undefined,
                arrivalCompany: undefined,
                arrivalRepository: undefined,
                accountDate: this.timeFormat(),
                purchaseClassify: 'dt_purchase_class_2'

            },
            queryState: ['ADD', 'PART'],
            khxzDialogFormVisible: undefined,
            khxzDialogFormVisible1: undefined,
            listQuerySub: {
                currentPage: 1,
                pageSize: 10
            },
            dta: {
                currentPage: 1,
                pageSize: 10
            },
            listQueryProofMain: {
                currentPage: 1,
                pageSize: 10,
                orgCode: undefined,
                supplierName: undefined,
                supplierCode: undefined,
                warehouseCode: undefined,
                createDateStart: undefined,
                createDateEnd: undefined
            },
            listQueryProofSub: {
                currentPage: 1,
                pageSize: 10,
                businessType: undefined,
                outOrderNumber: undefined,
            },
            listQueryItems: {
                currentPage: 1,
                pageSize: 10,
                itemStates: undefined,
                instructCode: undefined
            },
            //保存数据
            saveTemp: {
                businessType: "",
                valuedate: ""
            },
            dto: {
                accountDate: undefined,
                outOrderPlans: undefined,
                businessType: undefined,
                customerCode: undefined,
                orgCode: undefined,
                sourceOrderCode: undefined,
                warehouseCode: undefined
            },
            parentsRows: [],
            locateWarehouseCode: null,
            downloadLoading: false,
            dialogPrintVisible: false,
            gysxzDialogFormVisible: false,
            gysxzDialogFormVisible1: false,
            isEdit: true,
            DT_ORG_TYPE: DT_ORG_TYPE,
            printData: {},//打印数据
            tableKey: 0
        }
    },

    created () {
        //创建时加载备选项
    },
    computed: {
        ...mapState({
            dt_business_type_screenGroupNum_wwsh: state =>
                state.businessComponent.dt_business_type_screenGroupNum_wwsh,
            dt_purchase_class: state => state.dict.dt_purchase_class,
        })
    },
    mounted () {
        this.$store.dispatch("businessComponent/getBusinessComponentData", [
            "dt_business_type_screenGroupNum_wwsh"
        ])
        this.$store.dispatch("dict/getDicData", [
            "dt_purchase_class",

        ])
        this.setTableHeight()
    },
    methods: {
        handleTabClick (tab) {//选项卡切换
            if (tab.name == "second") {
                this.getProofMainList()
            } else {
                this.getList()
            }
        },
        //表格高度计算
        setTableHeight () {
            this.theight = this.$myFun.getSingleTbHeight() - 110
            this.lheight = this.$myFun.getSingleTbHeight()
            this.rheight = this.$myFun.getSingleTbHeight() - 80
        },
        handleQuery () {
            if (this.listQuery.arrivalCompany && this.listQuery.arrivalRepository && this.listQuery.businessType) {
                this.listQuery.currentPage = 1
                this.getList()
            } else {
                this.$message({
                    title: this.$t("message.warning"),
                    message: "请先选择组织，仓库，业务类型",
                    type: "warning"
                })
            }

        },
        handleQueryProof () {
            this.listQueryProofMain.currentPage = 1
            this.getProofMainList()
        },
        handleDblclickgysxz (row) { //父子页面通信
            this.parentsRows = row
            if (!this.parentsRows[0]) {
                this.listQuery.supplierCode = ""
                this.listQuery.supplierName = ""
                this.$refs.gysxz.dialogVisible = false
            } else {
                this.listQuery.supplierCode = this.parentsRows[0].supplierCode
                this.listQuery.supplierName = this.parentsRows[0].supplierName
                this.$refs.gysxz.dialogVisible = false
            }
        },
        handleDblclickgysxz1 (row) {
            this.parentsRows1 = row
            if (!this.parentsRows1[0]) {
                this.listQueryProofMain.supplierCode = ""
                this.listQueryProofMain.supplierName = ""
                this.$refs.gysxz1.dialogVisible = false
            } else {
                this.listQueryProofMain.supplierCode = this.parentsRows1[0].supplierCode
                this.listQueryProofMain.supplierName = this.parentsRows1[0].supplierName
                this.$refs.gysxz1.dialogVisible = false
            }

        },
        handleClickgysxz () {
            this.$refs.gysxz.dialogVisible = true

        },
        handleClickgysxz1 () {
            this.$refs.gysxz1.dialogVisible = true
        },

        timeFormat () {
            var d = new Date()
            var year = d.getFullYear()       //年  
            var month = d.getMonth() + 1     //月  
            var day = d.getDate()            //日  

            var clock = year + "-"
            if (month < 10)
                clock += "0"
            clock += month + "-"

            if (day < 10)
                clock += "0"
            clock += day
            return (clock)
        },
        getList () {//查询出库登记主表
            this.listQuery.itemStates = this.queryState.join(",")
            api.queryRecords(this.listQuery).then(res => {
                this.list = res.data.list
                this.total = res.data.pages.count
                if (this.total == 0) {
                    this.subList = []
                }
                this.$nextTick(function () {
                    this.checked()
                })
            })

        },
        checked () {
            this.$refs.dd.toggleRowSelection(this.list[0], true)//官网方法，重点
        },
        getSubList () {  //查询出库登记从表
            this.listQueryItems.itemStates = this.queryState.join(",")
            this.listQueryItems.orderNumber = this.selectlistRow.orderNumber
            api.queryRecordscb(this.listQueryItems).then(res => {
                res.data.list.forEach((item, index) => {
                    res.data.list[index].curQuantity = 0
                })
                this.subList = res.data.list.map(v => {
                    v.receivedQuantity = v.receiveQuantity + v.stockQuantity
                    return v
                })
                for (let i = 0; i < this.subList.length; i++) {
                    this.locatorList.push([])
                }

                api1.getArea(this.locateWarehouseCode).then(resa => {
                    this.areaList = resa.data
                })
            })
        },

        getProofMainList () {//查询出库凭证主表
            if (this.value3) {
                var d1 = this.value3[0]
                var d2 = this.value3[1]
                this.listQueryProofMain.createDateStart = d1
                this.listQueryProofMain.createDateEnd = d2
            } else {
                this.listQueryProofMain.createDateStart = ""
                this.listQueryProofMain.createDateEnd = ""
            }
            this.proofSubList = []
            api.queryRecorditems(this.listQueryProofMain).then(res => {
                this.proofMainList = res.data.list
                // this.total2=res.pages.count
            })
        },
        getProofSubList () {//查询出库凭证从表
            this.listQueryProofSub.receiveOrderNumber = this.selectlistRowProof.receiveOrderNumber
            this.listQueryProofSub.businessType = this.selectlistRowProof.businessType
            api.queryRecordsmx(this.listQueryProofSub).then(res => {
                this.proofSubList = res.data.list
                this.total3 = res.data.pages.count
            })
        },
        //保存
        handleSave () {
            if (this.listQuery.businessType && this.listQuery.accountDate) {
                //save接口
                if (this.selectlistRow.orderNumber) {
                    let result = true
                    let check = false
                    let saveList = []
                    let b = this.subList.map(v => {
                        v.receiveQuantity = v.curQuantity
                        v.toWareAreaCode = '*'
                        v.toWareLocationCode = '*'
                        v.sourceOrderNumber = this.selectlistRow.orderNumber
                        v.sourceRowNo = v.rowNo
                        v.receiveOrderNumber = this.selectlistRow.orderNumber
                        return v
                    })
                    b.forEach(item => {
                        if (item.receiveQuantity > 0 && item.wareAreaCode || item.wareLocationCode) {
                            result = false
                            saveList.push(item)
                        }
                        if (item.curQuantity > item.stockQuantity) {
                            check = true
                        }
                    })
                    if (result) {
                        this.$message({
                            title: "警告",
                            message: "本次收货量不可全部为0或空,区位储位不能为空",
                            type: "warning"
                        })
                    } else if (check) {
                        this.$message({
                            title: "警告",
                            message: "本次收货量不可大于可收货量",
                            type: "warning"
                        })
                    } else {
                        this.dto.receiveOrderItemDTOs = saveList
                        this.dto.businessType = this.listQuery.businessType
                        this.dto.accountDate = this.listQuery.accountDate
                        this.dto.orgCode = this.selectlistRow.arrivalCompany
                        this.dto.sourceOrderNumber = this.selectlistRow.orderNumber
                        this.dto.sourceRowNo = this.selectlistRow.rowNo
                        this.dto.receiveOrderNumber = this.selectlistRow.orderNumber
                        this.dto.supplierCode = this.selectlistRow.supplierCode
                        this.dto.warehouseCode = this.listQuery.arrivalRepository
                        this.dto.preSourceOrderNumber = this.selectlistRow.orderNumber
                        api.Save(this.dto).then(() => {
                            this.$notify({
                                title: "成功",
                                message: "保存成功",
                                type: "success",
                                duration: 2000
                            })
                            this.getSubList()
                        })
                    }
                } else {
                    this.$message({
                        title: this.$t("message.warning"),
                        message: "请选择要保存的指示单",
                        type: "warning"
                    })
                }
            } else {
                this.$message({
                    title: this.$t("message.warning"),
                    message: "请先选择业务类型和记账日期",
                    type: "warning"
                })
            }
        },
        handleRecommend () {
            if (this.selectlistRowSub) {
                this.dto.businessType = this.selectlistRow.businessType,
                    this.dto.orgCode = this.selectlistRow.arrivalCompany,
                    this.dto.wareMaterielDTOS = this.selectlistRowSub.map(v => {
                        let DTOSlist = {}
                        DTOSlist.materielCode = v.materielCode
                        DTOSlist.materielClassify = ""
                        return DTOSlist
                    }),
                    this.dto.warehouseCode = this.listQuery.arrivalRepository,
                    api.recommend(this.dto).then(res => {
                        res.data.forEach((val) => {
                            this.subList.forEach((val1, index) => {
                                if (val.materielCode == val1.materielCode) {
                                    this.$set(this.subList[index], 'wareAreaCode', val.areaCode)
                                    this.$set(this.subList[index], 'wareLocationCode', val.locationCode)
                                    this.$set(this.subList, index, this.subList[index])
                                }
                            })
                        })
                    })
            } else {
                this.$message({
                    title: "警告",
                    message: "请选择信息",
                    type: "warning"
                })
            }
        },
        //复制
        handleCopy () {
            if (this.selectlistRowSub.length) {
                this.subList.map((item, index) => {
                    this.selectlistRowSub.forEach(items => {
                        if (items.materielCode === item.materielCode) {
                            this.subList[index].curQuantity = item.deliverableQuantity
                            this.$set(this.subList, index, this.subList[index])
                        }
                    })
                })
            } else {
                this.$message({
                    title: this.$t("message.warning"),
                    message: this.$t("message.checkedplease"),
                    type: "warning"
                })
            }
        },
        handleDownload () {
            this.downloadLoading = true
            import('@/vendor/Export2Excel').then(excel => {
                const tHeader = [
                    '凭证号', '业务类型',
                    '物料编码', '物料名称', '数量',
                    '委外工厂', '委外名称', '组织编码',
                    '仓库编码', '区位', '储位',
                    '记账日期', '创建时间',
                    '记账人', '订单号',
                    '订单行号'
                ]
                const filterVal = [
                    'receiveOrderNumber ', 'businessType',
                    'materielCode', 'materielName', 'receiveQuantity',
                    'supplierCode', 'supplierName', 'orgCode',
                    'warehouseCode', 'WareAreaCode',
                    'WareLocationCode', 'accountDate',
                    'createTime', 'creator',
                    'sourceOrderCode', 'sourceRowNo'
                ]
                const data = this.formatJson(filterVal, this.list)
                excel.export_json_to_excel({
                    header: tHeader,
                    data,
                    filename: 'table-list'
                })
                this.downloadLoading = false
            })
        },
        formatJson (filterVal, jsonData) {
            return jsonData.map(v => filterVal.map(j => {
                if (j === 'timestamp') {
                    return
                } else {
                    return v[j]
                }
            }))
        },
        //打印
        receiptPrint () {
            if (this.selectlistRowProof) {
                this.dialogPrintVisible = true
                this.dialogStatus = "print"
                this.printData = this.selectlistRowProof
                this.$nextTick(() => {
                    this.$refs.childQRcode.qrCreate(this.printData.receiveOrderNumber)
                })

            } else {
                this.$message.warning(this.$t('message.checkedplease'))
                return false
            }

        },
        //打印执行
        doReceiptPrint () {
            let newWin = window.open("")
            var newstr = document.getElementById('receipt').innerHTML
            newWin.document.write(newstr)
            newWin.document.close()//IE添加

            setTimeout(function () {
                newWin.print()
                newWin.close()
            }, 100)
        },
        getLocatorList (warehouseCode, areaCode, i) {
            api1.getLocator(warehouseCode, areaCode).then(res => {
                this.$set(this.locatorList, i, res.data)
                this.$set(this.subList[i], 'wareLocationCode', res.data[0].locationCode)
                this.$set(this.subList, i, this.subList[i])
            })
        },
        changeAreaSel (areaCode, i) {
            this.getLocatorList(this.locateWarehouseCode, areaCode, i)
        },


        selectRow (val) {//出库登记复选框选中方法
            this.selectlistRow = val[val.length - 1]
            if (val.length > 1) {
                this.$refs.dd.clearSelection()
                this.$refs.dd.toggleRowSelection(val.pop())
            }
            this.$refs.tb.setCurrentRow(this.selectlistRow)
            if (this.selectlistRow) {
                this.locateWarehouseCode = this.listQuery.arrivalRepository
                this.getSubList()
            }
        },
        selectRowMain (val) {//出库登记单击行选中方法
            this.selectlistRowMain = val
            this.locateWarehouseCode = val.warehouseCode
            this.$refs.dd.clearSelection()
            this.$refs.dd.toggleRowSelection(val) //单击行绑定点击事件
        },

        selectRowSub (val) {//已选项选中行
            // this.$emit('sendVal' ,val)
            this.selectlistRowSub = val
        },
        selectRowProof (val) {//出库凭证复选框选中方法
            this.selectlistRowProof = val[val.length - 1]
            if (val.length > 1) {
                this.$refs.tb.clearSelection()
                this.$refs.tb.toggleRowSelection(val.pop())
            }
            this.$refs.tb.setCurrentRow(this.selectlistRowProof)
            if (this.selectlistRowProof) {
                this.getProofSubList()
            }
        },
        selectRowProofMain (val) {//出库凭证单击行选中方法
            this.selectlistRowPmain = val
            this.$refs.tb.clearSelection()
            this.$refs.tb.toggleRowSelection(val) //单击行绑定点击事件
        },
        selectRowProofSub (val) {
            this.selectlistRowchange = val
        },
        setdefault (val, i) {
            if (i === 1) {
                this.listQuery.arrivalCompany = val
            } else if (i === 3) {
                this.listQuery.arrivalRepository = val
                this.$set(this.listQuery, 'businessType', this.dt_business_type_screenGroupNum_wwsh.options[0].wareRuleCode)
            }
        },
        setdefault1 (val, i) {
            if (i === 1) {
                this.listQueryProofMain.orgCode = val
            } else if (i === 3) {
                this.listQueryProofMain.warehouseCode = val
                this.listQueryProofMain.businessType = this.dt_business_type_screenGroupNum_wwsh.options[0].wareRuleCode
                this.getProofMainList()
            }
        }
    }
};
