import * as api from '@/api/project/srm/supplierManage/supplierChangeNotice'
import { queryRecords } from '@/api/project/srm/supplierManage/supplierConfigure'
import Pagination from "@/components/Pagination"
import { mapState } from 'vuex'
import addMain from "./dialog/addMain" //新增编辑弹框
import addMainsh from "./dialog/addMainsh" //审核弹框
import appendix from "./dialog/appendix" //附件弹框
import throttle from 'lodash/throttle'

export default {
    name: "delivery_Publish",
    components: { Pagination, addMain, addMainsh, appendix },
    data () {
        return {
            changeOrderNo: null,
            theight: 0,
            isSingle: true,//表格是否单选 点击各按钮根据流程逻辑控制单多选
            currentSelectedRow: null,//当前选中的行
            selectedRows: null,//多选时选中的所有行
            list: null,
            listSub: null,
            total: 0,
            selectedrow: null,//主表选中行
            dialogVisible: false,
            dialogVisiblefj: false,
            dialogVisiblesh: false,
            dialogStatus: "",
            changeType: [],
            queryState: ['PAR1', 'PAR2'],
            listQuery: {
                page: true,
                currentPage: 1,
                pageSize: 10
            },
            sublistQuery: {
                page: true,
                currentPage: 1,
                pageSize: 10
            },
            tableKey: [0, 1]//表格索引，虚拟dom渲染表格时使用

        }
    },
    computed: {
        ...mapState({
            dt_srm_p0: state => state.dict.dt_srm_p0,
            dt_srm_par: state => state.dict.dt_srm_par,
        })
    },
    mounted () {
        this.setTableHeight()
        //表格高度自适应
        window.onresize = () => {
            this.setTableHeight()
        }
        this.$store.dispatch('dict/getDicData', ['dt_srm_p0', 'dt_srm_par'])
        this.getList()
    },
    methods: {
        //表格高度计算
        setTableHeight () {
            this.theight = this.$myFun.getSingleTbHeight() - 185
        },
        refresh () {
            this.getList()
        },
        getType () {
            let a = {
                state: 'P002',
                changeMode: this.queryState.join(",")
            }
            queryRecords(a).then(res => {
                this.changeType = res.data
            })
        },
        //显示主表
        getList () {
            api.queryRecords(this.listQuery).then(res => {
                let options = [this.dt_srm_p0, this.dt_srm_par]
                res.data.list = this.$myFun.srmCodeToName(res.data.list, options, ['state', 'changeMode'])
                this.list = res.data.list
                this.total = res.data.pages.count
                this.$nextTick(function () {
                    this.checked()
                })
            })
            this.getType()
        },
        //显示从表
        getSubList () {
            this.sublistQuery.changeOrderNo = this.selectlistRow.changeOrderNo
            api.queryRecordsItems(this.sublistQuery).then(res => {
                this.listSub = res.data.list
            })
        },
        checked () {
            this.$refs.tbmain.toggleRowSelection(this.list[0], true)//官网方法，重点
        },
        //附件按钮单击事件
        handleAppendix () {
            this.$refs.appendix.dialogVisible = true
        },
        //新增按钮单击事件
        handleCreate () {
            this.$refs.addMain.resetTemp()
            this.$refs.addMain.list = []
            this.$refs.addMain.dialogStatus = "create"
            this.$refs.addMain.dialogVisible = true
            this.$refs.addMain.showForm = false
            this.$refs.addMain.getType()
        },
        //编辑
        handleUpdate (index, val) {
            console.log(index, val)
            this.$refs.addMain.getType()
            this.$refs.addMain.temp = { ...val }
            this.$refs.addMain.rowId = val.rowId
            this.$refs.addMain.changeOrderNo = val.changeOrderNo
            this.$refs.addMain.changeConfigId = val.changeConfigId
            this.$refs.addMain.dialogStatus = "update"
            this.$refs.addMain.dialogVisible = true
            this.$refs.addMain.showForm = false
            this.$refs.addMain.getItemList()
        },
        handleSearch (index, val) {
            console.log(index)
            this.$refs.addMain.temp = { ...val }
            this.$refs.addMain.changeOrderNo = val.changeOrderNo
            this.$refs.addMain.title = "供应商信息变更查询"
            this.$refs.addMain.dialogStatus = "update"
            this.$refs.addMain.dialogVisible = true
            this.$refs.addMain.showForm = true
            this.$refs.addMain.getItemList()
        },
        //生效
        handleEffect: throttle(function () {
            if (this.selectlistRow) {
                let dto = {
                    method: 'commit'
                }
                api.updatePublish(this.selectlistRow.rowId, dto).then(() => {
                    this.$message({
                        title: '成功',
                        message: '生效成功',
                        type: 'success',
                        duration: 2000
                    })
                    this.getList()
                })
            } else {
                this.$message({
                    title: '警告',
                    message: '请选择一条信息',
                    type: 'warning',
                    duration: 2000
                })
            }
        }, 2000,
            {
                leading: true,
                trailing: false
            }
        ),
        //审核
        handleExamine () {
            if (this.selectlistRow) {
                if (this.selectlistRow.state == "P002") {
                    this.$refs.addMainsh.resetExamine()
                    this.$refs.addMainsh.changeOrderNo = this.selectlistRow.changeOrderNo
                    this.$refs.addMainsh.dialogVisible = true
                    this.$refs.addMainsh.rowId = this.selectlistRow.rowId
                    this.$refs.addMainsh.temp = this.selectlistRow
                    this.$refs.addMainsh.getType()
                    this.$refs.addMainsh.getItemList()
                } else {
                    this.$message({
                        title: '警告',
                        message: '只有已提交状态的数据可审核',
                        type: 'warning',
                        duration: 2000
                    })
                }
            } else {
                this.$message({
                    title: '警告',
                    message: '请选择一条信息',
                    type: 'warning',
                    duration: 2000
                })
            }

        },
        //发布
        handlePublish () {
            if (this.selectlistRow) {
                this.dialogVisible = true
                this.changeOrderNo = this.selectlistRow.changeOrderNo
            } else {
                this.$message({
                    title: '警告',
                    message: '请选择一条信息',
                    type: 'warning',
                    duration: 2000
                })
            }

        },
        //发布确认
        publishConfirm: throttle(function () {
            let dto = {
                method: 'publish'
            }
            api.updatePublish(this.selectlistRow.rowId, dto).then(() => {
                this.$message({
                    title: '成功',
                    message: '确定成功',
                    type: 'success',
                    duration: 2000
                })
                this.dialogVisible = false
                this.getList()
            })
        }, 2000,
            {
                leading: true,
                trailing: false
            }
        ),
        //删除
        handleDelete (index, val) {
            this.$confirm("是否删除该信息", "提示", {
                confirmButtonText: "确定",
                cancelButtonText: "取消",
                type: "warning"
            }).then(() => {
                api.deleteRecord(val.rowId).then(() => {
                    this.$message({
                        title: '成功',
                        message: '删除成功',
                        type: 'success',
                        duration: 2000
                    })
                    this.getList()
                })
            }).catch(() => {
                this.$message({
                    type: "info",
                    message: "已取消"
                })
            })
        },
        //查询
        handleQuery () {
            this.listQuery.currentPage = 1
            this.listSub = []
            this.getList()
        },
        // 表格选择框选中 注:参数为选中的所有行的数组
        selectChange (val) {
            this.selectlistRow = val[val.length - 1]
            if (val.length > 1) {
                this.$refs.tbmain.clearSelection()
                this.$refs.tbmain.toggleRowSelection(val.pop())
            }
            if (this.selectlistRow) {
                this.getSubList()
            }
            this.$refs.tbmain.setCurrentRow(this.selectlistRow)
        },
        //点击表格某一行
        rowClick (val) {
            this.$refs.tbmain.clearSelection()
            this.$refs.tbmain.toggleRowSelection(val)
        },
        //加载时默认选中第一行
        setFirstLine () {
            this.$nextTick(() => {
                this.$refs.tbmain.setCurrentRow(this.list[0])
            })
        }
    }
}
