import * as auth from '@/utils/auth'
import * as api from '@/api/project/mes/dataBase/unqualifiedProduct'
import Pagination from '@/components/Pagination'
// import { mapState } from "vuex"

export default {
    name: 'unqualified',
    components: { Pagination },
    data() {
        return {
            username: auth.getName(),
            list: [''],
            selectlistRow: undefined,
            total: 0,
            theight: 0, //表格高度
            listQuery: {
                page: true,
                currentPage: 1,
                pageSize: 10,
                barCode: undefined,
                state: 'quality_inspection20'
            },
            tableKey: 0
        }
    },
    mounted() {
        this.setTableHeight()
        //表格高度自适应
        window.onresize = () => {
            this.setTableHeight()
        }
        this.getList()
    },
    methods: {
        getList() {
            api.queryRecords(this.listQuery).then(res => {
                this.list = res.data.list
                this.total = res.data.pages.count
            })
        },
        //表格高度计算
        setTableHeight() {
            this.theight = this.$myFun.getSingleTbHeight() * 0.85
        },
        handleQuery() {
            //查询
            this.listQuery.currentPage = 1
            this.getList()
        },
        // 获取表格选中时的数据
        selectList(val) {
            this.selectlistRow = val[val.length - 1]
            if (val.length > 1) {
                this.$refs.mainList.clearSelection()
                this.$refs.mainList.toggleRowSelection(
                    val[val.length - 1],
                    'selected'
                )
            }
        },
        //主表行点击
        selectListRow(val) {
            this.selectlistRow = val
            this.$refs.mainList.clearSelection()
            this.$refs.mainList.toggleRowSelection(val, 'selected')
        },
        patchRecords(method) {
            if (this.selectlistRow == undefined) {
                this.$message({
                    title: '警告',
                    message: '请选择待维修项',
                    type: 'warning'
                })
                return false
            }
            this.$confirm(
                '确认' +
                    (method == 'REPAIR'
                        ? '维修完成?'
                        : method == 'CONCEDE'
                        ? '让步接收?'
                        : method == 'DOWNGRADE'
                        ? '降级处理?'
                        : method == 'SCRAP'
                        ? '报废?'
                        : ''),
                '提示',
                {
                    confirmButtonText: '确定',
                    cancelButtonText: '取消',
                    type: 'warning'
                }
            ).then(() => {
                api.patchRecords(this.selectlistRow.rowId, method).then(res => {
                    if (res.status == 201) {
                        this.$message({
                            title: '成功',
                            message: '操作成功',
                            type: 'success'
                        })
                        this.getList()
                    }
                })
            })
        }
    }
}
