import * as api from "@/api/project/srm/quotaManagement/quotaAdjustment"
import Pagination from "@/components/Pagination"
import { mapState } from "vuex"
import dialogGysxz from '@/components/Dialog/dialogsGysxz'//供应商选择
import addMain from './dialog/addMain'
import examine from './dialog/examine'
import { DICT_CODE } from '@/utils/constant'
import caiGou from '@/components/Select/xtpt/caigou'//下拉组件
import dialogOrgSelect from '@/components/Dialog/dialogOrgSelect'
import dialogWlxzbycglx from '@/components/Dialog/dialogWlxzbycglx.vue'//物料选择
export default {
    name: "quotaAdjustment",
    components: { Pagination, dialogGysxz, addMain, examine, caiGou, dialogOrgSelect, dialogWlxzbycglx },
    data () {
        return {
            rangeDatezx: null,
            rangeDate: null,
            DICT_CODE: DICT_CODE,
            theight: 0,
            isSingle: true,//表格是否单选 点击各按钮根据流程逻辑控制单多选
            currentSelectedRow: null,//当前选中的行
            selectedRows: null,//多选时选中的所有行
            list: null,
            listSub: null,
            total: 0,
            selectedrow: null,//主表选中行
            listQuery: {
                page: true,
                currentPage: 1,
                pageSize: 10,
                orgName: undefined,
                orgCode: undefined
            },
            sublistQuery: {
                page: true,
                currentPage: 1,
                pageSize: 10
            },
            tableKey: [0, 1]//表格索引，虚拟dom渲染表格时使用

        }
    },
    computed: {
        ...mapState({
            dt_srm_p0: state => state.dict.dt_srm_p0,
            dt_srm_pc2: state => state.dict.dt_srm_pc2,
            dt_srm_paf: state => state.dict.dt_srm_paf,
            dt_srm_pag: state => state.dict.dt_srm_pag
        })
    },
    mounted () {
        this.$store.dispatch("dict/getDicData", ['dt_srm_p0', 'dt_srm_pc2', 'dt_srm_paf', 'dt_srm_pag'])
        this.setTableHeight()
        //表格高度自适应
        window.onresize = () => {
            this.setTableHeight()
        }

        this.getList()
    },
    watch: {
        'listQuery.orgCode' (val) {
            if (val) {
                this.getList()
            }
        },
        'rangeDate' (value) {
            if (value) {
                this.listQuery.firstCompileDate = value[0]
                this.listQuery.endCompileDate = value[1]
            } else {
                this.listQuery.firstCompileDate = undefined
                this.listQuery.endCompileDate = undefined
            }
        },
        'rangeDatezx' (value) {
            if (value) {
                this.listQuery.firstExecuteTime = value[0]
                this.listQuery.endExecuteTime = value[1]
            } else {
                this.listQuery.firstExecuteTime = undefined
                this.listQuery.endExecuteTime = undefined
            }
        }
    },
    methods: {
        //表格高度计算
        setTableHeight () {
            this.theight = this.$myFun.getSingleTbHeight() - 185
        },
        getData (val) {
            console.log(val, 'hhh')
            this.listQuery.orgCode = val[0].organizationCode
        },
        showGys () {
            this.$refs.refGys.dialogVisible = true
            this.$refs.refGys.getList()
        },
        //供应商选择确定
        handleGysClick (val) {
            console.log(val)
            this.$set(this.listQuery, 'supplierName', val[0].supplierName)
            this.$set(this.listQuery, 'supplierCode', val[0].supplierCode)
        },
        showOrg () {
            this.$refs.refOrg.dialogVisible = true
        },
        confirmOrg (data) {
            console.log(data)
            if (data) {
                this.$set(this.listQuery, 'editOrgCode', data[0].organizationCode)
                this.$set(this.listQuery, 'editOrgName', data[0].organizationName)
            }
        },
        showWl () {
            this.$refs.wlxz.dialogVisible = true
            this.$refs.wlxz.getList()
        },
        confirmWlxz (data) {
            console.log(data)
            if (data) {
                this.$set(this.listQuery, 'materialCode', data[0].materialCode)
                this.$set(this.listQuery, 'materialName', data[0].materialName)
            }
        },
        checked () {
            this.$refs.tb.toggleRowSelection(this.list[0], true)//官网方法，重点
        },
        refresh () {
            this.getList()
        },
        //显示主表
        getList () {
            if (this.listQuery.orgCode) {
                api.queryRecords(this.listQuery).then(res => {
                    let options = [this.dt_srm_pc2, this.dt_srm_p0, this.dt_srm_paf, this.dt_srm_pag]
                    res.data.list = this.$myFun.codeToName(res.data.list, options, ['adjustType', 'state', 'auditState', 'auditConclusion'])
                    this.list = res.data.list
                    this.total = res.data.pages.count
                    this.$nextTick(function () {
                        this.checked()
                    })
                })
            }
        },
        //显示从表
        getSubList () {
            let dto = {
                adjustNo: this.selectlistRow.adjustNo,
                currentPage: 1,
                pageSize: 10
            }
            api.queryRecordsItem(dto).then(res => {
                console.log(res, 'uuu')
                this.listSub = res.data
            })
        },
        //新增按钮单击事件
        handleCreate () {
            this.$refs.addMain.dialogStatus = 'create'
            this.$refs.addMain.dialogVisible = true
            this.$refs.addMain.initForm()
        },
        //编辑按钮单击事件
        handleUpdate (index, val) {
            console.log(val, 'valvalvalval')
            this.$refs.addMain.dialogStatus = 'update'
            this.$refs.addMain.dialogVisible = true
            this.$refs.addMain.temp = { ...val }
            let dto = {
                adjustNo: val.adjustNo,
                currentPage: 1,
                pageSize: 10
            }
            api.queryRecordsItem(dto).then(res => {
                this.$refs.addMain.list = res.data.map(v => {
                    v.orgCode = this.selectlistRow.orgCode
                    return v
                })
            })
        },
        //删除
        handleDelete (index, val) {
            console.log(index, val)
            this.$confirm("是否删除该信息", "提示", {
                confirmButtonText: "确定",
                cancelButtonText: "取消",
                type: "warning"
            }).then(() => {
                api.deleteRecord(val.rowId).then(() => {
                    this.$message({
                        title: '成功',
                        message: '删除成功',
                        type: 'success',
                        duration: 2000
                    })
                    this.getList()
                })
            }).catch(() => {
                this.$message({
                    type: "info",
                    message: "已取消"
                })
            })

        },
        //审核按钮单击事件
        handleEaxmine () {

        },
        //提交按钮单击事件
        handleSubmit (index, val) {
            this.$confirm("是否提交该信息", "提示", {
                confirmButtonText: "确定",
                cancelButtonText: "取消",
                type: "warning"
            }).then(() => {
                let dto = {
                    rowId: val.rowId
                }
                api.examineRecord(dto).then(() => {
                    this.$message({
                        title: '成功',
                        message: '提交成功',
                        type: 'success',
                        duration: 2000
                    })
                    this.getList()
                })
            }).catch(() => {
                this.$message({
                    type: "info",
                    message: "已取消"
                })
            })

        },
        //查询
        handleQuery () {
            this.listSub = []
            this.listQuery.currentPage = 1
            this.getList()
        },
        // 表格选择框选中 注:参数为选中的所有行的数组
        selectChange (val) {
            this.selectlistRow = val[val.length - 1]
            if (val.length > 1) {
                this.$refs.tb.clearSelection()
                this.$refs.tb.toggleRowSelection(val.pop())
            }
            if (this.selectlistRow) {
                this.getSubList()
            }
            this.$refs.tb.setCurrentRow(this.selectlistRow)
        },
        //点击表格某一行
        rowClick (val) {
            this.$refs.tb.clearSelection()
            this.$refs.tb.toggleRowSelection(val)
        },
    }
};
