import * as api from "@/api/project/srm/procurementDemand/divisionRules"
import { DICT_CODE } from '@/utils/constant'
import caiGou from '@/components/Select/xtpt/caigou'//下拉组件
import Pagination from "@/components/Pagination"
import addMain from "./dialog/addMain"
import addSub from "./dialog/addSub"
import { mapState } from "vuex"

export default {
    name: "delivery_Publish",
    components: { Pagination, caiGou, addMain, addSub },
    data () {
        return {
            DICT_CODE: DICT_CODE,
            showData: true,
            theight: 0,
            isSingle: true,//表格是否单选 点击各按钮根据流程逻辑控制单多选
            currentSelectedRow: null,//当前选中的行
            selectedRows: null,//多选时选中的所有行
            list: null,
            subList: null,
            total: 0,
            selectedrow: null,//主表选中行
            listQuery: {
                page: true,
                currentPage: 1,
                pageSize: 10
            },
            listQuerySub: {
                rowId: undefined,
                currentPage: 1,
                pageSize: 10
            },
            tableKey: [0, 1]//表格索引，虚拟dom渲染表格时使用

        }
    },
    computed: {
        ...mapState({
            dt_srm_pbb: state => state.dict.dt_srm_pbb,
            dt_srm_pbc: state => state.dict.dt_srm_pbc,
        })
    },
    mounted () {
        this.$store.dispatch("dict/getDicData", ["dt_srm_pbb", 'dt_srm_pbc'])
        this.setTableHeight()
        //表格高度自适应
        window.onresize = () => {
            this.setTableHeight()
        }

        this.getList()
    },
    watch: {
        'dt_srm_pbb': function () {
            this.getList()
        }
    },
    methods: {
        //表格高度计算
        setTableHeight () {
            this.theight = this.$myFun.getSingleTbHeight() - 185
        },
        //主表新增编辑成功之后刷新数据
        refresh () {
            this.getList()
        },
        //从表新增编辑成功之后刷新数据
        refreshSub () {
            this.getSubList()
        },
        checked () {
            this.$refs.tb.toggleRowSelection(this.list[0], true)//官网方法，重点
        },
        //显示主表
        getList () {
            api.queryRecords(this.listQuery).then(res => {
                let options = [this.dt_srm_pbb, this.dt_srm_pbc]
                res.data.list = this.$myFun.srmCodeToName(res.data.list, options, ['divisionType', 'ruleType'])
                this.list = res.data.list
                this.total = res.data.pages.count
                this.$nextTick(function () {
                    this.checked()
                })
            })
        },
        //显示从表
        getSubList () {
            this.listQuerySub.rowId = this.selectlistRow.rowId
            api.queryRecordSub(this.listQuerySub).then(res => {
                this.subList = res.data.list.map(v => {
                    if (!v.supplierCode) {
                        this.showData = false
                    } else {
                        this.showData = true
                    }
                    return v
                })
            })
        },
        //主表新增按钮单击事件
        handleCreate () {
            this.$refs.addMain.resetTemp()
            this.$refs.addMain.dialogStatus = 'create'
            this.$refs.addMain.dialogVisible = true
        },
        //主表编辑按钮单击事件
        handleUpdate (index, val) {
            console.log(index, val)
            this.$refs.addMain.dialogStatus = 'update'
            this.$refs.addMain.temp = { ...val }
            this.$refs.addMain.dialogVisible = true
        },
        //主表删除
        handleDelete (index, val) {
            this.$confirm("是否删除该信息", "提示", {
                confirmButtonText: "确定",
                cancelButtonText: "取消",
                type: "warning"
            }).then(() => {
                api.deleteRecord(val.rowId).then(() => {
                    this.$message({
                        title: '成功',
                        message: '删除成功',
                        type: 'success',
                        duration: 2000
                    })
                    this.getList()
                })
            }).catch(() => {
                this.$message({
                    type: "info",
                    message: "已取消"
                })
            })

        },
        //从表新增按钮单击事件
        handleCreateSub () {
            if (this.selectlistRow) {
                this.$refs.addSub.resetTemp()
                this.$refs.addSub.dialogStatus = 'create'
                this.$refs.addSub.temp = this.selectlistRow
                if (this.$refs.addSub.temp.ruleType === "PBC0") {
                    this.$refs.addSub.Edit = true
                } else {
                    this.$refs.addSub.Edit = false
                }
                this.$refs.addSub.rowId = this.selectlistRow.rowId
                this.$refs.addSub.dialogVisible = true
            } else {
                this.$message({
                    title: '警告',
                    message: '请选择一条信息',
                    type: 'warning',
                    duration: 2000
                })
            }
        },
        //从表编辑按钮单击事件
        handleUpdateSub (index, val) {
            this.$refs.addSub.dialogStatus = 'update'
            this.$refs.addSub.temp = { ...val }
            if (this.$refs.addSub.temp.ruleType === "PBC0") {
                this.$refs.addSub.Edit = true
            } else {
                this.$refs.addSub.Edit = false
            }
            this.$refs.addSub.dialogVisible = true
        },
        //从表删除
        handleDeleteSub (index, val) {
            this.$confirm("是否删除该信息", "提示", {
                confirmButtonText: "确定",
                cancelButtonText: "取消",
                type: "warning"
            }).then(() => {
                api.deleteRecordSub(val.ruleId, val.rowId).then(() => {
                    this.$message({
                        title: '成功',
                        message: '删除成功',
                        type: 'success',
                        duration: 2000
                    })
                    this.getSubList()
                })
            }).catch(() => {
                this.$message({
                    type: "info",
                    message: "已取消"
                })
            })
        },
        //查询
        handleQuery () {
            this.subList = []
            this.listQuery.currentPage = 1
            this.getList()
        },
        // 表格选择框选中 注:参数为选中的所有行的数组
        selectChange (val) {
            this.selectlistRow = val[val.length - 1]
            if (val.length > 1) {
                this.$refs.tb.clearSelection()
                this.$refs.tb.toggleRowSelection(val.pop())
            }
            if (this.selectlistRow) {
                this.getSubList()
            }
            this.$refs.tb.setCurrentRow(this.selectlistRow)
        },
        //点击表格某一行
        rowClick (val) {
            this.$refs.tb.clearSelection()
            this.$refs.tb.toggleRowSelection(val)
        },
    }
};
